import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './UserIcon.css';
import default_image from '../../Assets/default-image.jpg';

function UserIcon({ isAuthenticated, username, email, userType, userId, onLogout,onLogi, onProfileNavigate }) {
    const [showMenu, setShowMenu] = useState(false);
    const [loginEmail, setLoginEmail] = useState('');
    const [loginPassword, setLoginPassword] = useState('');
    const [message, setMessage] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();
    const menuRef = useRef(null);

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (menuRef.current && !menuRef.current.contains(event.target)) {
                setShowMenu(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    const handleLogin = async (e) => {
        e.preventDefault();
        
        if (!loginEmail || !loginPassword) {
            setMessage('Please fill in all fields');
            return;
        }

        setLoading(true);
        setMessage('');

        try {
            const response = await axios.post('http://localhost:5000/api/users/login', {
                email: loginEmail,
                password: loginPassword
            }, {
                headers: {
                    'Content-Type': 'application/json'
                }
            });

            console.log('Login response:', response.data);

            if (response.data && response.data.token && response.data.user) {
                setMessage('Login successful!');
                
                // Store token and user data
                localStorage.setItem('token', response.data.token);
                localStorage.setItem('user', JSON.stringify(response.data.user));
                
                console.log('User ID received:', response.data.user.id || response.data.user._id);
                
                // Call parent's onLogin with the user data
                if (typeof onLogin === 'function') {
                    onLogin(response.data.user);
                }
                
                // Close menu and redirect
                setTimeout(() => {
                    setShowMenu(false);
                    setLoginEmail('');
                    setLoginPassword('');
                    
                    // Navigate based on user type
                    if (response.data.user.userType === 'technician') {
                        navigate('/technician-profile');
                    } else {
                        navigate('/client-profile');
                    }
                }, 1000);

            } else {
                setMessage('Invalid response from server');
            }

        } catch (error) {
            console.error('Login error:', error);
            setMessage(
                error.response?.data?.message || 
                'Login failed. Please try again.'
            );
        } finally {
            setLoading(false);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        onLogout();
        setShowMenu(false);
        setLoginEmail('');
        setLoginPassword('');
        setMessage('');
        navigate('/');
    };

    const getInitials = (username) => {
        if (!username) return 'U';
        return username.charAt(0).toUpperCase();
    };

    return (
        <div className="user-auth" ref={menuRef}>
            <div 
                className="user-icon" 
                onClick={() => {
                    setShowMenu(!showMenu);
                    setMessage('');
                }}
            >
                {isAuthenticated ? (
                    <div className="initials">{getInitials(username)}</div>
                ) : (
                    <img src={default_image} alt="Default User Icon" />
                )}
            </div>

            {showMenu && (
                <div className="dropdown-menu">
                    {isAuthenticated ? (
                        <>
                            <div className="user-info">
                                <p className="username">{username}</p>
                                <p className="user-email">{email}</p>
                                <p className="user-type">{userType}</p>
                                <p className="user-id">ID: {userId}</p>
                            </div>
                            <div className="menu-actions">
                                <button 
                                    className="dashboard-btn"
                                    onClick={onProfileNavigate}
                                >
                                    My Profile
                                </button>
                                
                                <button 
                                    className="logout-btn"
                                    onClick={handleLogout}
                                >
                                    Logout
                                </button>
                            </div>
                        </>
                    ) : (
                        <div className="login-form-container">
                            <h3>Login</h3>
                            {message && (
                                <p className={`message ${message.includes('success') ? 'success' : 'error'}`}>
                                    {message}
                                </p>
                            )}
                            <form onSubmit={handleLogin}>
                                <div className="form-group">
                                    <label>Email</label>
                                    <input
                                        type="email"
                                        placeholder="Enter your email"
                                        value={loginEmail}
                                        onChange={(e) => setLoginEmail(e.target.value)}
                                        required
                                    />
                                </div>
                                <div className="form-group">
                                    <label>Password</label>
                                    <input
                                        type="password"
                                        placeholder="Enter your password"
                                        value={loginPassword}
                                        onChange={(e) => setLoginPassword(e.target.value)}
                                        required
                                    />
                                </div>
                                <button 
                                    type="submit" 
                                    disabled={loading}
                                    className="login-btn"
                                >
                                    {loading ? 'Logging in...' : 'Login'}
                                </button>
                            </form>
                            <div className="signup-prompt">
                                <p>Don't have an account?</p>
                                <button 
                                    className="signup-link"
                                    onClick={() => {
                                        setShowMenu(false);
                                        navigate('/signup');
                                    }}
                                >
                                    Sign up here
                                </button>
                            </div>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

export default UserIcon;
