import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify'; // ✅ Import ToastContainer
import 'react-toastify/dist/ReactToastify.css';

const TechnicianTickets = () => {
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [status, setStatus] = useState('');

  useEffect(() => {
    const fetchTickets = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/fetchtickets/assigned', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setTickets(response.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Error fetching tickets');
      } finally {
        setLoading(false);
      }
    };

    fetchTickets();
  }, [status]);

  const handleTicketClick = (ticket) => {
    setSelectedTicket(ticket);
    setStatus(ticket.status);
  };

  const handleStatusChange = async () => {
    try {
      const token = localStorage.getItem('token');
  
      // Fetch the latest ticket details to get the current status
      const { data: latestTicket } = await axios.get(
        `http://localhost:5000/api/tickets/${selectedTicket._id}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
  
      const statusFlow = [
        "Seen",
        "Leaving for Site",
        "Enroute",
        "Arrived on Site",
        "Submit Details",
        "Complete"
      ];
  
      const currentIndex = statusFlow.indexOf(latestTicket.status);
      if (currentIndex === -1 || currentIndex === statusFlow.length - 1) {
        toast.warning("No further status updates available.");
        return;
      }
  
      const newStatus = statusFlow[currentIndex + 1]; // Get next status
  
      // Update ticket status
      await axios.put(
        `http://localhost:5000/api/tickets/${selectedTicket._id}/status`,
        { status: newStatus },
        { headers: { Authorization: `Bearer ${token}` } }
      );
  
      setStatus(newStatus);
      setTickets((prevTickets) =>
        prevTickets.map((ticket) =>
          ticket._id === selectedTicket._id ? { ...ticket, status: newStatus } : ticket
        )
      );
  
      toast.success(`Status updated to ${newStatus}`); // ✅ Success toast
    } catch (err) {
      const errorMessage = err.response?.data?.message || "Error updating status";
      setError(errorMessage);
      toast.error(errorMessage); // ✅ Error toast
    }
  };
  

  if (loading) return <div>Loading tickets...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="technician-tickets-container">
      <ToastContainer /> {/* ✅ Add ToastContainer here */}
      <h2>Assigned Tickets</h2>
      {tickets.length === 0 ? (
        <p>No tickets assigned to you.</p>
      ) : (
        <div className="ticket-buttons">
          {tickets.map((ticket) => (
            <button key={ticket._id} className="ticket-button" onClick={() => handleTicketClick(ticket)}>
              {ticket.clientName}
            </button>
          ))}
        </div>
      )}

      {selectedTicket && (
        <div className="ticket-details">
          <h3>Ticket Details</h3>
          <p><strong>Status:</strong> {status}</p>
          <p><strong>Ticket Type:</strong> {selectedTicket.ticketType}</p>
          <p><strong>Client Name:</strong> {selectedTicket.clientName}</p>
          <p><strong>Mobile Number:</strong> {selectedTicket.mobileNumber}</p>
          <p><strong>Installation Type:</strong> {selectedTicket.installationType}</p>
          <p><strong>Assigned Technician:</strong> {selectedTicket.assignedTechnician.username}</p>
          <p><strong>Created By:</strong> {selectedTicket.createdBy.username}</p>
          <p><strong>Location:</strong> {selectedTicket.location}</p>
          <p><strong>Created At:</strong> {new Date(selectedTicket.createdAt).toLocaleDateString()}</p>

          {status === 'Seen' && <button onClick={() => handleStatusChange('Leaving for Site')}>Mark as Leaving for Site</button>}
          {status === 'Leaving for Site' && <button onClick={() => handleStatusChange('Enroute')}>Mark as Enroute</button>}
          {status === 'Enroute' && <button onClick={() => handleStatusChange('Arrived on Site')}>Mark as Arrived on Site</button>}
          {status === 'Arrived on Site' && <button onClick={() => handleStatusChange('Submit Details')}>Mark as Submit Details</button>}
          {status === 'Submit Details' && <button onClick={() => handleStatusChange('Complete')}>Mark as Complete</button>}

          {status === 'Submit Details' && (
            <div className="submit-details">
              <h4>Submit Details</h4>
              <label>Speedtest Screenshot: <input type="file" accept="image/*" /></label>
              <label>WAN Photo: <input type="file" accept="image/*" /></label>
              <label>LAN Photo: <input type="file" accept="image/*" /></label>
              <label>MAC Address of Router: <input type="text" /></label>
              <label>Signal Received: <input type="text" /></label>
              <label>BOM Used: <input type="text" /></label>
              <label>Additional Notes: <textarea /></label>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default TechnicianTickets;
