import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './Components/Navbar/Navbar';
import Home from './Pages/Home/Home';
import MyApplication from './Pages/My-Application/MyApps';
import Shifts from './Pages/Shifts/Shifts';
import InternetStatus from './Pages/InternetStatus/InternetStatus';
import Chat from './Pages/Chats/Chats';
import Login from './Pages/Login/Login';
import Signup from './Pages/Signup/Signup';
import TechnicianProfile from './Protected_Apps/TechnicianProfile/TechnicianProfile';
import TechnicianTickets from './Protected_Apps/Tickets/TechnicianTickets';
import ClientProfile from './Protected_Apps/ClientProfile/ClientProfile';

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [username, setUsername] = useState('');
    const [userType, setUserType] = useState('');
    const [email, setEmail] = useState('');

    const handleLogin = (user) => {
        setIsAuthenticated(true);
        setUsername(user.username);
        setUserType(user.userType);
        setEmail(user.email);
    };

    const handleLogout = () => {
        setIsAuthenticated(false);
        setUsername('');
        setUserType('');
        setEmail('');
    };

    // Protected Route Component
    const ProtectedRoute = ({ children, requiredUserType }) => {
        if (!isAuthenticated) {
            return <Navigate to="/login" replace />;
        }

        if (requiredUserType && userType !== requiredUserType) {
            return <Navigate to="/" replace />;
        }

        return children;
    };

    return (
        <Router>
            <Navbar 
                isAuthenticated={isAuthenticated} 
                username={username} 
                userType={userType}
                email={email}
                onLogout={handleLogout} 
            />
            <Routes>
                <Route path="/" element={<Home />} />

                {/* My Application routes */}
                <Route path="/my-application" element={
                    <ProtectedRoute requiredUserType="technician">
                        <MyApplication />
                    </ProtectedRoute>
                }>
                    <Route path="tickets" element={<TechnicianTickets username={username} />} />
                </Route>

                {/* Technician Profile - Direct Route */}
                <Route path="/technician-profile" element={
                    <ProtectedRoute requiredUserType="technician">
                        <TechnicianProfile 
                            userType={userType} 
                            username={username} 
                            email={email} 
                        />
                    </ProtectedRoute>
                } />

                {/* Client Profile - Direct Route */}
                <Route path="/client-profile" element={
                    <ProtectedRoute requiredUserType="client">
                        <ClientProfile 
                            userType={userType} 
                            username={username} 
                            email={email} 
                        />
                    </ProtectedRoute>
                } />

                {/* Shared Routes */}
                <Route path="/shifts" element={<Shifts />} />
                <Route path="/internet-status" element={<InternetStatus />} />
                <Route path="/chat" element={<Chat />} />
                <Route path="/login" element={<Login onLogin={handleLogin} />} />
                <Route path="/signup" element={<Signup />} />
            </Routes>
        </Router>
    );
}

export default App;
