import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './TechnicianProfile.css';

const TechnicianProfile = ({ userId }) => { // Receive userId as prop, not from useParams
  const navigate = useNavigate();
  const [technician, setTechnician] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editForm, setEditForm] = useState({});
  const [saving, setSaving] = useState(false);
  const [uploadingImage, setUploadingImage] = useState(false);

 
  
  

 useEffect(() => {
    const fetchTechnician = async () => {
      if (!userId || userId === 'undefined' || userId === 'null') {
        setError('Invalid user ID: ' + userId);
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        setError(null);
        
        console.log('Fetching technician with ID:', userId);
        
        // Get token from localStorage
        const token = localStorage.getItem('token');
        
        if (!token) {
          setError('Authentication required. Please log in.');
          setLoading(false);
          return;
        }

        // Use the PROTECTED endpoint with authentication
        const response = await axios.get(`http://localhost:5000/api/users/profile`, {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });
        
        console.log('Technician data received:', response.data);
        
        // Since this endpoint returns the current user's profile directly
        // (not wrapped in success/user object), access it directly
        setTechnician(response.data);
        setEditForm(response.data);
        
      } catch (err) {
        console.error('Error fetching technician:', err);
        console.error('Error details:', err.response?.data);
        
        const errorMessage = err.response?.data?.message || 
                            err.response?.data?.error || 
                            err.message || 
                            'Failed to load technician profile';
        setError(`Error: ${errorMessage}`);
        
        // If it's an authentication error, redirect to login
        if (err.response?.status === 401) {
          setTimeout(() => {
            navigate('/login');
          }, 2000);
        }
      } finally {
        setLoading(false);
      }
    };

    fetchTechnician();
  }, [userId, navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditForm(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleAddressChange = (e) => {
    const { name, value } = e.target;
    setEditForm(prev => ({
      ...prev,
      address: {
        ...prev.address,
        [name]: value
      }
    }));
  };

  const handleArrayChange = (field, value) => {
    const items = value.split(',').map(item => item.trim()).filter(item => item);
    setEditForm(prev => ({
      ...prev,
      [field]: items
    }));
  };

  const handleSaveProfile = async () => {
    try {
      setSaving(true);
      const token = localStorage.getItem('token');
      
      const response = await axios.put(
        'http://localhost:5000/api/users/profile/update',
        editForm,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      setTechnician(response.data.user);
      setIsEditing(false);
      alert('Profile updated successfully!');
    } catch (err) {
      console.error('Error updating profile:', err);
      alert(err.response?.data?.message || 'Failed to update profile');
    } finally {
      setSaving(false);
    }
  };

  const handleImageUpload = async (e) => {
    try {
      setUploadingImage(true);
      const file = e.target.files[0];
      if (!file) return;

      const formData = new FormData();
      formData.append('profilePicture', file);

      const token = localStorage.getItem('token');
      const response = await axios.put(
        'http://localhost:5000/api/users/profile/picture',
        formData,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          }
        }
      );

      // Update both technician and editForm with new image
      setTechnician(prev => ({
        ...prev,
        profilePicture: response.data.profilePicture
      }));
      setEditForm(prev => ({
        ...prev,
        profilePicture: response.data.profilePicture
      }));

      alert('Profile picture updated successfully!');
    } catch (err) {
      console.error('Error uploading image:', err);
      alert(err.response?.data?.message || 'Failed to upload image');
    } finally {
      setUploadingImage(false);
    }
  };

  const handleContact = () => {
    if (technician && technician.email) {
      window.location.href = `mailto:${technician.email}?subject=Service Inquiry`;
    }
  };

  // Helper function to get main profession
  const getMainProfession = () => {
    if (technician?.profession) {
      return technician.profession;
    }
    if (technician?.professions?.length > 0) {
      return technician.professions[0];
    }
    return 'Professional Technician';
  };

  // Helper function to get services to display
  const getServicesToDisplay = () => {
    if (technician?.subServices && technician.subServices.length > 0) {
      return technician.subServices;
    }
    if (technician?.services && technician.services.length > 0) {
      return technician.services;
    }
    if (technician?.professions && technician.professions.length > 0) {
      return technician.professions;
    }
    return [];
  };

  // Helper function to get profile picture URL
  const getProfilePicture = () => {
    if (technician?.profilePicture) {
      if (technician.profilePicture.startsWith('http')) {
        return technician.profilePicture;
      }
      return `http://localhost:5000${technician.profilePicture}`;
    }
    return 'https://i.pravatar.cc/150';
  };

  if (loading) return <div className="loading">Loading technician profile...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!technician) return <div className="error">Technician not found</div>;

  const servicesToDisplay = getServicesToDisplay();
  const mainProfession = getMainProfession();
  const profilePictureUrl = getProfilePicture();

  return (
    <div className="technician-profile">
      <div className="profile-header-actions">
        <button className="back-button" onClick={() => navigate(-1)}>
          <i className="fas fa-arrow-left"></i> Back
        </button>
        
        {isOwner() && !isEditing && (
          <button 
            className="edit-profile-btn"
            onClick={() => setIsEditing(true)}
          >
            <i className="fas fa-edit"></i> Edit Profile
          </button>
        )}
      </div>

      {isEditing ? (
        /* EDIT MODE */
        <div className="edit-profile-form">
          <div className="form-section">
            <h2>Edit Profile</h2>
            
            {/* Profile Picture Upload */}
            <div className="form-group">
              <label>Profile Picture</label>
              <div className="image-upload-section">
                <img 
                  src={getProfilePicture()} 
                  alt="Profile" 
                  className="profile-preview"
                />
                <div className="upload-controls">
                  <input
                    type="file"
                    accept="image/*"
                    onChange={handleImageUpload}
                    disabled={uploadingImage}
                  />
                  {uploadingImage && <span>Uploading...</span>}
                </div>
              </div>
            </div>

            {/* Basic Information */}
            <div className="form-group">
              <label>Username</label>
              <input
                type="text"
                name="username"
                value={editForm.username || ''}
                onChange={handleInputChange}
              />
            </div>

            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={editForm.email || ''}
                onChange={handleInputChange}
              />
            </div>

            <div className="form-group">
              <label>Phone Number</label>
              <input
                type="text"
                name="phoneNumber"
                value={editForm.phoneNumber || ''}
                onChange={handleInputChange}
              />
            </div>

            <div className="form-group">
              <label>Professions (comma separated)</label>
              <input
                type="text"
                value={editForm.professions?.join(', ') || ''}
                onChange={(e) => handleArrayChange('professions', e.target.value)}
                placeholder="e.g., Electrician, Plumbing, Carpentry"
              />
            </div>

            <div className="form-group">
              <label>Project Rate Category</label>
              <select
                name="projectRateCategory"
                value={editForm.projectRateCategory || ''}
                onChange={handleInputChange}
              >
                <option value="">Select Rate Category</option>
                <option value="under_10000">Under Ksh 10,000</option>
                <option value="10000_to_100000">Ksh 10,000 - 100,000</option>
                <option value="100000_to_1000000">Ksh 100,000 - 1,000,000</option>
                <option value="over_1000000">Over Ksh 1,000,000</option>
              </select>
            </div>

            {/* Address Information */}
            <div className="form-group">
              <label>Street Address</label>
              <input
                type="text"
                name="street"
                value={editForm.address?.street || ''}
                onChange={handleAddressChange}
              />
            </div>

            <div className="form-group">
              <label>City</label>
              <input
                type="text"
                name="city"
                value={editForm.address?.city || ''}
                onChange={handleAddressChange}
              />
            </div>

            <div className="form-group">
              <label>State/Region</label>
              <input
                type="text"
                name="state"
                value={editForm.address?.state || ''}
                onChange={handleAddressChange}
              />
            </div>

            <div className="form-group">
              <label>ZIP Code</label>
              <input
                type="text"
                name="zipCode"
                value={editForm.address?.zipCode || ''}
                onChange={handleAddressChange}
              />
            </div>

            {/* About Section */}
            <div className="form-group">
              <label>About Me</label>
              <textarea
                name="about"
                value={editForm.about || ''}
                onChange={handleInputChange}
                rows="4"
                placeholder="Tell clients about your experience, skills, and approach..."
              />
            </div>

            {/* Action Buttons */}
            <div className="form-actions">
              <button 
                className="save-btn" 
                onClick={handleSaveProfile}
                disabled={saving}
              >
                {saving ? 'Saving...' : 'Save Changes'}
              </button>
              <button 
                className="cancel-btn"
                onClick={() => {
                  setIsEditing(false);
                  setEditForm(technician); // Reset form
                }}
                disabled={saving}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      ) : (
        /* VIEW MODE */
        <>
          <div className="profile-header">
            {/* Left Side - Basic Info */}
            <div className="profile-basic-info">
              <div className="profile-image">
                <img 
                  src={profilePictureUrl} 
                  alt={technician.username}
                  onError={(e) => {
                    e.target.src = 'https://i.pravatar.cc/150';
                  }}
                />
                {isOwner() && (
                  <div className="image-upload-hint">
                    Click Edit Profile to change picture
                  </div>
                )}
              </div>
              <div className="basic-info">
                <h1>{technician.username}</h1>
                <p className="profession">{mainProfession}</p>
                
                <div className="rating">
                  <span className="stars"> {technician.rating?.average?.toFixed(1) || '0.0'}</span>
                  <span className="reviews">({technician.rating?.count || 0} reviews)</span>
                </div>
                
                <div className="contact-item">
                  <i className="fas fa-envelope"></i> 
                  <span>{technician.email || 'Email not provided'}</span>
                </div>
                <div className="contact-item">
                  <i className="fas fa-phone"></i> 
                  <span>{technician.phoneNumber || 'Phone not provided'}</span>
                </div>

                {technician.email && !isOwner() && (
                  <button className="contact-btn-large" onClick={handleContact}>
                    <i className="fas fa-envelope"></i> Contact Technician
                  </button>
                )}
              </div>
            </div>

            {/* Right Side - Key Details */}
            <div className="key-details-sidebar">
              <div className="key-detail-section">
                <h3><i className="fas fa-map-marker-alt"></i> Location</h3>
                <p>
                  {technician.address?.street && `${technician.address.street}, `}
                  {technician.address?.city && `${technician.address.city}, `}
                  {technician.address?.state && `${technician.address.state} `}
                  {technician.address?.zipCode && technician.address.zipCode}
                  {!technician.address?.street && !technician.address?.city && 
                  !technician.address?.state && 'Location not specified'}
                </p>
              </div>

              <div className="key-detail-section">
                <h3><i className="fas fa-tools"></i> Services Offered</h3>
                <div className="services-list">
                  {servicesToDisplay.length > 0 ? (
                    servicesToDisplay.map((service, index) => (
                      <span key={index} className="service-tag">{service}</span>
                    ))
                  ) : (
                    <p>No services listed</p>
                  )}
                </div>
              </div>

              {technician.userType === 'technician' && (
                <div className="key-detail-section">
                  <h3><i className="fas fa-money-bill-wave"></i> Project Rate</h3>
                  <p className="rate-display">
                    {technician.projectRateCategory === 'under_10000' && 'Under Ksh 10,000'}
                    {technician.projectRateCategory === '10000_to_100000' && 'Ksh 10,000 - 100,000'}
                    {technician.projectRateCategory === '100000_to_1000000' && 'Ksh 100,000 - 1,000,000'}
                    {technician.projectRateCategory === 'over_1000000' && 'Over Ksh 1,000,000'}
                    {!technician.projectRateCategory && 'Rate not specified'}
                  </p>
                </div>
              )}
            </div>
          </div>

          {/* About Section (Below) */}
          <div className="profile-details">
            <div className="detail-section">
              <h3>About {technician.username}</h3>
              <p>{technician.about || 'No information provided about this technician.'}</p>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default TechnicianProfile;
