import React, { useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode';

import axios from 'axios';
import './UserIcon.css';
import default_image from '../../Assets/default-image.jpg'

function UserIcon() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [showMenu, setShowMenu] = useState(false);
    const [showLogin, setShowLogin] = useState(false);
    const [showSignup, setShowSignup] = useState(false);
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState('');
    const [roles, setRoles] = useState([]);
    const [message, setMessage] = useState('');

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            // If token exists, set user to authenticated
            setIsAuthenticated(true);
            // Optionally fetch user info using the token if necessary, or use it directly
            const decodedToken = jwtDecode(token); // You might need to install `jwt-decode` or decode the token manually
            if (decodedToken) {
                setUsername(decodedToken.username);
                setEmail(decodedToken.email); // If email is available in the token
            }
        }
    }, []);
    

    useEffect(() => {
        const fetchRoles = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/roles/fetch');
                setRoles(response.data);
            } catch (error) {
                console.error('Error fetching roles:', error);
            }
        };

        fetchRoles();
    }, []);

    const handleSignup = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/api/users/register', {
                username,
                password,
                email,
                role
            });
            setMessage(response.data.message);
            setShowSignup(false);
            setShowLogin(true);
        } catch (error) {
            setMessage(error.response?.data?.message || 'An error occurred');
        }
    };

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/api/users/login', {
                email,
                password
            });
            // Access the user object from the response
        const { user } = response.data; // Extract the user object
        setUsername(user.username); // Set the username from the user object
        setMessage(response.data.message);
        setIsAuthenticated(true);
        setShowLogin(false);
        setShowMenu(false); // Close menu after login
        localStorage.setItem('token', response.data.token); // Store token
    } catch (error) {
        setMessage(error.response?.data?.message || 'An error occurred');
    }
};

    const handleLogout = () => {
        setIsAuthenticated(false);
        setUsername('');
        setEmail('');
        localStorage.removeItem('token'); // Clear token on logout
    };

    const getInitials = (username) => {
        if (!name) return ''; // Return an empty string if the name is undefined or empty
        const usernameArray = username.split(' ');
        return usernameArray[0][0].toUpperCase() + (usernameArray[1] ? nameArray[1][0].toUpperCase() : '');
    };
    

    return (
        <div className="user-auth">
            {/* User Icon */}
            <div className="user-icon" onClick={() => setShowMenu(!showMenu)}>
                {isAuthenticated ? (
                    <div className="initials">{getInitials(username)}</div>
                ) : (
                    <img src={default_image} alt="Default User Icon" />
                )}
            </div>

            {/* Menu and Forms */}
            {showMenu && (
                <div className="dropdown-menu">
                    {isAuthenticated ? (
                        <>
                            <p>Signed in as <br/><br/>{username}</p>
                            <p>{email}</p> {/* Display the email address */}
                            <button onClick={handleLogout}>Logout</button>
                        </>
                    ) : (
                        <>
                            {showSignup ? (
                                // Signup Form
                                <>
                                    <h3>Sign Up</h3>
                                    {message && <p>{message}</p>}
                                    <form onSubmit={handleSignup}>
                                        <input
                                            type="text"
                                            placeholder="Username"
                                            value={username}
                                            onChange={(e) => setUsername(e.target.value)}
                                            required
                                        />
                                        <input
                                            type="email"
                                            placeholder="Email"
                                            value={email}
                                            onChange={(e) => setEmail(e.target.value)}
                                            required
                                        />
                                        <input
                                            type="password"
                                            placeholder="Password"
                                            value={password}
                                            onChange={(e) => setPassword(e.target.value)}
                                            required
                                        />
                                        <select value={role} onChange={(e) => setRole(e.target.value)} required>
                                            <option value="">Select Role</option>
                                            {roles.map((role) => (
                                                <option key={role._id} value={role.name}>
                                                    {role.name}
                                                </option>
                                            ))}
                                        </select>
                                        <button type="submit">Sign Up</button>
                                    </form>
                                    <p>Already registered? <span onClick={() => { setShowSignup(false); setShowLogin(true); }}>Login</span></p>
                                </>
                            ) : showLogin ? (
                                // Login Form
                                <>
                                    <h3>Login</h3>
                                    {message && <p>{message}</p>}
                                    <form onSubmit={handleLogin}>
                                        <input
                                            type="email"
                                            placeholder="Email"
                                            value={email}
                                            onChange={(e) => setEmail(e.target.value)}
                                            required
                                        />
                                        <input
                                            type="password"
                                            placeholder="Password"
                                            value={password}
                                            onChange={(e) => setPassword(e.target.value)}
                                            required
                                        />
                                        <button type="submit">Login</button>
                                    </form>
                                    <p>Not yet registered? <span onClick={() => { setShowLogin(false); setShowSignup(true); }}>Sign up</span></p>
                                </>
                            ) : (
                                // Default Menu when not authenticated
                                <p>Not signed up yet? <span onClick={() => setShowSignup(true)}>Sign up</span></p>
                            )}
                        </>
                    )}
                </div>
            )}
        </div>
    );
}

export default UserIcon;
