// ITNetworkingPage.jsx
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Internet.css";

const ITNetworkingPage = () => {
  const navigate = useNavigate();

  // ================== STATE ==================
  const [servicesData, setServicesData] = useState({});
  const [expanded, setExpanded] = useState({});
  const [selectedService, setSelectedService] = useState(null);
  const [techniciansData, setTechniciansData] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // ================== HELPERS ==================
  const toggleExpand = (service) => {
    setExpanded((prev) => ({ ...prev, [service]: !prev[service] }));
  };

  const handleSelectService = (service) => {
    setSelectedService(service);
    fetchTechnicians(service);
  };

  const handleBack = () => setSelectedService(null);

  const handleNavigateToCategories = () => navigate("/categories");

  const handleViewProfile = (id) => {
    navigate(`/techniciancategoryprofile/${id}`);
  };

  // ================== FETCH SERVICES ==================
  useEffect(() => {
    const fetchServices = async () => {
      try {
        setLoading(true);
        const res = await axios.get("http://localhost:5000/api/services");

        // Restructure into { category: [subservices] }
        const formatted = {};
        res.data.forEach((s) => {
          formatted[s.name] = s.subservices || [];
        });
        setServicesData(formatted);
      } catch (err) {
        setError("Failed to fetch services. Please try again.");
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchServices();
  }, []);

  // ================== FETCH TECHNICIANS ==================
  const fetchTechnicians = async (subService) => {
    try {
      setLoading(true);
      setError(null);

      const res = await fetch(
        `http://localhost:5000/api/technicians_subservice?subService=${encodeURIComponent(
          subService
        )}&category=IT%20&%20Networking`
      );
      const data = await res.json();

      setTechniciansData((prev) => ({
        ...prev,
        [subService]: data.exactMatches || [],
        related: data.relatedServices || [],
      }));
    } catch (err) {
      setError("Failed to fetch technicians. Please try again.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // ================== RENDERERS ==================
  const renderServicesList = () => (
    <div className="services-section">
      <h2>Our IT & Networking Services</h2>
      <p className="section-description">
        Select a service category to view available technicians
      </p>

      <div className="services-grid">
        {Object.entries(servicesData).map(([service, subs]) => (
          <div key={service} className="service-card">
            {/* Header */}
            <div
              className="service-header"
              onClick={() => subs.length && toggleExpand(service)}
              style={{ cursor: subs.length ? "pointer" : "default" }}
            >
              <div className="service-icon">
                {service === "Internet Services" && <i className="fas fa-wifi" />}
                {service === "CCTV" && <i className="fas fa-video" />}
                {service === "Computer Services" && <i className="fas fa-laptop" />}
                {service === "Alarm System Installation" && <i className="fas fa-shield-alt" />}
                {service === "POS Installation & Support" && <i className="fas fa-cash-register" />}
                {service === "Phone Repair" && <i className="fas fa-mobile-alt" />}
                {service === "TV Repair" && <i className="fas fa-tv" />}
              </div>

              <h3>{service}</h3>

              {subs.length > 0 && (
                <button
                  className={`expand-btn ${expanded[service] ? "expanded" : ""}`}
                  onClick={(e) => {
                    e.stopPropagation();
                    toggleExpand(service);
                  }}
                >
                  <i className="fas fa-chevron-down"></i>
                </button>
              )}
            </div>

            {/* Subservices */}
            {expanded[service] && subs.length > 0 && (
              <div className="subservices-list">
                {subs.map((sub) => (
                  <div
                    key={sub}
                    className="subservice-item"
                    onClick={() => handleSelectService(sub)}
                  >
                    <i className="fas fa-cog"></i>
                    <span>{sub}</span>
                    <i className="fas fa-arrow-right"></i>
                  </div>
                ))}
              </div>
            )}

            {/* If no subservices */}
            {subs.length === 0 && (
              <button
                className="select-service-btn"
                onClick={() => handleSelectService(service)}
              >
                Select Service <i className="fas fa-arrow-right"></i>
              </button>
            )}
          </div>
        ))}
      </div>
    </div>
  );

  const renderTechniciansList = () => {
    const techs = techniciansData[selectedService] || [];

    return (
      <div className="technicians-section">
        <button className="back-button" onClick={handleBack}>
          <i className="fas fa-arrow-left"></i> Back to Services
        </button>

        <h2>Technicians for {selectedService}</h2>
        <p className="technicians-count">{techs.length} technicians available</p>

        {techs.length > 0 ? (
          <div className="technicians-grid">
            {techs.map((t) => (
              <div key={t._id} className="technician-card">
                <div className="technician-image">
                  <img
                    src={`https://i.pravatar.cc/100?u=${t._id}`}
                    alt={t.username}
                  />
                </div>

                <div className="technician-info">
                  <h3>{t.username}</h3>
                  <p className="specialty">{t.profession}</p>

                  <div className="rating">
                    <span>‚≠ê {t.rating?.average || 0}</span>
                    <span>({t.rating?.count || 0} reviews)</span>
                  </div>

                  <div className="details">
                    <p>
                      <i className="fas fa-map-marker-alt"></i>{" "}
                      {t.address?.city || "Unknown"}
                    </p>
                    <p>
                      <i className="fas fa-clock"></i>{" "}
                      {t.projectRateCategory || "N/A"}
                    </p>
                  </div>

                  <div className="technician-actions">
                    <button className="contact-btn">
                      <i className="fas fa-envelope"></i> Contact
                    </button>
                    <button
                      className="profile-btn"
                      onClick={() => handleViewProfile(t._id)}
                    >
                      <i className="fas fa-user"></i> View Profile
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="no-technicians">
            <i className="fas fa-user-slash"></i>
            <h3>No technicians available</h3>
            <p>No technicians are available for {selectedService} right now.</p>
          </div>
        )}
      </div>
    );
  };

  // ================== RENDER ==================
  return (
    <div className="it-networking-page">
      {/* Header */}
      <header className="category-header">
        <div className="header-content">
          <button className="back-button" onClick={handleNavigateToCategories}>
            <i className="fas fa-arrow-left"></i> All Categories
          </button>
          <div className="category-icon">
            <i className="fas fa-network-wired"></i>
          </div>
          <h1>IT & Networking</h1>
          <p>
            Professional IT and networking services including internet setup,
            security systems, computer repairs, and more.
          </p>
        </div>
      </header>

      {/* Main */}
      <div className="main-content">
        {loading && <p>Loading...</p>}
        {error && <p className="error">{error}</p>}

        {!selectedService ? renderServicesList() : renderTechniciansList()}
      </div>
    </div>
  );
};

export default ITNetworkingPage;
