import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Internet.css'; // Import external CSS

const ITNetworkingPage = () => {
  const navigate = useNavigate();

  // ================== STATE VARIABLES ==================
  const [selectedService, setSelectedService] = useState(null); // Track the currently selected service/subservice
  const [expandedServices, setExpandedServices] = useState({}); // Track which service categories are expanded
  const [servicesData, setServicesData] = useState({}); // Store services fetched from backend
  const [techniciansData, setTechniciansData] = useState({}); // Store technicians fetched for a subservice
  const [loading, setLoading] = useState(false); // Loading state
  const [error, setError] = useState(null); // Error state

  // ================== FETCH SERVICES ON PAGE LOAD ==================
  useEffect(() => {
    const fetchServices = async () => {
      try {
        setLoading(true);
        setError(null);

        const response = await ax("http://localhost:5000/api/services"); // Backend API call
        const data = await response.json();

        // Convert services array into object { categoryName: [subservices] }
        const formattedData = {};
        data.forEach(service => {
          formattedData[service.name] = service.subservices || [];
        });

        setServicesData(formattedData);
      } catch (err) {
        setError("Failed to fetch services. Please try again.");
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchServices();
  }, []);

  // ================== FETCH TECHNICIANS FOR A SERVICE ==================
  const fetchTechnicians = async (service) => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch(`http://localhost:5000/api/users/${service}`);
      const data = await response.json();

      // Save technicians by service name
      setTechniciansData(prev => ({
        ...prev,
        [service]: data
      }));
    } catch (err) {
      setError("Failed to fetch technicians. Please try again.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // ================== EVENT HANDLERS ==================
  const toggleServiceExpansion = (service) => {
    setExpandedServices(prev => ({
      ...prev,
      [service]: !prev[service] // Toggle open/close state
    }));
  };

  const handleServiceSelect = (service) => {
    setSelectedService(service); // Show technicians list
    fetchTechnicians(service); // Fetch technicians for the selected service
  };

  const handleBackToServices = () => {
    setSelectedService(null); // Go back to main services list
  };

  const handleNavigateToCategory = () => {
    navigate('/categories'); // Navigate back to categories page
  };

  // ================== RENDER ==================
  return (
    <div className="it-networking-page">
      {/* ================== HEADER ================== */}
      <header className="category-header">
        <div className="header-content">
          <button className="back-button" onClick={handleNavigateToCategory}>
            <i className="fas fa-arrow-left"></i> All Categories
          </button>
          <div className="category-icon">
            <i className="fas fa-network-wired"></i>
          </div>
          <h1>IT & Networking</h1>
          <p className="category-description">
            Professional IT and networking services including internet setup, security systems, 
            computer repairs, and more. Connect with skilled technicians in your area.
          </p>
        </div>
      </header>

      {/* ================== MAIN CONTENT ================== */}
      <div className="main-content">
        {/* Loading/Error State */}
        {loading && <p>Loading...</p>}
        {error && <p className="error">{error}</p>}

        {/* Show Services List (if no service is selected) */}
        {!selectedService ? (
          <div className="services-section">
            <h2>Our IT & Networking Services</h2>
            <p className="section-description">
              Select a service category to view available technicians
            </p>
            
            <div className="services-grid">
              {Object.entries(servicesData).map(([service, subservices]) => (
                <div key={service} className="service-card">
                  {/* Category header (expandable if subservices exist) */}
                  <div 
                    className="service-header"
                    onClick={() => subservices.length > 0 && toggleServiceExpansion(service)}
                    style={{ cursor: subservices.length > 0 ? 'pointer' : 'default' }}
                  >
                    <div className="service-icon">
                      {service === 'Internet Services' && <i className="fas fa-wifi"></i>}
                      {service === 'CCTV' && <i className="fas fa-video"></i>}
                      {service === 'Computer Services' && <i className="fas fa-laptop"></i>}
                      {service === 'Alarm System Installation' && <i className="fas fa-shield-alt"></i>}
                      {service === 'POS Installation & Support' && <i className="fas fa-cash-register"></i>}
                      {service === 'Phone Repair' && <i className="fas fa-mobile-alt"></i>}
                      {service === 'TV Repair' && <i className="fas fa-tv"></i>}
                    </div>
                    
                    <h3>{service}</h3>
                    
                    {/* Expand button (only if subservices exist) */}
                    {subservices.length > 0 && (
                      <button 
                        className={`expand-btn ${expandedServices[service] ? 'expanded' : ''}`}
                        onClick={(e) => {
                          e.stopPropagation(); // Prevent parent click
                          toggleServiceExpansion(service);
                        }}
                      >
                        <i className="fas fa-chevron-down"></i>
                      </button>
                    )}
                  </div>
                  
                  {/* Subservices list */}
                  {expandedServices[service] && subservices.length > 0 && (
                    <div className="subservices-list">
                      {subservices.map(subservice => (
                        <div 
                          key={subservice} 
                          className="subservice-item"
                          onClick={() => handleServiceSelect(subservice)}
                        >
                          <i className="fas fa-cog"></i> 
                          <span>{subservice}</span>
                          <i className="fas fa-arrow-right"></i>
                        </div>
                      ))}
                    </div>
                  )}
                  
                  {/* If no subservices exist, show select button */}
                  {subservices.length === 0 && (
                    <button 
                      className="select-service-btn"
                      onClick={() => handleServiceSelect(service)}
                    >
                      Select Service <i className="fas fa-arrow-right"></i>
                    </button>
                  )}
                </div>
              ))}
            </div>
          </div>
        ) : (
          /* Show technicians for the selected service */
          <div className="technicians-section">
            <button className="back-button" onClick={handleBackToServices}>
              <i className="fas fa-arrow-left"></i> Back to Services
            </button>
            
            <h2>Technicians for {selectedService}</h2>
            <p className="technicians-count">
              {techniciansData[selectedService]?.length || 0} technicians available
            </p>
            
            {/* Show technician cards if available */}
            {techniciansData[selectedService]?.length > 0 ? (
              <div className="technicians-grid">
                {techniciansData[selectedService].map(tech => (
                  <div key={tech._id} className="technician-card">
                    <div className="technician-image">
                      <img src={`https://i.pravatar.cc/100?u=${tech._id}`} alt={tech.username} />
                    </div>
                    
                    <div className="technician-info">
                      <h3>{tech.username}</h3>
                      <p className="specialty">{tech.profession}</p>
                      
                      <div className="rating">
                        <span className="stars">‚≠ê {tech.rating?.average || 0}</span>
                        <span className="reviews">({tech.rating?.count || 0} reviews)</span>
                      </div>
                      
                      <div className="details">
                        <p><i className="fas fa-map-marker-alt"></i> {tech.address?.city || "Unknown"} </p>
                        <p><i className="fas fa-clock"></i> {tech.projectRateCategory || "N/A"}</p>
                      </div>
                      
                      <div className="technician-actions">
                        <button className="contact-btn">
                          <i className="fas fa-envelope"></i> Contact
                        </button>
                        <button className="profile-btn">
                          <i className="fas fa-user"></i> View Profile
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              /* No technicians available */
              <div className="no-technicians">
                <i className="fas fa-user-slash"></i>
                <h3>No technicians available</h3>
                <p>No technicians are available for {selectedService} at the moment.</p>
                <p>Please check back later or try a different service.</p>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default ITNetworkingPage;
