// UserIcon.js - Updated with proper profile handling
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './UserIcon.css';

function UserIcon({ isAuthenticated, username, userType, email, onLogin, onLogout, onProfileNavigate }) {
    const navigate = useNavigate();
    const [showDropdown, setShowDropdown] = useState(false);

    const handleLoginClick = () => {
        navigate('/login');
    };

    const handleRegisterClick = () => {
        navigate('/signup'); // Changed to match your signup route
    };

    const handleProfileClick = () => {
        // Navigate to appropriate profile based on user type
        if (userType === 'technician') {
            navigate('/technician-profile');
        } else if (userType === 'client') {
            navigate('/client-profile');
        } else {
            // Fallback to a generic profile page or handle accordingly
            navigate('/profile');
        }
        
        if (onProfileNavigate) {
            onProfileNavigate();
        }
        setShowDropdown(false);
    };

    const handleLogoutClick = () => {
        if (onLogout) {
            onLogout();
        }
        setShowDropdown(false);
        navigate('/'); // Redirect to home after logout
    };

    const toggleDropdown = () => {
        setShowDropdown(!showDropdown);
    };

    return (
        <div className="user-icon">
            {!isAuthenticated ? (
                <div className="auth-buttons">
                    <button onClick={handleLoginClick} className="login-btn">Login</button>
                    <button onClick={handleRegisterClick} className="register-btn">Register</button>
                </div>
            ) : (
                <div className="user-menu">
                    <div className="user-info" onClick={toggleDropdown}>
                        <span className="username">Hello, {username}</span>
                        <div className="avatar">
                            {username ? username.charAt(0).toUpperCase() : 'U'}
                        </div>
                    </div>
                    
                    {showDropdown && (
                        <div className="dropdown-menu">
                            <div className="user-details">
                                <div className="user-email">{email}</div>
                                <div className="user-role">{userType ? userType.charAt(0).toUpperCase() + userType.slice(1) : 'User'}</div>
                            </div>
                            <div className="dropdown-divider"></div>
                            <div className="dropdown-item" onClick={handleProfileClick}>
                                My Profile
                            </div>
                            <div className="dropdown-item" onClick={handleLogoutClick}>
                                Logout
                            </div>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

export default UserIcon;
