import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import './TechnicianLocation.css';

const TechnicianLocation = () => {
  const { category, service } = useParams();
  const navigate = useNavigate();
  const [technicians, setTechnicians] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [distanceTier, setDistanceTier] = useState('5-10');
  const [technicianCounts, setTechnicianCounts] = useState({});
  const [showDistanceModal, setShowDistanceModal] = useState(false);
  const [responseData, setResponseData] = useState({}); // Added to fix 'data' undefined error

  // Distance tier options
  const distanceTiers = [
    { value: '1-5', label: '1-5 km', description: 'Very close to you' },
    { value: '5-10', label: '5-10 km', description: 'Close to your area' },
    { value: '10-50', label: '10-50 km', description: 'Within your city' },
    { value: '50-100', label: '50-100 km', description: 'Wider area search' },
    { value: '100+', label: '100+ km', description: 'Entire region' }
  ];

  // Get user location - wrapped in useCallback to fix dependency warning
  const getUserLocation = useCallback(() => {
    if (!navigator.geolocation) {
      setError('Geolocation is not supported by your browser');
      return;
    }

    setLoading(true);
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        setUserLocation({ lat: latitude, lng: longitude });
        fetchTechnicianCounts(latitude, longitude);
        fetchTechnicians(latitude, longitude);
      },
      (error) => {
        setError('Unable to retrieve your location. Showing all technicians.');
        fetchTechnicians();
      }
    );
  }, [category, service]);

  // Fetch technician counts by distance
  const fetchTechnicianCounts = async (lat, lng) => {
    try {
      const params = {
        subService: service,
        category: category,
        lat,
        lng
      };

      const response = await axios.get('http://localhost:5000/api/techlocation/counts', { params });
      setTechnicianCounts(response.data.counts);
    } catch (err) {
      console.error('Error fetching technician counts:', err);
    }
  };

  // Fetch technicians
  const fetchTechnicians = async (lat = null, lng = null) => {
    try {
      setLoading(true);
      const params = {
        subService: service,
        category: category,
        distanceTier,
        page: 1,
        limit: 50
      };

      if (lat && lng) {
        params.lat = lat;
        params.lng = lng;
      }

      const response = await axios.get('http://localhost:5000/api/techlocation/service', { params });
      setTechnicians(response.data.technicians);
      setResponseData(response.data); // Store the full response data
      setError(null);
    } catch (err) {
      setError('Failed to fetch technicians');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleDistanceTierChange = (newTier) => {
    setDistanceTier(newTier);
    setShowDistanceModal(false);
    
    if (userLocation) {
      fetchTechnicians(userLocation.lat, userLocation.lng);
    } else {
      fetchTechnicians();
    }
  };

  useEffect(() => {
    getUserLocation();
  }, [getUserLocation]); // Fixed: Added getUserLocation to dependency array

  return (
    <div className="service-technicians">
      {/* Header */}
      <div className="service-header">
        <button className="back-button" onClick={() => navigate(-1)}>
          <i className="fas fa-arrow-left"></i> Back
        </button>
        
        <div className="service-info">
          <h1>{service} Services</h1>
          <p className="category-badge">{category}</p>
        </div>

        {/* Distance Filter */}
        <div className="distance-filter-section">
          <div className="location-status">
            {userLocation ? (
              <span className="location-active">
                <i className="fas fa-map-marker-alt"></i> Using your location
              </span>
            ) : (
              <span className="location-inactive">
                <i className="fas fa-map-marker-alt"></i> Location unavailable
              </span>
            )}
          </div>

          <div className="distance-selector">
            <button 
              className="distance-dropdown-btn"
              onClick={() => setShowDistanceModal(!showDistanceModal)}
            >
              <i className="fas fa-ruler"></i>
              {distanceTiers.find(t => t.value === distanceTier)?.label}
              <i className="fas fa-chevron-down"></i>
            </button>

            {showDistanceModal && (
              <div className="distance-modal">
                <div className="modal-header">
                  <h3>Search Radius</h3>
                  <button onClick={() => setShowDistanceModal(false)}>×</button>
                </div>
                <div className="distance-options">
                  {distanceTiers.map(tier => (
                    <button
                      key={tier.value}
                      className={`distance-option ${distanceTier === tier.value ? 'active' : ''}`}
                      onClick={() => handleDistanceTierChange(tier.value)}
                    >
                      <div className="tier-info">
                        <span className="tier-label">{tier.label}</span>
                        <span className="tier-description">{tier.description}</span>
                      </div>
                      <div className="tier-count">
                        {technicianCounts[tier.value] || 0} techs
                      </div>
                    </button>
                  ))}
                </div>
              </div>
            )}
          </div>

          <button className="refresh-location-btn" onClick={getUserLocation}>
            <i className="fas fa-sync-alt"></i>
          </button>
        </div>
      </div>

      {/* Premium Visibility Notice */}
      <div className="premium-notice">
        <div className="notice-content">
          <i className="fas fa-crown"></i>
          <div>
            <strong>Premium Technicians</strong> appear first in search results. 
            <span className="premium-learn-more" onClick={() => navigate('/premium-info')}>
              Learn about visibility packages
            </span>
          </div>
        </div>
      </div>

      {/* Results Section - Fixed: Removed comment and fixed data reference */}
      {!loading && (
        <div className="results-section">
          <div className="results-header">
            <h2>
              {technicians.length} {service} Technicians 
              {userLocation && ` within ${distanceTiers.find(t => t.value === distanceTier)?.label}`}
            </h2>
            
            {/* Breakdown Info - Fixed: Use responseData instead of undefined 'data' */}
            <div className="results-breakdown">
              <span className="breakdown-item">
                <span className="premium-indicator"></span>
                {responseData.premiumCount || 0} Premium
              </span>
              <span className="breakdown-item">
                <span className="regular-indicator"></span>
                {responseData.regularCount || 0} Nearby
              </span>
            </div>
          </div>

          {/* Enhanced technician cards with clear indicators */}
          <div className="technicians-grid">
            {technicians.map(technician => (
              <div key={technician._id} className={`technician-card ${technician.isPremiumActive ? 'premium' : 'regular'}`}>
                
                {/* Premium Badge */}
                {technician.isPremiumActive && (
                  <div className="premium-badge">
                    <i className="fas fa-crown"></i> 
                    {technician.visibilityTier === 'featured' ? 'Featured' : 'Premium'}
                    <span className="badge-tooltip">
                      {technician.visibilityTier === 'featured' 
                        ? 'Featured technician - visible everywhere' 
                        : 'Premium technician - visible everywhere'
                      }
                    </span>
                  </div>
                )}

                {/* Regular Nearby Badge */}
                {!technician.isPremiumActive && technician.displayDistance && (
                  <div className="nearby-badge">
                    <i className="fas fa-map-marker-alt"></i> 
                    {technician.displayDistance?.toFixed(1)} km away
                  </div>
                )}

                {/* Distance indicator for premium techs */}
                {technician.isPremiumActive && technician.displayDistance && (
                  <div className="premium-distance">
                    <i className="fas fa-bolt"></i>
                    {technician.displayDistance?.toFixed(1)} km away
                    <span className="distance-tooltip">
                      Premium technician - appears regardless of distance
                    </span>
                  </div>
                )}

                {/* Technician Image */}
                <div className="technician-image">
                  <img 
                    src={technician.profilePicture || 'https://i.pravatar.cc/150'} 
                    alt={technician.username}
                  />
                </div>
                
                <div className="technician-info">
                  <h3>{technician.username}</h3>
                  <p className="profession">{technician.professions?.[0] || 'Technician'}</p>
                        
                  <div className="rating">
                    <span className="stars">⭐ {technician.rating?.average?.toFixed(1) || '0.0'}</span>
                    <span className="reviews">({technician.rating?.count || 0} reviews)</span>
                  </div>

                  <div className="services">
                    {technician.subServices?.slice(0, 2).map((serviceItem, index) => (
                      <span key={index} className="service-tag">{serviceItem}</span>
                    ))}
                  </div>

                  <div className="location">
                    <i className="fas fa-map-marker-alt"></i>
                    {technician.address?.city || 'Location not specified'}
                  </div>

                  <div className="rate-indicator">
                    {technician.projectRateCategory && (
                      <span className="rate-tag">
                        {technician.projectRateCategory === 'under_10000' && 'Under Ksh 10,000'}
                        {technician.projectRateCategory === '10000_to_100000' && 'Ksh 10,000-100,000'}
                        {technician.projectRateCategory === '100000_to_1000000' && 'Ksh 100,000-1M'}
                        {technician.projectRateCategory === 'over_1000000' && 'Over Ksh 1M'}
                      </span>
                    )}
                  </div>

                  <button 
                    className="view-profile-btn"
                    onClick={() => navigate(`/technician/${technician._id}`)}
                  >
                    View Profile
                  </button>
                </div>
              </div>
            ))}
          </div>

          {technicians.length === 0 && !loading && (
            <div className="no-results">
              <i className="fas fa-search"></i>
              <h3>No technicians found</h3>
              <p>Try adjusting your search radius or check back later for new technicians in your area.</p>
              <button className="expand-search-btn" onClick={() => setDistanceTier('100+')}>
                Expand search to entire region
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default TechnicianLocation;
