import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import Navbar from './Components/Navbar/Navbar';

import Home from './Pages/Home/Home';
import MyApplication from './Pages/My-Application/MyApps';
import Shifts from './Pages/Shifts/Shifts';
import InternetStatus from './Pages/InternetStatus/InternetStatus';
import Chat from './Pages/Chats/Chats';
import Login from './Pages/Login/Login';
import Signup from './Pages/Signup/Signup';
import TechnicianProfile from './Protected_Apps/TechnicianProfile/TechnicianProfile';
import TechnicianTickets from './Protected_Apps/Tickets/TechnicianTickets';

// Add ProtectedRoute component
const ProtectedRoute = ({ children }) => {
  const [isAuthenticated] = useState(false); // You should replace this with your actual auth state

  if (!isAuthenticated) {
    return <Navigate to="/login" replace />;
  }

  return children;
};

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [loading, setLoading] = useState(true);
  const [authState, setAuthState] = useState({}); // Added authState

  const handleLogin = (user) => {
    setIsAuthenticated(true);
    setUsername(user.username);
    setAuthState(user.username); // Set the authState when user logs in
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setUsername('');
    setAuthState({}); // Clear authState on logout
  };

  return (
    <Router>
      <Navbar isAuthenticated={isAuthenticated} username={username} onLogout={handleLogout} />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/my-application" element={<MyApplication />}>
          <Route path="tickets" element={<TechnicianTickets username={username} />} />
        </Route>
        <Route path="/shifts" element={<Shifts />} />
        <Route path="/internet-status" element={<InternetStatus />} />
        <Route path="/chat" element={<Chat />} />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/signup" element={<Signup />} />
        <Route 
          path="/technicianprofile" 
          element={
            <ProtectedRoute>
              <TechnicianProfile user={authState} />
            </ProtectedRoute>
          } 
        />
      </Routes>
    </Router>
  );
}

export default App;
