import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css';
import UserIcon from '../UserIcon/UserIcon';

/**
 * Navbar Component - Main navigation bar for the application
 * Handles:
 * - Navigation between different pages
 * - User authentication state
 * - User login/logout functionality
 */
function Navbar({ isAuthenticated, user, onLogout }) {
    // State for the dynamic title of the My Application button
    const [title, setTitle] = useState('My Application');
    const [Category, setCategory] = useState('Category');
    const [NearbyTechs, setNearbyTechs] = useState('Technicians Near Me');
    
    // Hook for programmatic navigation
    const navigate = useNavigate();

    // Handle authentication state changes
    useEffect(() => {
        console.log('Navbar - Auth state updated:', { isAuthenticated, user });
    }, [isAuthenticated, user]);

    const handleProfileNavigation = () => {
        if (user?.userType === 'technician') {
            navigate(`/techniciancategoryprofile/${user.id || user._id}`);
        } else {
            navigate('/client-profile');
        }
    };

    /**
     * Handles click on the Category button
     */
    const handleCategoryClick = () => {
        navigate('/category');
    };

    const handleNearbyTechsClick = () => {
        navigate('/nearbytechnician');
    };

    const handleMyApplicationClick = () => {
        if (isAuthenticated) {
            navigate('/my-application');
        } else {
            navigate('/login');
        }
    };

    /**
     * Login handler - now handled by parent App.js
     * This is kept for compatibility with UserIcon if needed
     */
    const handleLogin = (userData) => {
        // This is now handled by the parent App.js component
        console.log('Login should be handled by parent component');
    };

    /**
     * Logout handler - calls parent's logout function
     */
    const handleLogout = () => {
        onLogout();
        navigate('/');
    };

    return (
        <nav className="navbar">
            {/* Main navigation links */}
            <ul>
                {/* Home link */}
                <li><Link to="/">Home</Link></li>

                {/* Category button */}
                <li>
                    <button onClick={handleCategoryClick}>
                        {Category}
                    </button>
                </li>

                {/* Nearby Technicians button */}
                <li>
                    <button onClick={handleNearbyTechsClick}>
                        {NearbyTechs}
                    </button>
                </li>
                
                {/* My Application button with dynamic text */}
                <li>
                    <button onClick={handleMyApplicationClick}>
                        {title}
                    </button>
                </li>
                
                {/* Other navigation links */}
                {isAuthenticated && (
                    <>
                        <li><Link to="/shifts">Shifts</Link></li>
                        <li><Link to="/internet-status">Internet Status</Link></li>
                        <li><Link to="/chat">Chat</Link></li>
                    </>
                )}
            </ul>

            {/* User authentication section (right-aligned) */}
            <div className="user-icon-container">
                <UserIcon 
                    isAuthenticated={isAuthenticated}
                    username={user?.username}
                    email={user?.email}
                    userType={user?.userType}
                    onLogin={handleLogin}        // Kept for compatibility
                    onLogout={handleLogout}
                    onProfileNavigate={handleProfileNavigation}
                />
            </div>
        </nav>
    );
}

export default Navbar;
