// components/Profile.js
import { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './TechnicianProfile.css';

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const [isEditingAbout, setIsEditingAbout] = useState(false);
  const [aboutText, setAboutText] = useState(profile?.about || '');
  const [newExperience, setNewExperience] = useState({
    name: '',
    category: '',
    experience: ''
  });

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/users/profile', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        });
        setProfile(response.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load profile');
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);


  const handleSaveAbout = async () => {
    try {
      const response = await axios.patch('http://localhost:5000/api/users/profile', 
        { about: aboutText },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        }
      );
      setProfile(response.data);
      setIsEditingAbout(false);
    } catch (error) {
      console.error('Error updating about section:', error);
    }};

    const handleAddExperience = async () => {
      try {
        const response = await axios.patch(
          'http://localhost:5000/api/users/profile',
          { $push: { professionalExperience: newExperience } },
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem('token')}`
            }
          }
        );
        setProfile(response.data);
        setNewExperience({ name: '', category: '', experience: '' });
      } catch (error) {
        console.error('Error adding experience:', error);
      }
    };


  if (loading) return <div className="loading-spinner">Loading...</div>;
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="profile-page">
      <div className="profile-header">
        <div className="profile-image-container">
          <img
            src={profile.profilePicture || '/default-profile.png'}
            alt="Profile"
            className="profile-image"
          />
          <button 
            className="edit-image-btn"
            onClick={() => navigate('/profile/upload-image')}
          >
            Add Photo
          </button>
        </div>
        
        <div className="profile-info">
          <h1>{profile.username}</h1>
          <p className="user-type">{profile.userType}</p>

          {/* Display City and State if available */}
        {(profile.address?.city || profile.address?.state) && (
          <div className="location-text">
            {profile.address.city}{profile.address.city && profile.address.state ? ', ' : ''}
            {profile.address.state}
          </div>
        )}

        {/* Add Location Display Here */}
        {profile.location && profile.location.coordinates && (
          // Modify the location-info div to be clickable
<div 
  className="location-info clickable"
  onClick={() => {
    const [longitude, latitude] = profile.location.coordinates;
    window.open(`https://www.google.com/maps?q=${latitude},${longitude}`);
  }}
>
  <span className="location-icon">üìç</span>
  <span className="coordinates">
    Lat: {profile.location.coordinates[1].toFixed(4)}, 
    Long: {profile.location.coordinates[0].toFixed(4)}
  </span>
</div>
        )}

        {/* Ratings Display */}
<div className="ratings-display">
  <div className="stars">
    {[1, 2, 3, 4, 5].map((star) => (
      <span 
        key={star} 
        className={`star ${star <= Math.round(profile.rating?.average || 0) ? 'filled' : ''}`}
      >
        ‚òÖ
      </span>
    ))}
  </div>
  <span className="rating-text">
    ({profile.rating?.average?.toFixed(1) || '0.0'}) from {profile.rating?.count || 0} reviews
  </span>
</div>


          {profile.skills?.length > 0 && (
            <div className="skills-section">
              <h3>Skills</h3>
              <div className="skills-list">
                {profile.skills.map((skill, index) => (
                  <span key={index} className="skill-tag">
                    {skill.name} ({skill.experience} yrs)
                  </span>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>

      <div className="profile-details">
        <div className="detail-section">
          <h2>Contact Information</h2>
          <p><strong>Email:</strong> {profile.email}</p>
          <p><strong>Phone:</strong> {profile.phoneNumber}</p>
        </div>

        <div className="detail-section">
          <div className="section-header">
            <h2>About Me</h2>
            {!isEditingAbout ? (
              <button 
                className="edit-btn"
                onClick={() => setIsEditingAbout(true)}
              >
                Edit
              </button>
            ) : (
              <button 
                className="save-btn"
                onClick={handleSaveAbout}
              >
                Save
              </button>
            )}
          </div>
          
          {isEditingAbout ? (
            <textarea
              className="about-textarea"
              value={aboutText}
              onChange={(e) => setAboutText(e.target.value)}
              maxLength={500}
              placeholder="Tell us about yourself..."
            />
          ) : (
            <p className="about-text">
              {profile.about || 'No information provided yet.'}
            </p>
          )}
          {isEditingAbout && (
            <div className="character-count">
              {aboutText.length}/500 characters
            </div>
          )}
        </div>


      {/* Professional Experience Section */}
<div className="detail-section">
  <h2>S</h2>
  {profile.professionalExperience?.length > 0 ? (
    <div className="experience-container">
      {profile.professionalExperience.map((exp, index) => (
        <div key={index} className="experience-item">
          <div className="experience-details">
            <strong>{exp.name}</strong>
            {exp.category && <span> ({exp.category})</span>}
            {exp.experience && <span> - {exp.experience} year(s)</span>}
          </div>
        </div>
      ))}
    </div>
  ) : (
    <p>No professional experience added yet</p>
  )}

  {/* Add New Professional Experience Form */}
  <div className="add-experience-form">
    <h3>Add Professional Experience</h3>
    <div className="form-group">
      <input
        type="text"
        placeholder="Profession Name (e.g., Electrical Engineer)"
        value={newExperience.name}
        onChange={(e) => setNewExperience({...newExperience, name: e.target.value})}
      />
    </div>
    <div className="form-group">
      <input
        type="text"
        placeholder="Specialization/Category (optional)"
        value={newExperience.category}
        onChange={(e) => setNewExperience({...newExperience, category: e.target.value})}
      />
    </div>
    <div className="form-group">
      <input
        type="number"
        placeholder="Years of Experience"
        value={newExperience.experience}
        onChange={(e) => setNewExperience({...newExperience, experience: e.target.value})}
        min="0"
      />
    </div>
    <button 
      className="add-experience-btn"
      onClick={handleAddExperience}
      disabled={!newExperience.name || !newExperience.experience}
    >
      Add Experience
    </button>
  </div>
</div>
      </div>
    </div>
  );
};

export default Profile;
