import { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './TechnicianProfile.css';

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    professions: [],
    address: {
      street: '',
      city: '',
      state: '',
      zipCode: ''
    },
    email: '',
    phoneNumber: '',
    projectRateCategory: '',
    about: ''
  });
  const [newProfession, setNewProfession] = useState('');

  // Available professions from your backend model
  const availableProfessions = [
    'electrician', 'network technician', 'IT technician', 'CCTV Technician',
    'Alarm Technician', 'POS Technician', 'Computer Technician', 'Phone Technician',
    'software engineer', 'plumber', 'carpenter', 'mechanic', 'painter', 'other'
  ];

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          setError('No authentication token found');
          setLoading(false);
          return;
        }

        const response = await axios.get('http://localhost:5000/api/users/profile', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        
        setProfile(response.data);
        setFormData({
          username: response.data.username || '',
          professions: response.data.professions || [],
          address: response.data.address || {
            street: '',
            city: '',
            state: '',
            zipCode: ''
          },
          email: response.data.email || '',
          phoneNumber: response.data.phoneNumber || '',
          projectRateCategory: response.data.projectRateCategory || '',
          about: response.data.about || ''
        });
      } catch (err) {
        console.error('Profile fetch error:', err);
        setError(err.response?.data?.message || 'Failed to load profile');
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleAddressChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      address: { ...prev.address, [name]: value }
    }));
  };

  const addProfession = () => {
    if (newProfession && !formData.professions.includes(newProfession)) {
      setFormData(prev => ({
        ...prev,
        professions: [...prev.professions, newProfession]
      }));
      setNewProfession('');
    }
  };

  const removeProfession = (profession) => {
    setFormData(prev => ({
      ...prev,
      professions: prev.professions.filter(p => p !== profession)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put( // Changed from PATCH to PUT
        'http://localhost:5000/api/users/profile',
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`
          }
        }
      );
      setProfile(response.data.user || response.data);
      setIsEditing(false);
      // Show success message
      alert('Profile updated successfully!');
    } catch (error) {
      console.error('Error updating profile:', error);
      setError(error.response?.data?.message || 'Failed to update profile');
    }
  };

  const handleCancelEdit = () => {
    // Reset form data to current profile data
    setFormData({
      username: profile.username || '',
      professions: profile.professions || [],
      address: profile.address || {
        street: '',
        city: '',
        state: '',
        zipCode: ''
      },
      email: profile.email || '',
      phoneNumber: profile.phoneNumber || '',
      projectRateCategory: profile.projectRateCategory || '',
      about: profile.about || ''
    });
    setIsEditing(false);
    setError(null);
  };

  const handleProfilePictureUpdate = async (file) => {
    try {
      const token = localStorage.getItem('token');
      const formData = new FormData();
      formData.append('profilePicture', file);

      const response = await axios.post(
        'http://localhost:5000/api/users/profile/picture',
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          }
        }
      );

      // Update profile picture in state
      setProfile(prev => ({
        ...prev,
        profilePicture: response.data.profilePicture
      }));
      alert('Profile picture updated successfully!');
    } catch (error) {
      console.error('Error updating profile picture:', error);
      setError(error.response?.data?.message || 'Failed to update profile picture');
    }
  };

  if (loading) return <div className="loading-spinner">Loading...</div>;
  if (error && !profile) return <div className="error-message">{error}</div>;

  return (
    <div className="profile-page">
      {/* Top section with profile picture and basic info */}
      <div className="profile-top-section">
        {/* Edit button at top-right */}
        <div className="profile-actions">
          {isEditing ? (
            <div className="edit-actions">
              <button 
                className="save-btn"
                onClick={handleSubmit}
              >
                Save Changes
              </button>
              <button 
                className="cancel-btn"
                onClick={handleCancelEdit}
              >
                Cancel
              </button>
            </div>
          ) : (
            <button 
              className="edit-btn"
              onClick={() => setIsEditing(true)}
            >
              Edit Profile
            </button>
          )}
        </div>

        {/* Left column - profile picture and ratings */}
        <div className="profile-image-column">
          <div className="profile-image-container">
            <img
              src={profile?.profilePicture ? `http://localhost:5000${profile.profilePicture}` : '/default-profile.png'}
              alt="Profile"
              className="profile-image"
              onError={(e) => {
                e.target.src = '/default-profile.png';
              }}
            />
            {isEditing && (
              <div className="image-upload-section">
                <input
                  type="file"
                  id="profile-picture-upload"
                  accept="image/*"
                  onChange={(e) => {
                    if (e.target.files[0]) {
                      handleProfilePictureUpdate(e.target.files[0]);
                    }
                  }}
                  style={{ display: 'none' }}
                />
                <label htmlFor="profile-picture-upload" className="edit-image-btn">
                  {profile?.profilePicture ? 'Change Photo' : 'Add Photo'}
                </label>
              </div>
            )}
          </div>
          
          {/* Ratings section - only show for technicians */}
          {profile?.userType === 'technician' && (
            <div className="ratings-display">
              <div className="stars">
                {[1, 2, 3, 4, 5].map((star) => (
                  <span 
                    key={star} 
                    className={`star ${star <= Math.round(profile.rating?.average || 0) ? 'filled' : ''}`}
                  >
                    ★
                  </span>
                ))}
              </div>
              <span className="rating-text">
                ({profile.rating?.average?.toFixed(1) || '0.0'}) from {profile.rating?.count || 0} reviews
              </span>
            </div>
          )}
        </div>

        {/* Right column - basic info */}
        <div className="basic-info-column">
          {isEditing ? (
            <input
              type="text"
              name="username"
              value={formData.username}
              onChange={handleInputChange}
              className="edit-name-input"
              placeholder="Username"
            />
          ) : (
            <h1>{profile?.username}</h1>
          )}
          <p className="user-type">{profile?.userType}</p>
          
          <div className="location-info">
            {isEditing ? (
              <div className="address-fields">
                <input
                  type="text"
                  name="street"
                  value={formData.address.street}
                  onChange={handleAddressChange}
                  placeholder="Street Address"
                  className="address-input"
                />
                <div className="address-row">
                  <input
                    type="text"
                    name="city"
                    value={formData.address.city}
                    onChange={handleAddressChange}
                    placeholder="City"
                    className="address-input"
                  />
                  <input
                    type="text"
                    name="state"
                    value={formData.address.state}
                    onChange={handleAddressChange}
                    placeholder="State"
                    className="address-input"
                  />
                  <input
                    type="text"
                    name="zipCode"
                    value={formData.address.zipCode}
                    onChange={handleAddressChange}
                    placeholder="ZIP Code"
                    className="address-input"
                  />
                </div>
              </div>
            ) : (
              <>
                {profile?.address?.street && <div>{profile.address.street}</div>}
                {(profile?.address?.city || profile?.address?.state) && (
                  <div>
                    {profile.address.city}{profile.address.city && profile.address.state ? ', ' : ''}
                    {profile.address.state} {profile.address.zipCode}
                  </div>
                )}
                {!profile?.address?.street && !profile?.address?.city && !profile?.address?.state && (
                  <div>No address provided</div>
                )}
              </>
            )}
          </div>

          <div className="contact-info">
            <div className="form-group">
              <label>Email</label>
              {isEditing ? (
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  className="edit-input"
                />
              ) : (
                <p>{profile?.email}</p>
              )}
            </div>
            <div className="form-group">
              <label>Phone Number</label>
              {isEditing ? (
                <input
                  type="tel"
                  name="phoneNumber"
                  value={formData.phoneNumber}
                  onChange={handleInputChange}
                  className="edit-input"
                />
              ) : (
                <p>{profile?.phoneNumber}</p>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Error message display */}
      {error && (
        <div className="error-message" style={{ margin: '20px 0', padding: '10px', background: '#ffe6e6', color: '#d00', borderRadius: '5px' }}>
          {error}
        </div>
      )}

      {/* Skills section */}
      <div className="skills-section">
        <div className="section-header">
          <h2>Professional Information</h2>
        </div>

        <div className="form-group">
          <label>Profession(s)</label>
          {isEditing ? (
            <div className="professions-edit">
              <div className="professions-tags">
                {formData.professions.map((profession, index) => (
                  <span key={index} className="profession-tag">
                    {profession}
                    <button 
                      type="button" 
                      onClick={() => removeProfession(profession)}
                      className="remove-profession"
                    >
                      ×
                    </button>
                  </span>
                ))}
              </div>
              <div className="add-profession">
                <select
                  value={newProfession}
                  onChange={(e) => setNewProfession(e.target.value)}
                  className="profession-select"
                >
                  <option value="">Select a profession</option>
                  {availableProfessions.map(prof => (
                    <option key={prof} value={prof}>
                      {prof.charAt(0).toUpperCase() + prof.slice(1)}
                    </option>
                  ))}
                </select>
                <button 
                  type="button" 
                  onClick={addProfession}
                  disabled={!newProfession}
                  className="add-profession-btn"
                >
                  Add
                </button>
              </div>
            </div>
          ) : (
            <div className="professions-display">
              {profile?.professions?.length > 0 ? (
                profile.professions.map((profession, index) => (
                  <span key={index} className="profession-tag">
                    {profession}
                  </span>
                ))
              ) : (
                <p>No professions added</p>
              )}
            </div>
          )}
        </div>

        {/* Project Rate Category - for technicians only */}
        {profile?.userType === 'technician' && (
          <div className="form-group">
            <label>Project Rate Category</label>
            {isEditing ? (
              <div className="rate-options">
                <label className="rate-option">
                  <input
                    type="radio"
                    name="projectRateCategory"
                    value="under_10000"
                    checked={formData.projectRateCategory === 'under_10000'}
                    onChange={handleInputChange}
                  />
                  Under Ksh 10,000
                </label>
                <label className="rate-option">
                  <input
                    type="radio"
                    name="projectRateCategory"
                    value="10000_to_100000"
                    checked={formData.projectRateCategory === '10000_to_100000'}
                    onChange={handleInputChange}
                  />
                  Ksh 10,000-100,000
                </label>
                <label className="rate-option">
                  <input
                    type="radio"
                    name="projectRateCategory"
                    value="100000_to_1000000"
                    checked={formData.projectRateCategory === '100000_to_1000000'}
                    onChange={handleInputChange}
                  />
                  Ksh 100,000-1,000,000
                </label>
                <label className="rate-option">
                  <input
                    type="radio"
                    name="projectRateCategory"
                    value="over_1000000"
                    checked={formData.projectRateCategory === 'over_1000000'}
                    onChange={handleInputChange}
                  />
                  Over Ksh 1,000,000
                </label>
              </div>
            ) : (
              <div className="rate-display">
                {profile.projectRateCategory ? (
                  <span className="rate-tag">
                    {(() => {
                      switch(profile.projectRateCategory) {
                        case 'under_10000': return 'Under Ksh 10,000';
                        case '10000_to_100000': return 'Ksh 10,000-100,000';
                        case '100000_to_1000000': return 'Ksh 100,000-1,000,000';
                        case 'over_1000000': return 'Over Ksh 1,000,000';
                        default: return 'Not specified';
                      }
                    })()}
                  </span>
                ) : (
                  <p>No rate category specified</p>
                )}
              </div>
            )}
          </div>
        )}
      </div>

      {/* About me section */}
      <div className="about-section">
        <h2>About Me</h2>
        {isEditing ? (
          <>
            <textarea
              className="about-textarea"
              name="about"
              value={formData.about}
              onChange={handleInputChange}
              maxLength={500}
              placeholder="Tell us about yourself, your experience, and what makes you great at what you do..."
              rows={4}
            />
            <div className="character-count">
              {formData.about.length}/500 characters
            </div>
          </>
        ) : (
          <p className="about-text">
            {profile?.about || 'No information provided yet.'}
          </p>
        )}
      </div>
    </div>
  );
};

export default Profile;
