import React, { useEffect, useState } from 'react'; // Importing React and necessary hooks
import axios from 'axios'; // Importing axios for API requests
import { toast, ToastContainer } from 'react-toastify'; // Importing toast notifications
import 'react-toastify/dist/ReactToastify.css'; // Importing toast styles
import './TechnicianTickets.css'

const TechnicianTickets = () => {
  // Defining state variables
  const [tickets, setTickets] = useState([]); // Stores assigned tickets
  const [loading, setLoading] = useState(true); // Tracks loading state
  const [error, setError] = useState(''); // Stores error messages
  const [selectedTicket, setSelectedTicket] = useState(null); // Stores the currently selected ticket
  const [status, setStatus] = useState(null); // Tracks the status of the selected ticket
  const [macAddress, setMacAddress] = useState(''); // Stores MAC address input
  const [signalReceived, setSignalReceived] = useState(''); // Stores signal received input
  const [bomUsed, setBomUsed] = useState(''); // Stores BOM used input
  const [additionalNotes, setAdditionalNotes] = useState(''); // Stores additional notes input

  useEffect(() => {
    // Fetching assigned tickets when component mounts or status updates
    const fetchTickets = async () => {
      try {
        const token = localStorage.getItem('token'); // Retrieve authentication token
        const response = await axios.get('http://localhost:5000/api/fetchtickets/assigned', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setTickets(response.data); // Updating ticket state with API response
      } catch (err) {
        setError(err.response?.data?.message || 'Error fetching tickets'); // Handling errors
      } finally {
        setLoading(false); // Setting loading to false after request completes
      }
    };

    fetchTickets(); // Calling function to fetch tickets
  }, [status]); // Runs effect when status updates

  // Handles clicking on a ticket
  const handleTicketClick = (ticket) => {
    setSelectedTicket(ticket); // Sets the selected ticket
    setStatus(ticket.status || null); // Sets the ticket status
  };

  // Defines the flow of ticket statuses
  const statusFlow = ["Seen", "Leaving for Site", "Enroute", "Arrived on Site", "Submit Details", "Complete"];

  // Handles status change
  

  if (loading) return <div>Loading tickets...</div>; // Display loading state
  if (error) return <div>Error: {error}</div>; // Display error state

  return (
    <div className="p-4 border rounded shadow-md w-96">
      <ToastContainer /> {/* Toast notifications container */}
      <h2 className="text-lg font-bold mb-2">Assigned Tickets</h2>
  
      {tickets.length === 0 ? (
        <p>No tickets assigned to you.</p>
      ) : (
        <div className="ticket-buttons">
          {tickets.map((ticket) => (
            <button key={ticket._id} className="ticket-button" onClick={() => handleTicketClick(ticket)}>
              {ticket.clientName} {/* Display client name */}
            </button>
          ))}
        </div>
      )}
  
      {selectedTicket && (
        <div className="ticket-details">
          <h3>Ticket Details</h3>
          <p><strong>Status:</strong> 
            <select value={status || ''} onChange={handleStatusChange}>
              {statusFlow.slice(statusFlow.indexOf(status)).map((s, index) => (
                <option key={index} value={s}>{s}</option>
              ))}
            </select>
          </p>
  
         {/* Show different details based on status */}
{status === "Arrived on Site" && (
  <div className="site-details">
    <p><strong>Client Name:</strong> {selectedTicket?.clientName}</p>
    <p><strong>Mobile Number:</strong> {selectedTicket?.mobileNumber}</p>
    <p><strong>Installation Type:</strong> {selectedTicket?.installationType}</p>
    <p><strong>Assigned Technician:</strong> {selectedTicket?.assignedTechnician?.username}</p>

    {/* Ensure PPPoE credentials exist before displaying */}
    {selectedTicket?.pppoeCredentials && (
      <p>
        <strong>PPPoE Credentials:</strong> <br />
        <strong>Username:</strong> {selectedTicket.pppoeCredentials.username} <br />
        <strong>Password:</strong> {selectedTicket.pppoeCredentials.password}
      </p>
    )}

    <p><strong>Location:</strong> {selectedTicket?.location}</p>
    <p><strong>Created At:</strong> {new Date(selectedTicket?.createdAt).toLocaleDateString()}</p>
  </div>
)}

          {status === "Submit Details" && (
            <div className="submit-details">
              <h4>Submit Installation Details</h4>
              <label>Speedtest Screenshot: <input type="file" accept="image/*" /></label>
              <label>WAN Photo: <input type="file" accept="image/*" /></label>
              <label>LAN Photo: <input type="file" accept="image/*" /></label>
              <label>MAC Address of Router: <input type="text" value={macAddress} onChange={(e) => setMacAddress(e.target.value)} /></label>
              <label>Signal Received: <input type="text" value={signalReceived} onChange={(e) => setSignalReceived(e.target.value)} /></label>
              <label>BOM Used: <input type="text" value={bomUsed} onChange={(e) => setBomUsed(e.target.value)} /></label>
              <label>Additional Notes: <textarea value={additionalNotes} onChange={(e) => setAdditionalNotes(e.target.value)} /></label>
              <button onClick={() => handleStatusChange()}>Submit</button>
            </div>
          )}
  
          {status === "Complete" && (
            <button onClick={() => handleStatusChange()}>Close Ticket</button>
          )}
        </div>
      )}
    </div>
  );
  
};

export default TechnicianTickets;
