import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Signup.css';

const Signup = ({ onSuccessfulSignup }) => {
    const navigate = useNavigate();
    
    // Initial form state with removed fields and added profession field
    const initialFormState = {
        username: '',
        password: '',
        email: '',
        phoneNumber: '',
        userType: 'client', // Default to client
        profession: '', // New field for technician's profession/category
        address: {
            street: '',
            city: '',
            state: '',
            zipCode: ''
        }
    };

    // State management
    const [formData, setFormData] = useState(initialFormState);
    const [message, setMessage] = useState({ text: '', type: '' }); // For status messages
    const [loading, setLoading] = useState(false); // Loading state
    const showProfessionField = formData.userType === 'technician'; // Only show profession for technicians

    // Handle basic form field changes
    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value
        }));
    };

    // Handle address field changes
    const handleAddressChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            address: {
                ...prev.address,
                [name]: value
            }
        }));
    };

    // Handle user type change (client/technician)
    const handleUserTypeChange = (e) => {
        setFormData(prev => ({ 
            ...prev, 
            userType: e.target.value,
            profession: e.target.value === 'technician' ? prev.profession : '' // Clear profession if not technician
        }));
    };

    // Form submission handler
    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setMessage({ text: '', type: '' });

        try {
            // Validate required fields
            if (!formData.username || !formData.password || !formData.email || !formData.phoneNumber) {
                throw new Error('Please fill in all required fields');
            }

            // Validate profession for technicians
            if (showProfessionField && !formData.profession) {
                throw new Error('Please select your profession');
            }

            // Make API call to register user
            const response = await axios.post('http://localhost:5000/api/users/register', formData);

            setMessage({ text: 'Registration successful!', type: 'success' });
            
            // Handle successful registration
            if (response.data.token) {
                localStorage.setItem('token', response.data.token);
                onSuccessfulSignup?.(response.data.user);
                navigate('/my-application');
            } else {
                setTimeout(() => navigate('/login'), 2000);
            }
        } catch (error) {
            // Handle errors
            const errorMsg = error.response?.data?.message || error.message || 'Registration failed';
            setMessage({ text: errorMsg, type: 'error' });
            console.error('Registration error:', error);
        } finally {
            setLoading(false);
        }
    };

    // Render basic information section
    const renderBasicInfo = () => (
        <div className="form-section">
            <h3>Basic Information</h3>
            <div className="form-group">
                <label>Username*</label>
                <input
                    type="text"
                    name="username"
                    value={formData.username}
                    onChange={handleChange}
                    required
                />
            </div>
            
            <div className="form-group">
                <label>Email*</label>
                <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                />
            </div>
            
            <div className="form-group">
                <label>Password*</label>
                <input
                    type="password"
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    required
                    minLength="6"
                />
            </div>
            
            <div className="form-group">
                <label>Phone Number*</label>
                <input
                    type="tel"
                    name="phoneNumber"
                    value={formData.phoneNumber}
                    onChange={handleChange}
                    required
                />
            </div>
            
            <div className="form-group">
                <label>Account Type*</label>
                <div className="user-type-selector">
                    <label>
                        <input
                            type="radio"
                            name="userType"
                            value="client"
                            checked={formData.userType === 'client'}
                            onChange={handleUserTypeChange}
                        />
                        <span>Client</span>
                    </label>
                    <label>
                        <input
                            type="radio"
                            name="userType"
                            value="technician"
                            checked={formData.userType === 'technician'}
                            onChange={handleUserTypeChange}
                        />
                        <span>Service Provider</span>
                    </label>
                </div>
            </div>

            {/* Profession field only shown for technicians */}
            {showProfessionField && (
                <div className="form-group">
                    <label>Profession*</label>
                    <select
                        name="profession"
                        value={formData.profession}
                        onChange={handleChange}
                        required
                    >
                        <option value="">Select your profession</option>
                        <option value="electrician">Electrician</option>
                        <option value="plumber">Plumber</option>
                        <option value="carpenter">Carpenter</option>
                        <option value="mechanic">Mechanic</option>
                        <option value="painter">Painter</option>
                        <option value="other">Other</option>
                    </select>
                </div>
            )}
        </div>
    );

    // Render location information section
    const renderLocationInfo = () => (
        <div className="form-section">
            <h3>Location Information</h3>
            <div className="form-group">
                <label>Street Address*</label>
                <input
                    type="text"
                    name="street"
                    value={formData.address.street}
                    onChange={handleAddressChange}
                    required
                />
            </div>
            
            <div className="form-row">
                <div className="form-group">
                    <label>City*</label>
                    <input
                        type="text"
                        name="city"
                        value={formData.address.city}
                        onChange={handleAddressChange}
                        required
                    />
                </div>
                
                <div className="form-group">
                    <label>State*</label>
                    <input
                        type="text"
                        name="state"
                        value={formData.address.state}
                        onChange={handleAddressChange}
                        required
                    />
                </div>
                
                <div className="form-group">
                    <label>ZIP Code*</label>
                    <input
                        type="text"
                        name="zipCode"
                        value={formData.address.zipCode}
                        onChange={handleAddressChange}
                        required
                    />
                </div>
            </div>
        </div>
    );

    return (
        <div className="signup-container">
            <h2>Create Your Account</h2>
            <p className="signup-subtitle">Join as a client or service provider</p>
            
            {/* Display status messages */}
            {message.text && (
                <div className={`message ${message.type}`}>
                    {message.text}
                </div>
            )}

            <form onSubmit={handleSubmit} className="signup-form">
                {renderBasicInfo()}
                {renderLocationInfo()}
                
                <button type="submit" className="submit-btn" disabled={loading}>
                    {loading ? (
                        <>
                            <span className="spinner"></span> Creating Account...
                        </>
                    ) : (
                        'Create Account'
                    )}
                </button>

                <p className="login-prompt">
                    Already have an account? <a href="/login">Log in</a>
                </p>
            </form>
        </div>
    );
};

export default Signup;
