import React, { useState } from 'react';

import { MapContainer, TileLayer, Marker } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix Leaflet marker icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png').default,
  iconUrl: require('leaflet/dist/images/marker-icon.png').default,
  shadowUrl: require('leaflet/dist/images/marker-shadow.png').default,
});

const TechnicianProfile = ({ user = {}, onSubmit }) => {
  const { 
    register, 
    handleSubmit, 
    setValue, 
    watch, 
    formState: { errors } 
  } = useForm({
    defaultValues: {
      name: user?.username || '',
      phoneNumber: user?.phoneNumber || '',
      location: user?.location?.coordinates || [51.505, -0.09],
      skills: user?.skills || []
    }
  });

  const [editing, setEditing] = useState(false);
  const [mapCenter, setMapCenter] = useState(watch('location'));

  const handleFormSubmit = (data) => {
    onSubmit(data);
    setEditing(false);
  };

  const handleLocationChange = (e) => {
    const { lat, lng } = e.target.getLatLng();
    setMapCenter([lat, lng]);
    setValue('location', [lat, lng]);
  };

  const BasicInfoSection = () => (
    <div className="form-section">
      <h3>Basic Information</h3>
      <div className="form-row">
        <div className="form-group">
          <label>Name</label>
          <input {...register("name", { required: "Name is required" })} />
          {errors.name && <span className="error">{errors.name.message}</span>}
        </div>
        <div className="form-group">
          <label>Phone Number</label>
          <input {...register("phoneNumber", { required: "Phone is required" })} />
          {errors.phoneNumber && <span className="error">{errors.phoneNumber.message}</span>}
        </div>
      </div>
    </div>
  );

  const LocationSection = () => (
    <div className="form-section">
      <h3>Location</h3>
      <div className="form-group">
        <label>Drag marker to set your location</label>
        <div className="map-container">
          <MapContainer 
            center={mapCenter} 
            zoom={13} 
            style={{ height: '200px', width: '100%' }}
          >
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
            <Marker 
              position={mapCenter}
              draggable={true}
              eventHandlers={{ dragend: handleLocationChange }}
            />
          </MapContainer>
        </div>
      </div>
    </div>
  );

  const SkillsSection = () => (
    <div className="form-section">
      <h3>Skills</h3>
      {watch('skills').map((skill, index) => (
        <div key={index} className="skill-item">
          <div className="form-row">
            <div className="form-group">
              <label>Skill Name</label>
              <input
                {...register(`skills.${index}.name`)}
                placeholder="e.g., Electrical Wiring"
              />
            </div>
            <div className="form-group">
              <label>Experience (years)</label>
              <input
                type="number"
                {...register(`skills.${index}.experience`, { min: 0 })}
                min="0"
              />
            </div>
          </div>
        </div>
      ))}
    </div>
  );

  const ProfileView = () => (
    <div className="profile-view">
      <div className="profile-section">
        <h2>{watch('name')}</h2>
        <div className="info-row">
          <span><strong>Phone:</strong> {watch('phoneNumber')}</span>
        </div>
      </div>

      <div className="profile-section">
        <h3>Location</h3>
        <div className="map-container">
          <MapContainer 
            center={watch('location')} 
            zoom={13} 
            style={{ height: '200px', width: '100%' }}
          >
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
            <Marker position={watch('location')} />
          </MapContainer>
        </div>
      </div>

      <div className="profile-section">
        <h3>Skills</h3>
        <div className="skills-list">
          {watch('skills').filter(skill => skill.name).map((skill, index) => (
            <div key={index} className="skill-item">
              <strong>{skill.name}</strong> - {skill.experience} years experience
            </div>
          ))}
        </div>
      </div>

      <button onClick={() => setEditing(true)} className="btn-edit">
        Edit Profile
      </button>
    </div>
  );

  if (!editing) {
    return <ProfileView />;
  }

  return (
    <form onSubmit={handleSubmit(handleFormSubmit)} className="profile-form">
      <BasicInfoSection />
      <LocationSection />
      <SkillsSection />
      <div className="form-actions">
        <button type="submit" className="btn-save">Save Profile</button>
        <button type="button" onClick={() => setEditing(false)} className="btn-cancel">
          Cancel
        </button>
      </div>
    </form>
  );
};

export default TechnicianProfile;
