import React from 'react';
import { Link, Outlet } from 'react-router-dom';

function MyApplication() {
    return (
        // Main container with full height, centered content, and background color
        <div className="p-6 bg-gray-100 min-h-screen flex flex-col items-center">

            {/* Page title */}
            <h2 className="text-3xl font-bold text-center mb-10">My Services</h2>

            {/* Navigation Buttons - Displayed in a Row and spread out evenly */}
            <div className="w-full flex justify-center space-x-6 mb-10 px-10">
                
                {/* HR Hub Button */}
                <Link 
                    to="hr-hub" 
                    className="flex-1 text-center px-8 py-4 bg-blue-600 text-white text-lg font-semibold rounded-lg shadow-md hover:bg-blue-700 transition"
                >
                    HR Hub
                </Link>

                {/* Expense Hub Button */}
                <Link 
                    to="expense-hub" 
                    className="flex-1 text-center px-8 py-4 bg-blue-600 text-white text-lg font-semibold rounded-lg shadow-md hover:bg-blue-700 transition"
                >
                    Expense Hub
                </Link>

                {/* Tool Manager Button */}
                <Link 
                    to="tool-manager" 
                    className="flex-1 text-center px-8 py-4 bg-blue-600 text-white text-lg font-semibold rounded-lg shadow-md hover:bg-blue-700 transition"
                >
                    Tool Manager
                </Link>

                {/* Tickets Button */}
                <Link 
                    to="tickets" 
                    className="flex-1 text-center px-8 py-4 bg-blue-600 text-white text-lg font-semibold rounded-lg shadow-md hover:bg-blue-700 transition"
                >
                    Tickets
                </Link>
            </div>

            {/* This Outlet component will render the content of the selected service dynamically */}
            <div className="w-full flex-grow">
                <Outlet />
            </div>
        </div>
    );
}

export default MyApplication;
