import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css';
import UserIcon from '../UserIcon/UserIcon';

/**
 * Navbar Component - Main navigation bar for the application
 * Handles:
 * - Navigation between different pages
 * - User authentication state
 * - User login/logout functionality
 */
function Navbar({ isAuthenticated, username, userType, email, id, onLogout, onLogin }) {
    // State for the dynamic title of the My Application button
    const [title, setTitle] = useState('My Application');
    const [Category, setCategory] = useState('Category');
    const [NearbyTechs, setNearbyTechs] = useState('Technicians Near Me');
    
    // Hook for programmatic navigation
    const navigate = useNavigate();

    const handleProfileNavigation = () => {
        if (userType === 'technician') {
            navigate('/technician-profile');
        } else {
            navigate('/client-profile');
        }
    };

    /**
     * Handles click on the My Application button
     * - Updates the button text
     * - Navigates to the my-application route
     */
    const handleCategoryClick = () => {
        navigate('/category');  // Navigate to route
    };

    const handleNearbyTechsClick = () => {
        navigate('/category');  // Navigate to route
    };

    const handleMyApplicationClick = () => {
        navigate('/my-application');  // Navigate to route
    };

    /**
     * Handle login from UserIcon
     */
    const handleUserIconLogin = (userData) => {
        // Call the parent's onLogin function to update global state
        if (onLogin) {
            onLogin(userData);
        }
    };

    /**
     * Handle logout from UserIcon
     */
    const handleUserIconLogout = () => {
        // Call the parent's onLogout function to update global state
        if (onLogout) {
            onLogout();
        }
        navigate('/');  // Redirect to home after logout
    };

    return (
        <nav className="navbar">
            {/* Main navigation links */}
            <ul>
                {/* Home link */}
                <li><Link to="/">Home</Link></li>

                 {/* My Category button  */}
                <li>
                    <button onClick={handleCategoryClick}>
                        {Category}
                    </button>
                </li>

                <li>
                    <button onClick={handleNearbyTechsClick}>
                        {NearbyTechs}
                    </button>
                </li>
                
                {/* My Application button with dynamic text */}
                <li>
                    <button onClick={handleMyApplicationClick}>
                        {title}
                    </button>
                </li>
                
                {/* Other navigation links */}
                <li><Link to="/shifts">Shifts</Link></li>
                <li><Link to="/internet-status">Internet Status</Link></li>
                <li><Link to="/chat">Chat</Link></li>
            </ul>

            {/* User authentication section (right-aligned) */}
            <div className="user-icon-container">
                <UserIcon 
                    isAuthenticated={isAuthenticated}
                    username={username}
                    email={email}
                    userType={userType}
                    onLogin={handleUserIconLogin}  // Use the local handler
                    userId={id}
                    onLogout={handleUserIconLogout}  // Use the local handler
                    onProfileNavigate={handleProfileNavigation}
                />
            </div>
        </nav>
    );
}

export default Navbar;
