import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './TechnicianProfile.css';

const TechnicianProfile = ({ userId }) => {
  const navigate = useNavigate();
  const [technician, setTechnician] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editForm, setEditForm] = useState({});
  const [saving, setSaving] = useState(false);
  const [uploadingImage, setUploadingImage] = useState(false);
  const [activeTab, setActiveTab] = useState('about');

  // Form states for new sections
  const [newEducation, setNewEducation] = useState({
    institution: '',
    educationType: '',
    fieldOfStudy: '',
    graduationYear: '',
    description: ''
  });
  const [newCertification, setNewCertification] = useState({
    name: '',
    issuedBy: '',
    issueYear: '',
    expirationYear: '',
    credentialId: '',
    credentialUrl: ''
  });
  const [newSkill, setNewSkill] = useState({
    name: '',
    level: '',
    yearsOfExperience: ''
  });
  const [newProject, setNewProject] = useState({
    title: '',
    description: '',
    projectUrl: '',
    technologies: '',
    projectDate: ''
  });

  const isOwner = () => true;

  useEffect(() => {
    const fetchMyProfile = async () => {
      try {
        setLoading(true);
        setError(null);
        
        const token = localStorage.getItem('token');
        
        if (!token) {
          setError('Please log in to view your profile');
          setLoading(false);
          navigate('/login');
          return;
        }

        const response = await axios.get(`http://localhost:5000/api/users/profile/me`, {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });
        
        setTechnician(response.data);
        setEditForm(response.data);
        
      } catch (err) {
        console.error('Error fetching profile:', err);
        const errorMessage = err.response?.data?.message || 
                            'Failed to load your profile. Please try logging in again.';
        setError(errorMessage);
        
        if (err.response?.status === 401) {
          localStorage.removeItem('token');
          localStorage.removeItem('user');
          setTimeout(() => {
            navigate('/login');
          }, 2000);
        }
      } finally {
        setLoading(false);
      }
    };

    fetchMyProfile();
  }, [navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditForm(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleAddressChange = (e) => {
    const { name, value } = e.target;
    setEditForm(prev => ({
      ...prev,
      address: {
        ...prev.address,
        [name]: value
      }
    }));
  };

  const handleArrayChange = (field, value) => {
    const items = value.split(',').map(item => item.trim()).filter(item => item);
    setEditForm(prev => ({
      ...prev,
      [field]: items
    }));
  };

  const handleSaveProfile = async () => {
    try {
      setSaving(true);
      const token = localStorage.getItem('token');
      
      const response = await axios.put(
        'http://localhost:5000/api/users/profile/update',
        editForm,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      setTechnician(response.data.user);
      setIsEditing(false);
      alert('Profile updated successfully!');
    } catch (err) {
      console.error('Error updating profile:', err);
      alert(err.response?.data?.message || 'Failed to update profile');
    } finally {
      setSaving(false);
    }
  };

  const handleImageUpload = async (e) => {
    try {
      setUploadingImage(true);
      const file = e.target.files[0];
      if (!file) return;

      const formData = new FormData();
      formData.append('profilePicture', file);

      const token = localStorage.getItem('token');
      const response = await axios.put(
        'http://localhost:5000/api/users/profile/picture',
        formData,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          }
        }
      );

      setTechnician(prev => ({
        ...prev,
        profilePicture: response.data.profilePicture
      }));
      setEditForm(prev => ({
        ...prev,
        profilePicture: response.data.profilePicture
      }));

      alert('Profile picture updated successfully!');
    } catch (err) {
      console.error('Error uploading image:', err);
      alert(err.response?.data?.message || 'Failed to upload image');
    } finally {
      setUploadingImage(false);
    }
  };

  // New functions for handling additions
  const handleAddEducation = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://localhost:5000/api/users/profile/education',
        newEducation,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      setTechnician(prev => ({ ...prev, education: response.data.education }));
      setNewEducation({
        institution: '',
        educationType: '',
        fieldOfStudy: '',
        graduationYear: '',
        description: ''
      });
      document.getElementById('education-modal').style.display = 'none';
      alert('Education added successfully!');
    } catch (err) {
      console.error('Error adding education:', err);
      alert(err.response?.data?.message || 'Failed to add education');
    }
  };

  const handleAddCertification = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://localhost:5000/api/users/profile/certifications',
        newCertification,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      setTechnician(prev => ({ ...prev, certifications: response.data.certifications }));
      setNewCertification({
        name: '',
        issuedBy: '',
        issueYear: '',
        expirationYear: '',
        credentialId: '',
        credentialUrl: ''
      });
      document.getElementById('certification-modal').style.display = 'none';
      alert('Certification added successfully!');
    } catch (err) {
      console.error('Error adding certification:', err);
      alert(err.response?.data?.message || 'Failed to add certification');
    }
  };

  const handleAddSkill = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://localhost:5000/api/users/profile/skills',
        newSkill,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      setTechnician(prev => ({ ...prev, skills: response.data.skills }));
      setNewSkill({
        name: '',
        level: '',
        yearsOfExperience: ''
      });
      document.getElementById('skill-modal').style.display = 'none';
      alert('Skill added successfully!');
    } catch (err) {
      console.error('Error adding skill:', err);
      alert(err.response?.data?.message || 'Failed to add skill');
    }
  };

  const handleAddProject = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://localhost:5000/api/users/profile/portfolio',
        newProject,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      setTechnician(prev => ({ ...prev, portfolio: response.data.portfolio }));
      setNewProject({
        title: '',
        description: '',
        projectUrl: '',
        technologies: '',
        projectDate: ''
      });
      document.getElementById('project-modal').style.display = 'none';
      alert('Project added to portfolio!');
    } catch (err) {
      console.error('Error adding project:', err);
      alert(err.response?.data?.message || 'Failed to add project');
    }
  };

  const handleRemoveItem = async (type, id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(
        `http://localhost:5000/api/users/profile/${type}/${id}`,
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );

      setTechnician(prev => ({
        ...prev,
        [type]: prev[type].filter(item => item._id !== id)
      }));
      
      alert(`${type.charAt(0).toUpperCase() + type.slice(1)} removed successfully!`);
    } catch (err) {
      console.error(`Error removing ${type}:`, err);
      alert(err.response?.data?.message || `Failed to remove ${type}`);
    }
  };

  const handleUpdateIntroVideo = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put(
        'http://localhost:5000/api/users/profile/intro-video',
        { videoUrl: editForm.introVideo },
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      setTechnician(prev => ({ ...prev, introVideo: response.data.introVideo }));
      alert('Intro video updated successfully!');
    } catch (err) {
      console.error('Error updating intro video:', err);
      alert(err.response?.data?.message || 'Failed to update intro video');
    }
  };

  const getProfilePicture = () => {
    if (technician?.profilePicture) {
      if (technician.profilePicture.startsWith('http')) {
        return technician.profilePicture;
      }
      return `http://localhost:5000${technician.profilePicture}`;
    }
    return 'https://i.pravatar.cc/150';
  };

  const getMainProfession = () => {
    if (technician?.profession) return technician.profession;
    if (technician?.professions?.length > 0) return technician.professions[0];
    return 'Professional Technician';
  };

  const getServicesToDisplay = () => {
    if (technician?.subServices?.length > 0) return technician.subServices;
    if (technician?.services?.length > 0) return technician.services;
    if (technician?.professions?.length > 0) return technician.professions;
    return [];
  };

  if (loading) return <div className="loading">Loading technician profile...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!technician) return <div className="error">Technician not found</div>;

  const servicesToDisplay = getServicesToDisplay();
  const mainProfession = getMainProfession();
  const profilePictureUrl = getProfilePicture();

  return (
    <div className="technician-profile">
      <div className="profile-header-actions">
        <button className="back-button" onClick={() => navigate(-1)}>
          <i className="fas fa-arrow-left"></i> Back
        </button>
        
        {isOwner() && !isEditing && (
          <button 
            className="edit-profile-btn"
            onClick={() => setIsEditing(true)}
          >
            <i className="fas fa-edit"></i> Edit Profile
          </button>
        )}
      </div>

      {isEditing ? (
        /* EDIT MODE */
        <div className="edit-profile-form">
          <div className="form-section">
            <h2>Edit Profile</h2>
            
            {/* Profile Picture Upload */}
            <div className="form-group">
              <label>Profile Picture</label>
              <div className="image-upload-section">
                <img 
                  src={getProfilePicture()} 
                  alt="Profile" 
                  className="profile-preview"
                />
                <div className="upload-controls">
                  <input
                    type="file"
                    accept="image/*"
                    onChange={handleImageUpload}
                    disabled={uploadingImage}
                  />
                  {uploadingImage && <span>Uploading...</span>}
                </div>
              </div>
            </div>

            {/* Basic Information */}
            <div className="form-group">
              <label>Username</label>
              <input
                type="text"
                name="username"
                value={editForm.username || ''}
                onChange={handleInputChange}
              />
            </div>

            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={editForm.email || ''}
                onChange={handleInputChange}
              />
            </div>

            <div className="form-group">
              <label>Phone Number</label>
              <input
                type="text"
                name="phoneNumber"
                value={editForm.phoneNumber || ''}
                onChange={handleInputChange}
              />
            </div>

            <div className="form-group">
              <label>Professions (comma separated)</label>
              <input
                type="text"
                value={editForm.professions?.join(', ') || ''}
                onChange={(e) => handleArrayChange('professions', e.target.value)}
                placeholder="e.g., Electrician, Plumbing, Carpentry"
              />
            </div>

            <div className="form-group">
              <label>Project Rate Category</label>
              <select
                name="projectRateCategory"
                value={editForm.projectRateCategory || ''}
                onChange={handleInputChange}
              >
                <option value="">Select Rate Category</option>
                <option value="under_10000">Under Ksh 10,000</option>
                <option value="10000_to_100000">Ksh 10,000 - 100,000</option>
                <option value="100000_to_1000000">Ksh 100,000 - 1,000,000</option>
                <option value="over_1000000">Over Ksh 1,000,000</option>
              </select>
            </div>

            {/* Address Information */}
            <div className="form-group">
              <label>Street Address</label>
              <input
                type="text"
                name="street"
                value={editForm.address?.street || ''}
                onChange={handleAddressChange}
              />
            </div>

            <div className="form-group">
              <label>City</label>
              <input
                type="text"
                name="city"
                value={editForm.address?.city || ''}
                onChange={handleAddressChange}
              />
            </div>

            <div className="form-group">
              <label>State/Region</label>
              <input
                type="text"
                name="state"
                value={editForm.address?.state || ''}
                onChange={handleAddressChange}
              />
            </div>

            <div className="form-group">
              <label>ZIP Code</label>
              <input
                type="text"
                name="zipCode"
                value={editForm.address?.zipCode || ''}
                onChange={handleAddressChange}
              />
            </div>

            {/* About Section */}
            <div className="form-group">
              <label>About Me</label>
              <textarea
                name="about"
                value={editForm.about || ''}
                onChange={handleInputChange}
                rows="4"
                placeholder="Tell clients about your experience, skills, and approach..."
              />
            </div>

            {/* Intro Video */}
            <div className="form-group">
              <label>Intro Video URL</label>
              <div className="video-input-group">
                <input
                  type="text"
                  name="introVideo"
                  value={editForm.introVideo || ''}
                  onChange={handleInputChange}
                  placeholder="Paste YouTube, Vimeo, or video URL"
                />
                <button 
                  className="save-video-btn"
                  onClick={handleUpdateIntroVideo}
                >
                  Save Video
                </button>
              </div>
            </div>

            {/* Action Buttons */}
            <div className="form-actions">
              <button 
                className="save-btn" 
                onClick={handleSaveProfile}
                disabled={saving}
              >
                {saving ? 'Saving...' : 'Save Changes'}
              </button>
              <button 
                className="cancel-btn"
                onClick={() => {
                  setIsEditing(false);
                  setEditForm(technician);
                }}
                disabled={saving}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      ) : (
        /* VIEW MODE - COMPACT LAYOUT */
        <div className="profile-layout-compact">
          {/* Compact Header */}
          <div className="compact-header">
            <div className="compact-profile-info">
              <div className="compact-image">
                <img 
                  src={profilePictureUrl} 
                  alt={technician.username}
                  onError={(e) => {
                    e.target.src = 'https://i.pravatar.cc/150';
                  }}
                />
              </div>
              <div className="compact-details">
                <h1>{technician.username}</h1>
                <p className="compact-profession">{mainProfession}</p>
                <div className="compact-rating">
                  <span className="stars"> {technician.rating?.average?.toFixed(1) || '0.0'}</span>
                  <span className="reviews">({technician.rating?.count || 0} reviews)</span>
                </div>
              </div>
            </div>

            <div className="compact-contact-info">
              <div className="contact-item">
                <i className="fas fa-envelope"></i> 
                <span>{technician.email || 'Email not provided'}</span>
              </div>
              <div className="contact-item">
                <i className="fas fa-phone"></i> 
                <span>{technician.phoneNumber || 'Phone not provided'}</span>
              </div>
              <div className="contact-item">
                <i className="fas fa-map-marker-alt"></i> 
                <span>
                  {technician.address?.city || technician.address?.state || 'Location not specified'}
                </span>
              </div>
            </div>
          </div>

          {/* Two Column Layout */}
          <div className="two-column-layout">
            {/* Left Column - Tabs */}
            <div className="left-column">
              <div className="compact-tabs">
                <button 
                  className={`compact-tab-button ${activeTab === 'about' ? 'active' : ''}`}
                  onClick={() => setActiveTab('about')}
                >
                  <i className="fas fa-user"></i> About
                </button>
                <button 
                  className={`compact-tab-button ${activeTab === 'portfolio' ? 'active' : ''}`}
                  onClick={() => setActiveTab('portfolio')}
                >
                  <i className="fas fa-briefcase"></i> Portfolio
                </button>
                <button 
                  className={`compact-tab-button ${activeTab === 'education' ? 'active' : ''}`}
                  onClick={() => setActiveTab('education')}
                >
                  <i className="fas fa-graduation-cap"></i> Education
                </button>
                <button 
                  className={`compact-tab-button ${activeTab === 'certifications' ? 'active' : ''}`}
                  onClick={() => setActiveTab('certifications')}
                >
                  <i className="fas fa-certificate"></i> Certifications
                </button>
                <button 
                  className={`compact-tab-button ${activeTab === 'skills' ? 'active' : ''}`}
                  onClick={() => setActiveTab('skills')}
                >
                  <i className="fas fa-tools"></i> Skills
                </button>
              </div>

              {/* Quick Stats Sidebar */}
              <div className="quick-stats">
                <div className="stat-item">
                  <h4>Services</h4>
                  <div className="services-tags">
                    {servicesToDisplay.slice(0, 3).map((service, index) => (
                      <span key={index} className="service-tag-small">{service}</span>
                    ))}
                    {servicesToDisplay.length > 3 && (
                      <span className="more-tag">+{servicesToDisplay.length - 3} more</span>
                    )}
                  </div>
                </div>
                
                {technician.userType === 'technician' && technician.projectRateCategory && (
                  <div className="stat-item">
                    <h4>Project Rate</h4>
                    <p className="rate-display-small">
                      {technician.projectRateCategory === 'under_10000' && 'Under Ksh 10,000'}
                      {technician.projectRateCategory === '10000_to_100000' && 'Ksh 10,000 - 100,000'}
                      {technician.projectRateCategory === '100000_to_1000000' && 'Ksh 100,000 - 1,000,000'}
                      {technician.projectRateCategory === 'over_1000000' && 'Over Ksh 1,000,000'}
                    </p>
                  </div>
                )}
              </div>
            </div>

            {/* Right Column - Content */}
            <div className="right-column">
              <div className="tab-content-compact">
                {activeTab === 'about' && (
                  <div className="detail-section-compact">
                    <div className="section-header-compact">
                      <h3>About Me</h3>
                    </div>
                    <p className="about-text">{technician.about || 'No information provided about this technician.'}</p>
                    
                    {technician.introVideo && (
                      <div className="intro-video-section">
                        <h4>Intro Video</h4>
                        <div className="video-container">
                          <iframe
                            src={technician.introVideo}
                            title="Intro Video"
                            frameBorder="0"
                            allowFullScreen
                          ></iframe>
                        </div>
                      </div>
                    )}
                  </div>
                )}

                {activeTab === 'portfolio' && (
                  <div className="detail-section-compact">
                    <div className="section-header-compact">
                      <h3>Portfolio Projects</h3>
                      {isOwner() && (
                        <button 
                          className="add-item-btn"
                          onClick={() => document.getElementById('project-modal').style.display = 'block'}
                        >
                          + Add Project
                        </button>
                      )}
                    </div>

                    {technician.portfolio?.length > 0 ? (
                      <div className="portfolio-grid-compact">
                        {technician.portfolio.map((project) => (
                          <div key={project._id} className="portfolio-item-compact">
                            <h4>{project.title}</h4>
                            <p className="project-description">{project.description}</p>
                            {project.projectUrl && (
                              <a href={project.projectUrl} target="_blank" rel="noopener noreferrer" className="project-link">
                                View Project
                              </a>
                            )}
                            {isOwner() && (
                              <button 
                                className="remove-btn-small"
                                onClick={() => handleRemoveItem('portfolio', project._id)}
                              >
                                Remove
                              </button>
                            )}
                          </div>
                        ))}
                      </div>
                    ) : (
                      <p className="no-data-message">No portfolio projects yet.</p>
                    )}
                  </div>
                )}

                {activeTab === 'education' && (
                  <div className="detail-section-compact">
                    <div className="section-header-compact">
                      <h3>Education</h3>
                      {isOwner() && (
                        <button 
                          className="add-item-btn"
                          onClick={() => document.getElementById('education-modal').style.display = 'block'}
                        >
                          + Add Education
                        </button>
                      )}
                    </div>

                    {technician.education?.length > 0 ? (
                      <div className="education-list-compact">
                        {technician.education.map((edu) => (
                          <div key={edu._id} className="education-item-compact">
                            <div className="edu-header">
                              <h4>{edu.institution}</h4>
                              {isOwner() && (
                                <button 
                                  className="remove-btn-small"
                                  onClick={() => handleRemoveItem('education', edu._id)}
                                >
                                  Remove
                                </button>
                              )}
                            </div>
                            <p className="edu-details">{edu.fieldOfStudy} - {edu.educationType}</p>
                            <p className="edu-year">Graduated: {edu.graduationYear}</p>
                            {edu.description && <p className="edu-description">{edu.description}</p>}
                          </div>
                        ))}
                      </div>
                    ) : (
                      <p className="no-data-message">No education information added.</p>
                    )}
                  </div>
                )}

                {activeTab === 'certifications' && (
                  <div className="detail-section-compact">
                    <div className="section-header-compact">
                      <h3>Certifications</h3>
                      {isOwner() && (
                        <button 
                          className="add-item-btn"
                          onClick={() => document.getElementById('certification-modal').style.display = 'block'}
                        >
                          + Add Certification
                        </button>
                      )}
                    </div>

                    {technician.certifications?.length > 0 ? (
                      <div className="certifications-list-compact">
                        {technician.certifications.map((cert) => (
                          <div key={cert._id} className="certification-item-compact">
                            <div className="cert-header">
                              <h4>{cert.name}</h4>
                              {isOwner() && (
                                <button 
                                  className="remove-btn-small"
                                  onClick={() => handleRemoveItem('certifications', cert._id)}
                                >
                                  Remove
                                </button>
                              )}
                            </div>
                            <p className="cert-details">Issued by: {cert.issuedBy}</p>
                            <p className="cert-year">Year: {cert.issueYear}</p>
                            {cert.expirationYear && <p className="cert-expiry">Expires: {cert.expirationYear}</p>}
                          </div>
                        ))}
                      </div>
                    ) : (
                      <p className="no-data-message">No certifications added.</p>
                    )}
                  </div>
                )}

                {activeTab === 'skills' && (
                  <div className="detail-section-compact">
                    <div className="section-header-compact">
                      <h3>Skills & Experience</h3>
                      {isOwner() && (
                        <button 
                          className="add-item-btn"
                          onClick={() => document.getElementById('skill-modal').style.display = 'block'}
                        >
                          + Add Skill
                        </button>
                      )}
                    </div>

                    {technician.skills?.length > 0 ? (
                      <div className="skills-grid-compact">
                        {technician.skills.map((skill) => (
                          <div key={skill._id} className="skill-item-compact">
                            <div className="skill-header">
                              <h4>{skill.name}</h4>
                              {isOwner() && (
                                <button 
                                  className="remove-btn-small"
                                  onClick={() => handleRemoveItem('skills', skill._id)}
                                >
                                  Remove
                                </button>
                              )}
                            </div>
                            <p className="skill-level">Level: <span className={`level-${skill.level}`}>{skill.level}</span></p>
                            <p className="skill-experience">Experience: {skill.yearsOfExperience} years</p>
                          </div>
                        ))}
                      </div>
                    ) : (
                      <p className="no-data-message">No skills added yet.</p>
                    )}
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Modals for adding new items */}
      {isOwner() && !isEditing && (
        <>
          {/* Add Project Modal */}
          <div id="project-modal" className="modal">
            <div className="modal-content">
              <span className="close" onClick={() => document.getElementById('project-modal').style.display = 'none'}>&times;</span>
              <h3>Add Portfolio Project</h3>
              <input
                type="text"
                placeholder="Project Title"
                value={newProject.title}
                onChange={(e) => setNewProject({...newProject, title: e.target.value})}
              />
              <textarea
                placeholder="Project Description"
                value={newProject.description}
                onChange={(e) => setNewProject({...newProject, description: e.target.value})}
              />
              <input
                type="text"
                placeholder="Project URL (optional)"
                value={newProject.projectUrl}
                onChange={(e) => setNewProject({...newProject, projectUrl: e.target.value})}
              />
              <input
                type="text"
                placeholder="Technologies (comma separated)"
                value={newProject.technologies}
                onChange={(e) => setNewProject({...newProject, technologies: e.target.value})}
              />
              <input
                type="date"
                value={newProject.projectDate}
                onChange={(e) => setNewProject({...newProject, projectDate: e.target.value})}
              />
              <div className="modal-actions">
                <button onClick={handleAddProject}>Add Project</button>
                <button onClick={() => document.getElementById('project-modal').style.display = 'none'}>
                  Cancel
                </button>
              </div>
            </div>
          </div>

          {/* Add Education Modal */}
          <div id="education-modal" className="modal">
            <div className="modal-content">
              <span className="close" onClick={() => document.getElementById('education-modal').style.display = 'none'}>&times;</span>
              <h3>Add Education</h3>
              <input
                type="text"
                placeholder="Institution Name"
                value={newEducation.institution}
                onChange={(e) => setNewEducation({...newEducation, institution: e.target.value})}
              />
              <select
                value={newEducation.educationType}
                onChange={(e) => setNewEducation({...newEducation, educationType: e.target.value})}
              >
                <option value="">Select Type</option>
                <option value="degree">Degree</option>
                <option value="diploma">Diploma</option>
                <option value="certificate">Certificate</option>
              </select>
              <input
                type="text"
                placeholder="Field of Study"
                value={newEducation.fieldOfStudy}
                onChange={(e) => setNewEducation({...newEducation, fieldOfStudy: e.target.value})}
              />
              <input
                type="number"
                placeholder="Graduation Year"
                value={newEducation.graduationYear}
                onChange={(e) => setNewEducation({...newEducation, graduationYear: e.target.value})}
              />
              <textarea
                placeholder="Description (optional)"
                value={newEducation.description}
                onChange={(e) => setNewEducation({...newEducation, description: e.target.value})}
              />
              <div className="modal-actions">
                <button onClick={handleAddEducation}>Add Education</button>
                <button onClick={() => document.getElementById('education-modal').style.display = 'none'}>
                  Cancel
                </button>
              </div>
            </div>
          </div>

          {/* Add Certification Modal */}
          <div id="certification-modal" className="modal">
            <div className="modal-content">
              <span className="close" onClick={() => document.getElementById('certification-modal').style.display = 'none'}>&times;</span>
              <h3>Add Certification</h3>
              <input
                type="text"
                placeholder="Certification Name"
                value={newCertification.name}
                onChange={(e) => setNewCertification({...newCertification, name: e.target.value})}
              />
              <input
                type="text"
                placeholder="Issued By"
                value={newCertification.issuedBy}
                onChange={(e) => setNewCertification({...newCertification, issuedBy: e.target.value})}
              />
              <input
                type="number"
                placeholder="Issue Year"
                value={newCertification.issueYear}
                onChange={(e) => setNewCertification({...newCertification, issueYear: e.target.value})}
              />
              <input
                type="number"
                placeholder="Expiration Year (optional)"
                value={newCertification.expirationYear}
                onChange={(e) => setNewCertification({...newCertification, expirationYear: e.target.value})}
              />
              <input
                type="text"
                placeholder="Credential ID (optional)"
                value={newCertification.credentialId}
                onChange={(e) => setNewCertification({...newCertification, credentialId: e.target.value})}
              />
              <input
                type="text"
                placeholder="Credential URL (optional)"
                value={newCertification.credentialUrl}
                onChange={(e) => setNewCertification({...newCertification, credentialUrl: e.target.value})}
              />
              <div className="modal-actions">
                <button onClick={handleAddCertification}>Add Certification</button>
                <button onClick={() => document.getElementById('certification-modal').style.display = 'none'}>
                  Cancel
                </button>
              </div>
            </div>
          </div>

          {/* Add Skill Modal */}
          <div id="skill-modal" className="modal">
            <div className="modal-content">
              <span className="close" onClick={() => document.getElementById('skill-modal').style.display = 'none'}>&times;</span>
              <h3>Add Skill</h3>
              <input
                type="text"
                placeholder="Skill Name"
                value={newSkill.name}
                onChange={(e) => setNewSkill({...newSkill, name: e.target.value})}
              />
              <select
                value={newSkill.level}
                onChange={(e) => setNewSkill({...newSkill, level: e.target.value})}
              >
                <option value="">Select Level</option>
                <option value="beginner">Beginner</option>
                <option value="intermediate">Intermediate</option>
                <option value="expert">Expert</option>
              </select>
              <input
                type="number"
                placeholder="Years of Experience"
                value={newSkill.yearsOfExperience}
                onChange={(e) => setNewSkill({...newSkill, yearsOfExperience: e.target.value})}
              />
              <div className="modal-actions">
                <button onClick={handleAddSkill}>Add Skill</button>
                <button onClick={() => document.getElementById('skill-modal').style.display = 'none'}>
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default TechnicianProfile;
