import React from "react";
import { Link } from "react-router-dom"; // Ensure React Router is installed

const sections = [
  { name: "HR Hub", icon: "ðŸ‘¥", link: "/hr-hub" },
  { name: "Expense Hub", icon: "ðŸ’°", link: "/expense-hub" },
  { name: "Tool Manager", icon: "ðŸ› ï¸", link: "/tool-manager" },
  { name: "Tickets", icon: "ðŸŽŸï¸", link: "/tickets" },
];

const MyApplication = () => {
  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-900 text-white p-10">
      <h1 className="text-3xl font-bold mb-8">My</h1>
      <div className="grid grid-cols-2 gap-6 w-full max-w-4xl">
        {sections.map((section, index) => (
          <Link
            key={index}
            to={section.link}
            className="bg-gray-800 p-12 rounded-xl flex flex-col items-center justify-center shadow-lg w-full h-52 transition duration-300 hover:bg-gray-700 hover:scale-105"
          >
            <span className="text-5xl mb-4">{section.icon}</span>
            <p className="text-lg font-semibold">{section.name}</p>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default MyApplication;
