import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './UserIcon.css';
import default_image from '../../Assets/default-image.jpg';

function UserIcon({ isAuthenticated, username, userType, onLogin, onLogout }) {
    const [showMenu, setShowMenu] = useState(false);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        setLoading(true);
        setMessage('');
        
        try {
            const response = await axios.post('http://localhost:5000/api/users/login', {
                email,
                password
            });
            
            const { token, user } = response.data;
            localStorage.setItem('token', token);
            
            // Pass user data to parent component
            onLogin({
                username: user.username,
                email: user.email,
                role: user.role.name,
                userType: user.userType,
                id: user._id
            });
            
            setMessage('Login successful');
            setShowMenu(false);
            navigate(user.userType === 'technician' ? '/my-application/tickets' : '/my-application');
        } catch (error) {
            setMessage(error.response?.data?.message || 'Login failed. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        onLogout();
        setShowMenu(false);
        navigate('/');
        setMessage('');
    };

    const getInitials = (username) => {
        if (!username) return '';
        const nameArray = username.split(' ');
        return nameArray.map(name => name[0]?.toUpperCase()).join('').slice(0, 2);
    };

    return (
        <div className="user-auth">
            {/* User Icon */}
            <div 
                className="user-icon" 
                onClick={() => {
                    setShowMenu(!showMenu);
                    setMessage('');
                }}
                aria-label={isAuthenticated ? 'User menu' : 'Login menu'}
            >
                {isAuthenticated ? (
                    <div className="initials">{getInitials(username)}</div>
                ) : (
                    <img src={default_image} alt="Default User Icon" />
                )}
            </div>

            {/* Dropdown Menu */}
            {showMenu && (
                <div className="dropdown-menu">
                    {isAuthenticated ? (
                        <>
                            <div className="user-info">
                                <p className="username">{username}</p>
                                <p className="user-type">{userType}</p>
                            </div>
                            <div className="menu-actions">
                                <button 
                                    className="dashboard-btn"
                                    onClick={() => {
                                        navigate(userType === 'technician' ? '/my-application/tickets' : '/my-application');
                                        setShowMenu(false);
                                    }}
                                >
                                    My Dashboard
                                </button>
                                <button 
                                    className="logout-btn"
                                    onClick={handleLogout}
                                >
                                    Logout
                                </button>
                            </div>
                        </>
                    ) : (
                        <div className="login-form-container">
                            <h3>Login</h3>
                            {message && <p className={`message ${message.includes('success') ? 'success' : 'error'}`}>{message}</p>}
                            <form onSubmit={handleLogin}>
                                <input
                                    type="email"
                                    placeholder="Email"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    required
                                    autoComplete="username"
                                />
                                <input
                                    type="password"
                                    placeholder="Password"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    required
                                    autoComplete="current-password"
                                />
                                <button 
                                    type="submit" 
                                    disabled={loading}
                                    className="login-btn"
                                >
                                    {loading ? (
                                        <span className="spinner"></span>
                                    ) : (
                                        'Login'
                                    )}
                                </button>
                            </form>
                            <div className="signup-prompt">
                                <p>Don't have an account?</p>
                                <button 
                                    className="signup-link"
                                    onClick={() => {
                                        setShowMenu(false);
                                        navigate('/signup');
                                    }}
                                >
                                    Sign up here
                                </button>
                            </div>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

export default UserIcon;
