import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TechnicianTickets = () => {
  const [tickets, setTickets] = useState([]); // State to store the list of tickets
  const [loading, setLoading] = useState(true); // State to manage loading state
  const [error, setError] = useState(''); // State to manage error messages
  const [selectedTicket, setSelectedTicket] = useState(null); // State to track the selected ticket
  const [status, setStatus] = useState(''); // State to manage ticket status

  // Fetch tickets assigned to the technician
  useEffect(() => {
    const fetchTickets = async () => {
      try {
        const token = localStorage.getItem('token'); // Retrieve token from localStorage
        const response = await axios.get('http://localhost:5000/api/technician/yourUsername', {
          headers: {
            Authorization: `Bearer ${token}`, // Attach token to the request header for authentication
          },
        });
        setTickets(response.data); // Set the fetched tickets into the state
        setLoading(false); // Set loading to false once data is fetched
      } catch (err) {
        setError(err.response?.data?.message || 'Error fetching tickets'); // Set the error if there's an issue fetching data
        setLoading(false); // Set loading to false in case of an error
      }
    };

    fetchTickets(); // Call the fetchTickets function on component mount
  }, []);

  // Handle ticket selection
  const handleTicketClick = (ticket) => {
    setSelectedTicket(ticket); // Set the selected ticket to display details
    setStatus(ticket.status); // Set the initial status of the ticket
  };

  // Handle status change
  const handleStatusChange = async (newStatus) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `http://localhost:5000/api/tickets/${selectedTicket._id}/status`,
        { status: newStatus },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setStatus(newStatus); // Update the status in the state
      // Update the ticket in the tickets list
      setTickets((prevTickets) =>
        prevTickets.map((ticket) =>
          ticket._id === selectedTicket._id ? { ...ticket, status: newStatus } : ticket
        )
      );
    } catch (err) {
      setError(err.response?.data?.message || 'Error updating status');
    }
  };

  if (loading) {
    return <div>Loading tickets...</div>; // Show loading message while tickets are being fetched
  }

  if (error) {
    return <div>Error: {error}</div>; // Show error message if an error occurred
  }

  return (
    <div className="technician-tickets-container">
      <h2>Assigned Tickets</h2>
      {tickets.length === 0 ? (
        <p>No tickets assigned to you.</p>
      ) : (
        <div className="ticket-buttons">
          {tickets.map((ticket) => (
            <button
              key={ticket._id}
              className="ticket-button"
              onClick={() => handleTicketClick(ticket)}
            >
              {ticket.clientName} {/* Display only the client name on the button */}
            </button>
          ))}
        </div>
      )}

      {selectedTicket && (
        <div className="ticket-details">
          <h3>Ticket Details</h3>
          <p><strong>Status:</strong> {status}</p>
          <p><strong>Ticket Type:</strong> {selectedTicket.ticketType}</p>
          <p><strong>Client Name:</strong> {selectedTicket.clientName}</p>
          <p><strong>Mobile Number:</strong> {selectedTicket.mobileNumber}</p>
          <p><strong>Installation Type:</strong> {selectedTicket.installationType}</p>
          <p><strong>Assigned Technician:</strong> {selectedTicket.assignedTechnician.username}</p>
          <p><strong>Created By:</strong> {selectedTicket.createdBy.username}</p>
          <p><strong>Location:</strong> {selectedTicket.location}</p>
          <p><strong>Created At:</strong> {new Date(selectedTicket.createdAt).toLocaleDateString()}</p>

          {/* Stage-specific details */}
          {status === 'Seen' && (
            <div className="stage-buttons">
              <button onClick={() => handleStatusChange('Leaving for Site')}>Mark as Leaving for Site</button>
            </div>
          )}
          {status === 'Leaving for Site' && (
            <div className="stage-buttons">
              <button onClick={() => handleStatusChange('Enroute')}>Mark as Enroute</button>
            </div>
          )}
          {status === 'Enroute' && (
            <div className="stage-buttons">
              <button onClick={() => handleStatusChange('Arrived on Site')}>Mark as Arrived on Site</button>
            </div>
          )}
          {status === 'Arrived on Site' && (
            <div className="stage-buttons">
              <button onClick={() => handleStatusChange('Submit Details')}>Mark as Submit Details</button>
            </div>
          )}
          {status === 'Submit Details' && (
            <div className="stage-buttons">
              <button onClick={() => handleStatusChange('Complete')}>Mark as Complete</button>
            </div>
          )}

          {/* Stage-specific form for 'Submit Details' */}
          {status === 'Submit Details' && (
            <div className="submit-details">
              <h4>Submit Details</h4>
              <label>
                Speedtest Screenshot:
                <input type="file" accept="image/*" />
              </label>
              <label>
                WAN Photo:
                <input type="file" accept="image/*" />
              </label>
              <label>
                LAN Photo:
                <input type="file" accept="image/*" />
              </label>
              <label>
                MAC Address of Router:
                <input type="text" />
              </label>
              <label>
                Signal Received:
                <input type="text" />
              </label>
              <label>
                BOM Used:
                <input type="text" />
              </label>
              <label>
                Additional Notes:
                <textarea />
              </label>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default TechnicianTickets;
