import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css';
import UserIcon from '../UserIcon/UserIcon';

/**
 * Navbar Component - Main navigation bar for the application
 * Handles:
 * - Navigation between different pages
 * - User authentication state
 * - User login/logout functionality
 */
function Navbar() {
    // State for the dynamic title of the My Application button
    const [title, setTitle] = useState('My Application');
    
    // Hook for programmatic navigation
    const navigate = useNavigate();
    
    /**
     * Authentication state object:
     * - isAuthenticated: tracks if user is logged in (boolean)
     * - username: stores the logged in user's name
     * - userType: stores the user's role/type (e.g., 'technician', 'admin')
     */
    const [authState, setAuthState] = useState({
        isAuthenticated: false,
        username: null,
        userType: null,
        email:null
    });

    /**
     * Handles click on the My Application button
     * - Updates the button text
     * - Navigates to the my-application route
     */
    const handleMyApplicationClick = () => {
        setTitle('My Services');  // Update button text
        navigate('/my-application');  // Navigate to route
    };

    /**
     * Login handler - called after successful authentication
     * @param {Object} userData - User information from login
     * @param {string} userData.username - The user's username
     * @param {string} userData.userType - The user's role/type
     */
    const handleLogin = (userData) => {
        setAuthState({
            isAuthenticated: true,
            username: userData.username,
            userType: userData.userType,
            email: userData.email,
        });
    };

    /**
     * Logout handler - resets authentication state
     * - Clears user data from state
     * - Redirects to home page
     */
    const handleLogout = () => {
        setAuthState({
            isAuthenticated: false,
            username: null,
            userType: null,
        });
        navigate('/');  // Redirect to home after logout
    };

    return (
        <nav className="navbar">
            {/* Main navigation links */}
            <ul>
                {/* Home link */}
                <li><Link to="/">Home</Link></li>
                
                {/* My Application button with dynamic text */}
                <li>
                    <button onClick={handleMyApplicationClick}>
                        {title}
                    </button>
                </li>
                
                {/* Other navigation links */}
                <li><Link to="/shifts">Shifts</Link></li>
                <li><Link to="/internet-status">Internet Status</Link></li>
                <li><Link to="/chat">Chat</Link></li>
            </ul>

            {/* User authentication section (right-aligned) */}
            <div className="user-icon-container">
                <UserIcon 
                    isAuthenticated={authState.isAuthenticated}
                    username={authState.username}
                    userType={authState.userType}
                    onLogin={handleLogin}        // Callback for successful login
                    onLogout={handleLogout}     // Callback for logout
                />
            </div>
        </nav>
    );
}

export default Navbar;
