import React, { useEffect, useState } from 'react'; // Import React and React hooks (useEffect, useState) for component functionality
import axios from 'axios'; // Import axios for making HTTP requests

const TechnicianTickets = () => {
  const [tickets, setTickets] = useState([]); // State to store the list of tickets
  const [loading, setLoading] = useState(true); // State to manage the loading state of the component
  const [error, setError] = useState(''); // State to store any error messages

  useEffect(() => { 
    const fetchTickets = async () => { // Define an asynchronous function to fetch tickets
      try {
        const token = localStorage.getItem('token'); // Retrieve the authentication token from local storage
        const response = await axios.get('/api/fetchtickets/assigned', { // Make a GET request to fetch assigned tickets
          headers: {
            Authorization: `Bearer ${token}`, // Include the token in the Authorization header
          },
        });
        setTickets(response.data); // Update the state with the fetched tickets data
        setLoading(false); // Set loading to false as data is successfully fetched
      } catch (err) {
        setError(err.response?.data?.message || 'Error fetching tickets'); // Capture and set the error message
        setLoading(false); // Set loading to false even if an error occurs
      }
    };

    fetchTickets(); // Call the fetchTickets function to fetch data when the component mounts
  }, []); // The empty dependency array ensures this effect runs only once when the component mounts

  if (loading) { 
    return <div>Loading tickets...</div>; // Display a loading message while tickets are being fetched
  }

  if (error) {
    return <div>Error: {error}</div>; // Display the error message if an error occurred during data fetching
  }

  return (
    <div className="technician-tickets-container"> {/* Main container for tickets */}
      <h2>Assigned Tickets</h2> {/* Title for the tickets section */}
      {tickets.length === 0 ? ( // Conditional rendering: Check if there are no tickets
        <p>No tickets assigned to you.</p> // Display a message if no tickets are assigned
      ) : (
        <ul className="ticket-list"> {/* Unordered list to display tickets */}
          {tickets.map((ticket) => ( // Map over the tickets array to render each ticket
            <li key={ticket._id} className="ticket-item"> {/* Each ticket is displayed as a list item */}
              <h3>{ticket.ticketType}</h3> {/* Display the type of the ticket */}
              <p><strong>Client Name:</strong> {ticket.clientName}</p> {/* Display the client's name */}
              <p><strong>Mobile Number:</strong> {ticket.mobileNumber}</p> {/* Display the client's mobile number */}
              <p><strong>Location:</strong> {ticket.location}</p> {/* Display the ticket's location */}
              <p><strong>Created By:</strong> {ticket.createdBy?.username}</p> {/* Display the name of the user who created the ticket */}
              <p><strong>Date:</strong> {new Date(ticket.createdAt).toLocaleDateString()}</p> {/* Display the creation date of the ticket */}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TechnicianTickets; // Export the TechnicianTickets component for use in other parts of the application
