import React from 'react';
import { Link, Outlet } from 'react-router-dom';

function MyApplication() {
    return (
        // Main container with padding, background color, and minimum screen height
        <div className="p-6 bg-gray-100 min-h-screen">

            {/* Page title */}
            <h2 className="text-2xl font-bold text-center mb-6">My Services</h2>

            {/* Navigation Buttons - Displayed in a Row */}
            <div className="flex justify-center space-x-4 mb-6">
                
                {/* HR Hub Button */}
                <Link 
                    to="hr-hub" 
                    className="px-6 py-2 bg-blue-600 text-white font-semibold rounded-lg shadow-md hover:bg-blue-700 transition"
                >
                    HR Hub
                </Link>

                {/* Expense Hub Button */}
                <Link 
                    to="expense-hub" 
                    className="px-6 py-2 bg-blue-600 text-white font-semibold rounded-lg shadow-md hover:bg-blue-700 transition"
                >
                    Expense Hub
                </Link>

                {/* Tool Manager Button */}
                <Link 
                    to="tool-manager" 
                    className="px-6 py-2 bg-blue-600 text-white font-semibold rounded-lg shadow-md hover:bg-blue-700 transition"
                >
                    Tool Manager
                </Link>

                {/* Tickets Button */}
                <Link 
                    to="tickets" 
                    className="px-6 py-2 bg-blue-600 text-white font-semibold rounded-lg shadow-md hover:bg-blue-700 transition"
                >
                    Tickets
                </Link>
            </div>

            {/* This Outlet component will render the content of the selected service dynamically */}
            <Outlet />
        </div>
    );
}

export default MyApplication;
