import React, { useState, useEffect, useRef, } from 'react';
import { useForm } from 'react-hook-form';
import { MapContainer, TileLayer, Marker, Popup, Circle } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

// Main Technician Profile Form Component
const TechnicianProfileForm = ({ user, onSubmit }) => {
    const menuRef = useRef(null);
  // Initialize form with react-hook-form
  const { register, handleSubmit, setValue, watch, formState: { errors } } = useForm({
    defaultValues: {
      // Set default values from user prop or empty values
      name: user?.username || '',               // Technician's name
      phoneNumber: user?.phoneNumber || '',     // Contact number
      address: user?.address?.formattedAddress || '',  // Full address string
      location: user?.location?.coordinates || [0, 0], // [lat, lng] coordinates
      serviceAreas: user?.serviceAreas || [],  // Array of service areas
      willTravel: user?.willTravel || false,   // Travel willingness flag
      travelFee: user?.travelFee || 0,         // Travel charge amount
      availability: user?.availability || [],  // Weekly availability schedule
      preferredCategories: user?.preferredCategories || [], // Preferred work categories
      // Skills array (max 5), fill with empty objects if none exist
      skills: user?.skills?.slice(0, 5) || Array(5).fill({ name: '', category: '', experience: 0 })
    }
  });

  // Component state
  const [editing, setEditing] = useState(false); // Toggle between view/edit modes
  const [mapCenter, setMapCenter] = useState(user?.location?.coordinates || [51.505, -0.09]); // Default to London coordinates
  const [serviceAreaRadius, setServiceAreaRadius] = useState(10); // Default service radius in km
  
  // Handle form submission
  const handleFormSubmit = (data) => {
    onSubmit(data); // Pass data to parent component
    setEditing(false); // Switch back to view mode
  };

  // Add a new service area at current map center
  const addServiceArea = () => {
    const newServiceArea = {
      type: 'Point', // GeoJSON point type
      coordinates: mapCenter, // Current map center coordinates
      radius: serviceAreaRadius // Selected radius
    };
    // Update form value by appending new area
    setValue('serviceAreas', [...watch('serviceAreas'), newServiceArea]);
  };

  // Remove a service area by index
  const removeServiceArea = (index) => {
    const updatedAreas = [...watch('serviceAreas')]; // Get current areas
    updatedAreas.splice(index, 1); // Remove the selected area
    setValue('serviceAreas', updatedAreas); // Update form value
  };

  // Toggle availability for a specific day
  const toggleAvailability = (day) => {
    const updatedAvailability = [...watch('availability')]; // Get current availability
    const dayIndex = updatedAvailability.findIndex(a => a.day === day); // Find day index
    
    if (dayIndex >= 0) {
      // Toggle availability if day exists
      updatedAvailability[dayIndex].available = !updatedAvailability[dayIndex].available;
    } else {
      // Add new day with default time slot if doesn't exist
      updatedAvailability.push({
        day,
        available: true,
        timeSlots: [{ start: '09:00', end: '17:00', maxJobs: 1 }] // Default 9-5 slot
      });
    }
    
    setValue('availability', updatedAvailability); // Update form value
  };

  // Update specific time slot field for a day
  const updateTimeSlot = (day, slotIndex, field, value) => {
    const updatedAvailability = [...watch('availability')]; // Get current availability
    const dayIndex = updatedAvailability.findIndex(a => a.day === day); // Find day index
    
    if (dayIndex >= 0) {
      // Update the specific field in the time slot
      updatedAvailability[dayIndex].timeSlots[slotIndex][field] = value;
      setValue('availability', updatedAvailability); // Update form value
    }
  };

  // Main component render
  return (
    <div className="technician-profile" ref={menuRef}>
      {/* Conditional rendering based on edit mode */}
      {editing ? (
        /* EDIT MODE FORM */
        <form onSubmit={handleSubmit(handleFormSubmit)} className="profile-form">
          
          {/* BASIC INFORMATION SECTION */}
          <div className="form-row">
            <div className="form-group">
              <label>Name</label>
              <input {...register("name", { required: true })} /> {/* Required field */}
              {errors.name && <span className="error">Name is required</span>}
            </div>
            <div className="form-group">
              <label>Phone Number</label>
              <input {...register("phoneNumber", { required: true })} /> {/* Required field */}
              {errors.phoneNumber && <span className="error">Phone is required</span>}
            </div>
          </div>

          {/* ADDRESS AND LOCATION SECTION */}
          <div className="form-row">
            <div className="form-group">
              <label>Address</label>
              <input {...register("address", { required: true })} /> {/* Required field */}
              {errors.address && <span className="error">Address is required</span>}
            </div>
            <div className="form-group">
              <label>Location (Drag marker to set)</label>
              <div className="map-container">
                {/* Interactive map for location selection */}
                <MapContainer 
                  center={mapCenter} 
                  zoom={13} 
                  style={{ height: '200px', width: '100%' }}
                  whenCreated={(map) => map.setView(mapCenter)}
                >
                  <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                  />
                  {/* Draggable marker for location selection */}
                  <Marker 
                    position={mapCenter}
                    draggable={true}
                    eventHandlers={{
                      dragend: (e) => {
                        const { lat, lng } = e.target.getLatLng();
                        setMapCenter([lat, lng]); // Update map center
                        setValue('location', [lat, lng]); // Update form value
                      }
                    }}
                  />
                </MapContainer>
              </div>
            </div>
          </div>

          {/* SERVICE AREAS SECTION */}
          <div className="form-section">
            <h3>Service Areas</h3>
            <div className="form-row">
              <div className="form-group">
                <label>Radius (km)</label>
                <input 
                  type="number" 
                  value={serviceAreaRadius} 
                  onChange={(e) => setServiceAreaRadius(e.target.value)} 
                  min="1" // Minimum 1km radius
                />
              </div>
              <button type="button" onClick={addServiceArea} className="btn-add">
                Add Service Area
              </button>
            </div>
            
            {/* List of existing service areas */}
            <div className="service-areas-list">
              {watch('serviceAreas').map((area, index) => (
                <div key={index} className="service-area-item">
                  <span>Area {index + 1}: {area.coordinates[0].toFixed(4)}, {area.coordinates[1].toFixed(4)} - {area.radius}km</span>
                  <button type="button" onClick={() => removeServiceArea(index)} className="btn-remove">
                    Remove
                  </button>
                </div>
              ))}
            </div>
          </div>

          {/* TRAVEL PREFERENCES SECTION */}
          <div className="form-row">
            <div className="form-group checkbox-group">
              <input 
                type="checkbox" 
                id="willTravel" 
                {...register("willTravel")} 
              />
              <label htmlFor="willTravel">Willing to travel to clients</label>
            </div>
            {/* Conditionally show travel fee if willing to travel */}
            {watch('willTravel') && (
              <div className="form-group">
                <label>Travel Fee ($)</label>
                <input 
                  type="number" 
                  {...register("travelFee", { min: 0 })} 
                  min="0" // No negative fees
                />
              </div>
            )}
          </div>

          {/* AVAILABILITY SCHEDULE SECTION */}
          <div className="form-section">
            <h3>Availability</h3>
            {/* Render controls for each day of the week */}
            {['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday'].map(day => {
              const dayAvailability = watch('availability').find(a => a.day === day);
              const isAvailable = dayAvailability?.available || false;
              
              return (
                <div key={day} className="day-availability">
                  <div className="form-row">
                    <div className="form-group checkbox-group">
                      <input
                        type="checkbox"
                        id={`available-${day}`}
                        checked={isAvailable}
                        onChange={() => toggleAvailability(day)}
                      />
                      <label htmlFor={`available-${day}`}>{day.charAt(0).toUpperCase() + day.slice(1)}</label>
                    </div>
                  </div>
                  
                  {/* Show time slots if day is available */}
                  {isAvailable && dayAvailability.timeSlots.map((slot, slotIndex) => (
                    <div key={slotIndex} className="time-slot">
                      <div className="form-row">
                        <div className="form-group">
                          <label>Start Time</label>
                          <input
                            type="time"
                            value={slot.start}
                            onChange={(e) => updateTimeSlot(day, slotIndex, 'start', e.target.value)}
                          />
                        </div>
                        <div className="form-group">
                          <label>End Time</label>
                          <input
                            type="time"
                            value={slot.end}
                            onChange={(e) => updateTimeSlot(day, slotIndex, 'end', e.target.value)}
                          />
                        </div>
                        <div className="form-group">
                          <label>Max Jobs</label>
                          <input
                            type="number"
                            value={slot.maxJobs}
                            min="1" // At least 1 job per slot
                            onChange={(e) => updateTimeSlot(day, slotIndex, 'maxJobs', parseInt(e.target.value))}
                          />
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              );
            })}
          </div>

          {/* SKILLS SECTION */}
          <div className="form-section">
            <h3>Skills (Max 5)</h3>
            {/* Render inputs for up to 5 skills */}
            {watch('skills').map((skill, index) => (
              <div key={index} className="skill-item">
                <div className="form-row">
                  <div className="form-group">
                    <label>Skill Name</label>
                    <input
                      {...register(`skills.${index}.name`)}
                      placeholder="e.g., Electrical Wiring"
                    />
                  </div>
                  <div className="form-group">
                    <label>Category</label>
                    <input
                      {...register(`skills.${index}.category`)}
                      placeholder="e.g., Electrical"
                    />
                  </div>
                  <div className="form-group">
                    <label>Experience (years)</label>
                    <input
                      type="number"
                      {...register(`skills.${index}.experience`, { min: 0 })}
                      min="0" // No negative experience
                    />
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* PREFERRED CATEGORIES SECTION */}
          <div className="form-section">
            <h3>Preferred Categories</h3>
            <div className="form-row">
              <div className="form-group">
                {/* Multi-select dropdown for categories */}
                <select
                  multiple
                  {...register("preferredCategories")}
                  className="multi-select"
                >
                  <option value="electrical">Electrical</option>
                  <option value="plumbing">Plumbing</option>
                  <option value="hvac">HVAC</option>
                  <option value="carpentry">Carpentry</option>
                  <option value="automotive">Automotive</option>
                </select>
              </div>
            </div>
          </div>

          {/* FORM ACTION BUTTONS */}
          <div className="form-actions">
            <button type="submit" className="btn-save">Save Profile</button>
            <button type="button" onClick={() => setEditing(false)} className="btn-cancel">
              Cancel
            </button>
          </div>
        </form>
      ) : (
        /* VIEW MODE DISPLAY */
        <div className="profile-view">
          {/* BASIC INFORMATION DISPLAY */}
          <div className="profile-section">
            <h2>{watch('name')}</h2>
            <div className="info-row">
              <span><strong>Phone:</strong> {watch('phoneNumber')}</span>
              <span><strong>Address:</strong> {watch('address')}</span>
            </div>
          </div>

          {/* SERVICE AREAS DISPLAY */}
          <div className="profile-section">
            <h3>Service Areas</h3>
            {watch('serviceAreas').length > 0 ? (
              <div className="service-areas-map">
                {/* Map visualization of service areas */}
                <MapContainer 
                  center={watch('location')} 
                  zoom={12} 
                  style={{ height: '250px', width: '100%' }}
                >
                  <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                  />
                  <Marker position={watch('location')} />
                  {/* Draw circles for each service area */}
                  {watch('serviceAreas').map((area, index) => (
                    <Circle
                      key={index}
                      center={area.coordinates}
                      radius={area.radius * 1000} // Convert km to meters
                      color="blue"
                      fillColor="blue"
                      fillOpacity={0.2}
                    />
                  ))}
                </MapContainer>
                <div className="service-areas-list">
                  {/* List of service area coordinates */}
                  {watch('serviceAreas').map((area, index) => (
                    <div key={index} className="service-area-item">
                      Area {index + 1}: {area.coordinates[0].toFixed(4)}, {area.coordinates[1].toFixed(4)} - {area.radius}km radius
                    </div>
                  ))}
                </div>
              </div>
            ) : (
              <p>No service areas defined</p>
            )}
          </div>

          {/* TRAVEL PREFERENCES DISPLAY */}
          <div className="profile-section">
            <h3>Travel Preferences</h3>
            <p>
              {watch('willTravel') 
                ? `Willing to travel to clients (Fee: $${watch('travelFee')})`
                : 'Not available for travel to clients'}
            </p>
          </div>

          {/* AVAILABILITY SCHEDULE DISPLAY */}
          <div className="profile-section">
            <h3>Availability</h3>
            <div className="availability-grid">
              {/* Show weekly availability */}
              {['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday'].map(day => {
                const dayAvailability = watch('availability').find(a => a.day === day);
                return (
                  <div key={day} className="availability-day">
                    <strong>{day.charAt(0).toUpperCase() + day.slice(1)}:</strong>
                    {dayAvailability?.available ? (
                      <div className="time-slots">
                        {/* Show time slots for available days */}
                        {dayAvailability.timeSlots.map((slot, index) => (
                          <div key={index}>
                            {slot.start} - {slot.end} (Max jobs: {slot.maxJobs})
                          </div>
                        ))}
                      </div>
                    ) : (
                      <span>Not available</span>
                    )}
                  </div>
                );
              })}
            </div>
          </div>

          {/* SKILLS DISPLAY */}
          <div className="profile-section">
            <h3>Skills</h3>
            <div className="skills-list">
              {/* Filter out empty skills and display */}
              {watch('skills').filter(skill => skill.name).map((skill, index) => (
                <div key={index} className="skill-item">
                  <strong>{skill.name}</strong> ({skill.category}) - {skill.experience} years
                </div>
              ))}
            </div>
          </div>

          {/* PREFERRED CATEGORIES DISPLAY */}
          <div className="profile-section">
            <h3>Preferred Categories</h3>
            {watch('preferredCategories').length > 0 ? (
              <div className="categories-list">
                {/* Display as tags */}
                {watch('preferredCategories').map((category, index) => (
                  <span key={index} className="category-tag">
                    {category}
                  </span>
                ))}
              </div>
            ) : (
              <p>No preferred categories selected</p>
            )}
          </div>

          {/* EDIT BUTTON */}
          <button onClick={() => setEditing(true)} className="btn-edit">
            Edit Profile
          </button>
        </div>
      )}
    </div>
  );
};

export default TechnicianProfileForm;
