import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./Internet.css";
import axios from "axios";

const ITNetworkingPage = () => {
  const navigate = useNavigate();

  // ================== STATE ==================
  const [selectedService, setSelectedService] = useState(null);
  const [expandedServices, setExpandedServices] = useState({});
  const [servicesData, setServicesData] = useState({});
  const [techniciansData, setTechniciansData] = useState({
    exactMatches: [],
    relatedServices: []
  });
  const [allCategoryTechnicians, setAllCategoryTechnicians] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // ================== HELPERS ==================
  const handleViewProfile = (technicianId) => {
    navigate(`/techniciancategoryprofile/${technicianId}`);
  };

  // ================== FETCH SERVICES ==================
  useEffect(() => {
    const fetchServices = async () => {
      try {
        setLoading(true);
        const response = await axios.get("http://localhost:5000/api/users/categories");
        const categories = response.data;

        const formattedData = {};
        
        categories.forEach((category) => {
          category.services.forEach((service) => {
            formattedData[service.name] = service.subServices || [];
          });
        });

        setServicesData(formattedData);
      } catch (err) {
        console.error(err);
        setError("Failed to fetch services. Please try again.");
      } finally {
        setLoading(false);
      }
    };

    fetchServices();
  }, []);

  // ================== FETCH ALL IT & NETWORKING TECHNICIANS ==================
  const fetchAllCategoryTechnicians = async () => {
    try {
      const response = await axios.get(
        `http://localhost:5000/api/technicians/technicians`,
        {
          params: {
            category: "IT & Networking"
          }
        }
      );
      setAllCategoryTechnicians(response.data.technicians || []);
    } catch (err) {
      console.error("Error fetching all category technicians:", err);
    }
  };

  // ================== FETCH TECHNICIANS BY SERVICE ==================
  const fetchTechnicians = async (service) => {
    try {
      setLoading(true);
      setError("");

      // Fetch technicians for the specific service
      const response = await axios.get(
        `http://localhost:5000/api/technicians/technicians_subservice`,
        {
          params: {
            subService: service,
            category: "IT & Networking"
          }
        }
      );

      const data = response.data;
      
      setTechniciansData({
        exactMatches: data.exactMatches || [],
        relatedServices: data.relatedServices || []
      });

      // Also fetch all technicians in the IT & Networking category
      await fetchAllCategoryTechnicians();

    } catch (err) {
      console.error(err);
      setError("Failed to fetch technicians. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  // ================== EVENT HANDLERS ==================
  const toggleServiceExpansion = (service) => {
    setExpandedServices((prev) => ({
      ...prev,
      [service]: !prev[service],
    }));
  };

  const handleServiceSelect = (service) => {
    setSelectedService(service);
    fetchTechnicians(service);
  };

  const handleBack = () => {
    setSelectedService(null);
    setTechniciansData({ exactMatches: [], relatedServices: [] });
    setAllCategoryTechnicians([]);
  };

  const handleNavigateToCategory = () => {
    navigate("/categories");
  };

  // ================== RENDER OTHER CATEGORY TECHNICIANS ==================
  const renderOtherCategoryTechnicians = () => {
    // Filter out technicians that are already shown in exact matches or related services
    const exactMatchIds = new Set(techniciansData.exactMatches.map(t => t._id));
    const relatedServiceIds = new Set(techniciansData.relatedServices.map(t => t._id));
    
    const otherTechnicians = allCategoryTechnicians.filter(
      tech => !exactMatchIds.has(tech._id) && !relatedServiceIds.has(tech._id)
    );

    if (otherTechnicians.length === 0) return null;

    return (
      <div className="other-category-technicians">
        <div className="section-header">
          <h3>Other IT & Networking Technicians</h3>
          <p className="section-subtitle">
            Discover {otherTechnicians.length} more skilled professionals in the IT & Networking category
          </p>
        </div>

        <div className="technicians-grid">
          {otherTechnicians.map((tech) => (
            <div key={tech._id} className="technician-card category-wide">
              <div className="technician-image">
                <img
                  src={tech.profilePicture || `https://i.pravatar.cc/100?u=${tech._id}`}
                  alt={tech.username}
                  onError={(e) => {
                    e.target.src = `https://i.pravatar.cc/100?u=${tech._id}`;
                  }}
                />
              </div>
              <div className="technician-info">
                <h4>{tech.username}</h4>
                <p className="specialty">{tech.profession || "IT & Networking Specialist"}</p>
                
                <div className="rating">
                  <span className="stars">
                    ‚≠ê {tech.rating?.average || "No ratings"}
                  </span>
                  {tech.rating?.count > 0 && (
                    <span className="reviews">
                      ({tech.rating.count} reviews)
                    </span>
                  )}
                </div>

                <div className="services-offered">
                  <p className="services-title">Services Offered:</p>
                  <div className="services-tags">
                    {tech.services?.slice(0, 3).map((service, index) => (
                      <span key={index} className="service-tag">
                        {service}
                      </span>
                    ))}
                    {tech.services?.length > 3 && (
                      <span className="service-tag more">
                        +{tech.services.length - 3} more
                      </span>
                    )}
                  </div>
                </div>

                <div className="details">
                  <p>
                    <i className="fas fa-map-marker-alt"></i>{" "}
                    {tech.address?.city || tech.address?.state || "Location not specified"}
                  </p>
                </div>

                <button
                  className="profile-btn"
                  onClick={() => handleViewProfile(tech._id)}
                >
                  <i className="fas fa-user"></i> View Profile
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  // ================== RENDER TECHNICIANS ==================
  const renderTechniciansList = () => {
    const { exactMatches, relatedServices } = techniciansData;

    return (
      <div className="technicians-section">
        <button className="back-button" onClick={handleBack}>
          <i className="fas fa-arrow-left"></i> Back to Services
        </button>

        <h2>Technicians for {selectedService}</h2>
        <p className="technicians-count">{exactMatches.length} specialized technicians available</p>

        {/* Exact Matches */}
        {exactMatches.length > 0 ? (
          <div className="technicians-grid">
            {exactMatches.map((tech) => (
              <div key={tech._id} className="technician-card exact-match">
                <div className="technician-image">
                  <img
                    src={tech.profilePicture || `https://i.pravatar.cc/100?u=${tech._id}`}
                    alt={tech.username}
                    onError={(e) => {
                      e.target.src = `https://i.pravatar.cc/100?u=${tech._id}`;
                    }}
                  />
                  <div className="match-badge">Exact Match</div>
                </div>
                <div className="technician-info">
                  <h3>{tech.username}</h3>
                  <p className="specialty">{tech.profession || "IT & Networking Specialist"}</p>

                  <div className="rating">
                    <span className="stars">
                      ‚≠ê {tech.rating?.average || "No ratings"}
                    </span>
                    {tech.rating?.count > 0 && (
                      <span className="reviews">
                        ({tech.rating.count} reviews)
                      </span>
                    )}
                  </div>

                  <div className="details">
                    <p>
                      <i className="fas fa-map-marker-alt"></i>{" "}
                      {tech.address?.city || tech.address?.state || "Location not specified"}
                    </p>
                    <p>
                      <i className="fas fa-clock"></i>{" "}
                      {tech.projectRateCategory || "Flexible rates"}
                    </p>
                  </div>

                  <div className="technician-actions">
                    <button className="contact-btn">
                      <i className="fas fa-envelope"></i> Contact
                    </button>
                    <button
                      className="profile-btn"
                      onClick={() => handleViewProfile(tech._id)}
                    >
                      <i className="fas fa-user"></i> View Profile
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="no-technicians">
            <i className="fas fa-user-slash"></i>
            <h3>No specialized technicians available</h3>
            <p>No technicians specialize in {selectedService} right now.</p>
          </div>
        )}

        {/* Related Services Section */}
        {relatedServices.length > 0 && (
          <div className="related-services">
            <div className="section-header">
              <h3>Related Service Technicians</h3>
              <p className="section-subtitle">
                {relatedServices.length} technicians offering similar services
              </p>
            </div>
            <div className="technicians-grid">
              {relatedServices.map((tech) => (
                <div key={tech._id} className="technician-card related">
                  <div className="technician-image">
                    <img
                      src={tech.profilePicture || `https://i.pravatar.cc/100?u=${tech._id}`}
                      alt={tech.username}
                      onError={(e) => {
                        e.target.src = `https://i.pravatar.cc/100?u=${tech._id}`;
                      }}
                    />
                    <div className="match-badge">Related</div>
                  </div>
                  <div className="technician-info">
                    <h4>{tech.username}</h4>
                    <p className="specialty">{tech.profession || "IT & Networking Specialist"}</p>
                    
                    <div className="rating">
                      <span className="stars">
                        ‚≠ê {tech.rating?.average || "No ratings"}
                      </span>
                      {tech.rating?.count > 0 && (
                        <span className="reviews">
                          ({tech.rating.count} reviews)
                        </span>
                      )}
                    </div>

                    <div className="services-offered">
                      <p className="services-title">Also offers:</p>
                      <div className="services-tags">
                        {tech.services?.slice(0, 2).map((service, index) => (
                          <span key={index} className="service-tag">
                            {service}
                          </span>
                        ))}
                      </div>
                    </div>

                    <div className="details">
                      <p>
                        <i className="fas fa-map-marker-alt"></i>{" "}
                        {tech.address?.city || tech.address?.state || "Location not specified"}
                      </p>
                    </div>

                    <button
                      className="profile-btn"
                      onClick={() => handleViewProfile(tech._id)}
                    >
                      <i className="fas fa-user"></i> View Profile
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Other Category Technicians */}
        {renderOtherCategoryTechnicians()}
      </div>
    );
  };

  // ================== RENDER ==================
  return (
    <div className="it-networking-page">
      {/* HEADER */}
      <header className="category-header">
        <div className="header-content">
          <button className="back-button" onClick={handleNavigateToCategory}>
            <i className="fas fa-arrow-left"></i> All Categories
          </button>
          <div className="category-icon">
            <i className="fas fa-network-wired"></i>
          </div>
          <h1>IT & Networking</h1>
          <p className="category-description">
            Professional IT and networking services including internet setup,
            security systems, computer repairs, and more. Connect with skilled
            technicians in your area.
          </p>
        </div>
      </header>

      {/* MAIN CONTENT */}
      <div className="main-content">
        {loading && (
          <div className="loading">
            <i className="fas fa-spinner fa-spin"></i> Loading technicians...
          </div>
        )}
        {error && <div className="error">{error}</div>}

        {!selectedService ? (
          <div className="services-section">
            <h2>Our IT & Networking Services</h2>
            <p>Select a service category to view available technicians</p>

            <div className="services-grid">
              {Object.entries(servicesData).map(([service, subservices]) => (
                <div key={service} className="service-card">
                  {/* Service Header */}
                  <div
                    className="service-header"
                    onClick={() =>
                      subservices.length > 0 && toggleServiceExpansion(service)
                    }
                    style={{
                      cursor: subservices.length > 0 ? "pointer" : "default",
                    }}
                  >
                    <div className="service-icon">
                      {service === "Internet Services" && (
                        <i className="fas fa-wifi"></i>
                      )}
                      {service === "CCTV" && <i className="fas fa-video"></i>}
                      {service === "Computer Services" && (
                        <i className="fas fa-laptop"></i>
                      )}
                      {service === "Alarm System Installation" && (
                        <i className="fas fa-shield-alt"></i>
                      )}
                      {service === "POS Installation & Support" && (
                        <i className="fas fa-cash-register"></i>
                      )}
                      {service === "Phone Repair" && (
                        <i className="fas fa-mobile-alt"></i>
                      )}
                      {service === "TV Repair" && (
                        <i className="fas fa-tv"></i>
                      )}
                    </div>

                    <h3>{service}</h3>

                    {subservices.length > 0 && (
                      <button
                        className={`expand-btn ${
                          expandedServices[service] ? "expanded" : ""
                        }`}
                        onClick={(e) => {
                          e.stopPropagation();
                          toggleServiceExpansion(service);
                        }}
                      >
                        <i className="fas fa-chevron-down"></i>
                      </button>
                    )}
                  </div>

                  {/* Subservices */}
                  {expandedServices[service] && subservices.length > 0 && (
                    <div className="subservices-list">
                      {subservices.map((sub) => (
                        <div
                          key={sub}
                          className="subservice-item"
                          onClick={() => handleServiceSelect(sub)}
                        >
                          <i className="fas fa-cog"></i>
                          <span>{sub}</span>
                          <i className="fas fa-arrow-right"></i>
                        </div>
                      ))}
                    </div>
                  )}

                  {/* No Subservices */}
                  {subservices.length === 0 && (
                    <button
                      className="select-service-btn"
                      onClick={() => handleServiceSelect(service)}
                    >
                      Select Service <i className="fas fa-arrow-right"></i>
                    </button>
                  )}
                </div>
              ))}
            </div>
          </div>
        ) : (
          renderTechniciansList()
        )}
      </div>
    </div>
  );
};

export default ITNetworkingPage;
