import { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './TechnicianProfile.css';

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    professions: [],
    address: {
      street: '',
      city: '',
      state: '',
      zipCode: ''
    },
    email: '',
    phoneNumber: '',
    projectRateCategory: '',
    about: ''
  });
  const [newProfession, setNewProfession] = useState('');

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/users/profile', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        });
        setProfile(response.data);
        setFormData({
          username: response.data.username,
          professions: response.data.professions || [],
          address: response.data.address || {
            street: '',
            city: '',
            state: '',
            zipCode: ''
          },
          email: response.data.email,
          phoneNumber: response.data.phoneNumber,
          projectRateCategory: response.data.projectRateCategory || '',
          about: response.data.about || ''
        });
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load profile');
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleAddressChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      address: { ...prev.address, [name]: value }
    }));
  };

  const addProfession = () => {
    if (newProfession && !formData.professions.includes(newProfession)) {
      setFormData(prev => ({
        ...prev,
        professions: [...prev.professions, newProfession]
      }));
      setNewProfession('');
    }
  };

  const removeProfession = (profession) => {
    setFormData(prev => ({
      ...prev,
      professions: prev.professions.filter(p => p !== profession)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.patch(
        'http://localhost:5000/api/users/profile',
        formData,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        }
      );
      setProfile(response.data);
      setIsEditing(false);
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  if (loading) return <div className="loading-spinner">Loading...</div>;
  if (error) return <div className="error-message">{error}</div>;

  return (
   <div className="profile-page">
  {/* Profile Information Section */}
  <div className="profile-section">
    <div className="section-header">
      <h2>Profile Information</h2>
      <button className="edit-btn">
        Edit Profile
      </button>
    </div>
    
    <div className="profile-content">
      <div className="profile-image-container">
        <img src={profile.profilePicture || '/default-profile.png'} alt="Profile" />
        <button className="edit-image-btn">
          {profile.profilePicture ? 'Change Photo' : 'Add Photo'}
        </button>
      </div>
      
      <div className="profile-details">
        <h1>{profile.username}</h1>
        <p className="user-type">{profile.userType}</p>
        
        <div className="info-group">
          <label>Location</label>
          {profile.address && (
            <div className="info-text">
              {profile.address.street && <div>{profile.address.street}</div>}
              <div>
                {profile.address.city}{profile.address.state ? ', ' : ''}
                {profile.address.state} {profile.address.zipCode}
              </div>
            </div>
          )}
        </div>
        
        <div className="info-group">
          <label>Contact</label>
          <div className="info-text">
            <p>{profile.email}</p>
            <p>{profile.phoneNumber}</p>
          </div>
        </div>
      </div>
    </div>
  </div>

  {/* Skills Section */}
  <div className="profile-section">
    <div className="section-header">
      <h2>Skills & Services</h2>
      <button className="edit-btn">
        Edit Skills
      </button>
    </div>
    
    <div className="skills-content">
      <div className="info-group">
        <label>Professions</label>
        <div className="tags-container">
          {profile.professions?.length > 0 ? (
            profile.professions.map((profession, index) => (
              <span key={index} className="tag">
                {profession}
              </span>
            ))
          ) : (
            <p className="empty-state">No professions added</p>
          )}
        </div>
      </div>
      
      {profile.userType === 'technician' && (
        <div className="info-group">
          <label>Project Rate</label>
          {profile.projectRateCategory ? (
            <span className="rate-tag">
              {profile.projectRateCategory.replace(/_/g, ' ')}
            </span>
          ) : (
            <p className="empty-state">No rate specified</p>
          )}
        </div>
      )}
    </div>
  </div>

  {/* About Me Section */}
  <div className="profile-section">
    <div className="section-header">
      <h2>About Me</h2>
      <button className="edit-btn">
        Edit About
      </button>
    </div>
    
    <div className="about-content">
      {profile.about ? (
        <p className="about-text">{profile.about}</p>
      ) : (
        <p className="empty-state">No information provided yet</p>
      )}
    </div>
  </div>
</div>
  );
};

export default Profile;
