import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TechnicianTickets = () => {
  const [tickets, setTickets] = useState([]); // State to store the list of tickets
  const [loading, setLoading] = useState(true); // State to manage loading state
  const [error, setError] = useState(''); // State to manage error messages

  useEffect(() => {
    const fetchTickets = async () => { // Define an asynchronous function to fetch tickets
      try {
        const token = localStorage.getItem('token'); // Retrieve token from localStorage
        const response = await axios.get('http:5000/api/fetchtickets/assigned', { // Make a GET request to fetch tickets
          headers: {
            Authorization: `Bearer ${token}`, // Attach token to the request header for authentication
          },
        });
        setTickets(response.data); // Set the fetched tickets into the state
        setLoading(false); // Set loading to false once data is fetched
      } catch (err) {
        setError(err.response?.data?.message || 'Error fetching tickets'); // Set the error if there's an issue fetching data
        setLoading(false); // Set loading to false in case of an error
      }
    };

    fetchTickets(); // Call the fetchTickets function on component mount
  }, []);

  if (loading) {
    return <div>Loading tickets...</div>; // Show loading message while tickets are being fetched
  }

  if (error) {
    return <div>Error: {error}</div>; // Show error message if an error occurred
  }

  return (
    <div className="technician-tickets-container"> {/* Main container for tickets */}
      <h2>Assigned Tickets</h2> {/* Title for the ticket list section */}
      {tickets.length === 0 ? ( // Check if there are no tickets
        <p>No tickets assigned to you.</p> // Display message if no tickets are found
      ) : (
        <ul className="ticket-list"> {/* Display tickets as a list */}
          {tickets.map((ticket) => ( // Map over each ticket in the list
            <li key={ticket._id} className="ticket-item"> {/* Each ticket is displayed as a list item */}
              <h3>{ticket.ticketType}</h3> {/* Ticket Type: Installation or Support */}
              
              {ticket.ticketType === 'installation' && ( // Conditionally render fields based on ticket type
                <>
                  <p><strong>Client Name:</strong> {ticket.clientName}</p> {/* Client Name */}
                  <p><strong>Mobile Number:</strong> {ticket.mobileNumber}</p> {/* Client's Mobile Number */}
                  <p><strong>Installation Type:</strong> {ticket.installationType}</p> {/* Installation Type (Wireless/Fibre) */}
                  <p><strong>PPPoE Username:</strong> {ticket.pppoeCredentials.username}</p> {/* PPPoE Username */}
                  <p><strong>PPPoE Password:</strong> {ticket.pppoeCredentials.password}</p> {/* PPPoE Password */}
                </>
              )}

              <p><strong>Assigned Technician:</strong> {ticket.assignedTechnician.username}</p> {/* Assigned Technician's Name */}
              <p><strong>Created By:</strong> {ticket.createdBy.username}</p> {/* Creator's Name */}
              <p><strong>Location:</strong> {ticket.location}</p> {/* Ticket Location */}
              <p><strong>Created At:</strong> {new Date(ticket.createdAt).toLocaleDateString()}</p> {/* Date Ticket Was Created */}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TechnicianTickets;
