import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Signup.css';

const Signup = ({ onSuccessfulSignup }) => {
    const navigate = useNavigate();
    
    // Initial form state
    const initialFormState = {
        username: '',
        password: '',
        email: '',
        phoneNumber: '',
        userType: 'client',
        skills: [],
        address: {
            street: '',
            city: '',
            state: '',
            zipCode: ''
        },
        coordinates: [0, 0]
    };

    // State management
    const [formData, setFormData] = useState(initialFormState);
    const [message, setMessage] = useState({ text: '', type: '' });
    const [loading, setLoading] = useState(false);
    const [currentSkill, setCurrentSkill] = useState('');
    const [currentCert, setCurrentCert] = useState('');
    const showTechFields = formData.userType === 'technician';

    // Handlers
    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value
        }));
    };

    const handleAddressChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            address: {
                ...prev.address,
                [name]: value
            }
        }));
    };

    const handleUserTypeChange = (e) => {
        setFormData(prev => ({ ...prev, userType: e.target.value }));
    };

    // Skill management
    const addSkill = () => {
        if (currentSkill.trim()) {
            setFormData(prev => ({
                ...prev,
                skills: [...prev.skills, { name: currentSkill.trim() }]
            }));
            setCurrentSkill('');
        }
    };

    const removeSkill = (index) => {
        setFormData(prev => ({
            ...prev,
            skills: prev.skills.filter((_, i) => i !== index)
        }));
    };

    const updateSkillDetail = (index, field, value) => {
        setFormData(prev => {
            const updatedSkills = [...prev.skills];
            updatedSkills[index] = { 
                ...updatedSkills[index],
                [field]: field === 'experience' ? Number(value) : value 
            };
            return { ...prev, skills: updatedSkills };
        });
    };

    // Certification management
    const addCertification = () => {
        if (currentCert.trim()) {
            setFormData(prev => ({
                ...prev,
                certifications: [...prev.certifications, currentCert.trim()]
            }));
            setCurrentCert('');
        }
    };

    const removeCertification = (index) => {
        setFormData(prev => ({
            ...prev,
            certifications: prev.certifications.filter((_, i) => i !== index)
        }));
    };

    // Form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setMessage({ text: '', type: '' });

        try {
            // Validation
            if (!formData.username || !formData.password || !formData.email || !formData.phoneNumber) {
                throw new Error('Please fill in all required fields');
            }

            if (showTechFields && formData.skills.length === 0) {
                throw new Error('Please add at least one skill for service providers');
            }

            const response = await axios.post('http://localhost:5000/api/users/register', formData);

            setMessage({ text: 'Registration successful!', type: 'success' });
            
            if (response.data.token) {
                localStorage.setItem('token', response.data.token);
                onSuccessfulSignup?.(response.data.user);
                navigate(showTechFields ? '/my-application' : '/my-application');
            } else {
                setTimeout(() => navigate('/login'), 2000);
            }
        } catch (error) {
            const errorMsg = error.response?.data?.message || error.message || 'Registration failed';
            setMessage({ text: errorMsg, type: 'error' });
            console.error('Registration error:', error);
        } finally {
            setLoading(false);
        }
    };

    // Form sections
    const renderBasicInfo = () => (
        <div className="form-section">
            <h3>Basic Information</h3>
            <div className="form-group">
                <label>Username*</label>
                <input
                    type="text"
                    name="username"
                    value={formData.username}
                    onChange={handleChange}
                    required
                />
            </div>
            
            <div className="form-group">
                <label>Email*</label>
                <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                />
            </div>
            
            <div className="form-group">
                <label>Password*</label>
                <input
                    type="password"
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    required
                    minLength="6"
                />
            </div>
            
            <div className="form-group">
                <label>Phone Number*</label>
                <input
                    type="tel"
                    name="phoneNumber"
                    value={formData.phoneNumber}
                    onChange={handleChange}
                    required
                />
            </div>
            
            <div className="form-group">
                <label>Account Type*</label>
                <div className="user-type-selector">
                    <label>
                        <input
                            type="radio"
                            name="userType"
                            value="client"
                            checked={formData.userType === 'client'}
                            onChange={handleUserTypeChange}
                        />
                        <span>Client</span>
                    </label>
                    <label>
                        <input
                            type="radio"
                            name="userType"
                            value="technician"
                            checked={formData.userType === 'technician'}
                            onChange={handleUserTypeChange}
                        />
                        <span>Service Provider</span>
                    </label>
                </div>
            </div>
        </div>
    );

    const renderLocationInfo = () => (
        <div className="form-section">
            <h3>Location Information</h3>
            <div className="form-group">
                <label>Street Address*</label>
                <input
                    type="text"
                    name="street"
                    value={formData.address.street}
                    onChange={handleAddressChange}
                    required
                />
            </div>
            
            <div className="form-row">
                <div className="form-group">
                    <label>City*</label>
                    <input
                        type="text"
                        name="city"
                        value={formData.address.city}
                        onChange={handleAddressChange}
                        required
                    />
                </div>
                
                <div className="form-group">
                    <label>State*</label>
                    <input
                        type="text"
                        name="state"
                        value={formData.address.state}
                        onChange={handleAddressChange}
                        required
                    />
                </div>
                
                <div className="form-group">
                    <label>ZIP Code*</label>
                    <input
                        type="text"
                        name="zipCode"
                        value={formData.address.zipCode}
                        onChange={handleAddressChange}
                        required
                    />
                </div>
            </div>
        </div>
    );

    const renderTechFields = () => showTechFields && (
        <div className="form-section">
            <h3>Service Provider Details</h3>
            <div className="form-group">
                <label>Hourly Rate ($)</label>
                <input
                    type="number"
                    name="hourlyRate"
                    value={formData.hourlyRate}
                    onChange={handleChange}
                    min="0"
                    step="0.01"
                />
            </div>
            
            <div className="form-group">
                <label>Service Radius (km)</label>
                <input
                    type="number"
                    name="serviceRadius"
                    value={formData.serviceRadius}
                    onChange={handleChange}
                    min="1"
                />
            </div>
            
            <div className="form-group checkbox-group">
                <label>
                    <input
                        type="checkbox"
                        name="willTravel"
                        checked={formData.willTravel}
                        onChange={handleChange}
                    />
                    Willing to travel to client locations
                </label>
            </div>
            
            <div className="form-group">
                <label>Skills*</label>
                <div className="skills-input-container">
                    <input
                        type="text"
                        value={currentSkill}
                        onChange={(e) => setCurrentSkill(e.target.value)}
                        onKeyDown={(e) => e.key === 'Enter' && (e.preventDefault(), addSkill())}
                        placeholder="e.g., Electrical Wiring"
                    />
                    <button type="button" className="add-btn" onClick={addSkill}>
                        Add
                    </button>
                </div>
                <div className="skills-tags">
                    {formData.skills.map((skill, index) => (
                        <div key={index} className="skill-tag">
                            <span>{skill.name}</span>
                            <button type="button" onClick={() => removeSkill(index)}>
                                &times;
                            </button>
                        </div>
                    ))}
                </div>
                
                {formData.skills.length > 0 && (
                    <div className="skill-details">
                        <h4>Skill Details (Optional)</h4>
                        {formData.skills.map((skill, index) => (
                            <div key={index} className="skill-detail-card">
                                <h5>{skill.name}</h5>
                                <div className="form-row">
                                    <div className="form-group">
                                        <label>Category</label>
                                        <input
                                            type="text"
                                            value={skill.category || ''}
                                            onChange={(e) => updateSkillDetail(index, 'category', e.target.value)}
                                            placeholder="e.g., Construction"
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label>Years Experience</label>
                                        <input
                                            type="number"
                                            min="0"
                                            value={skill.experience || ''}
                                            onChange={(e) => updateSkillDetail(index, 'experience', e.target.value)}
                                            placeholder="0"
                                        />
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
            
            <div className="form-group">
                <label>Certifications (Optional)</label>
                <div className="cert-input-container">
                    <input
                        type="text"
                        value={currentCert}
                        onChange={(e) => setCurrentCert(e.target.value)}
                        onKeyDown={(e) => e.key === 'Enter' && (e.preventDefault(), addCertification())}
                        placeholder="e.g., OSHA Certification"
                    />
                    <button type="button" className="add-btn" onClick={addCertification}>
                        Add
                    </button>
                </div>
                <div className="certifications-list">
                    {formData.certifications.map((cert, index) => (
                        <div key={index} className="cert-item">
                            <span>{cert}</span>
                            <button type="button" onClick={() => removeCertification(index)}>
                                &times;
                            </button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );

    return (
        <div className="signup-container">
            <h2>Create Your Account</h2>
            <p className="signup-subtitle">Join as a client or service provider</p>
            
            {message.text && (
                <div className={`message ${message.type}`}>
                    {message.text}
                </div>
            )}

            <form onSubmit={handleSubmit} className="signup-form">
                {renderBasicInfo()}
                {renderLocationInfo()}
                {renderTechFields()}
                
                <button type="submit" className="submit-btn" disabled={loading}>
                    {loading ? (
                        <>
                            <span className="spinner"></span> Creating Account...
                        </>
                    ) : (
                        'Create Account'
                    )}
                </button>

                <p className="login-prompt">
                    Already have an account? <a href="/login">Log in</a>
                </p>
            </form>
        </div>
    );
};

export default Signup;
