import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './U'

function UserIcon({ isAuthenticated, username, onLogout }) {
    const [showMenu, setShowMenu] = useState(false);
    const navigate = useNavigate();

    // Helper function to get initials from the username
    const getInitials = (name) => {
        return name ? name[0].toUpperCase() : '';
    };

    const handleLogin = () => {
        navigate('/login');
    };

    const handleSignup = () => {
        navigate('/signup');
    };

    return (
        <div className="user-icon" onClick={() => setShowMenu(!showMenu)}>
            {isAuthenticated ? (
                <div className="initials">{getInitials(username)}</div>
            ) : (
                <button onClick={handleLogin}>Login</button>
            )}

            {showMenu && (
                <div className="dropdown-menu">
                    {isAuthenticated ? (
                        <>
                            <p>Signed in as {username}</p>
                            <button onClick={onLogout}>Sign Out</button>
                        </>
                    ) : (
                        <>
                            <button onClick={handleLogin}>Login</button>
                            <p>Not yet registered? <button onClick={handleSignup}>Sign up</button></p>
                        </>
                    )}
                </div>
            )}
        </div>
    );
}

export default UserIcon;
