import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Signup.css';

function Signup({ onSuccessfulSignup }) {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        username: '',
        password: '',
        email: '',
        phoneNumber: '',
        userType: 'client', // Default to client
        // Technician-specific fields
        skills: [],
        certifications: [],
        hourlyRate: '',
        serviceRadius: '',
        willTravel: false,
        // Location fields
        address: {
            street: '',
            city: '',
            state: '',
            zipCode: ''
        },
        coordinates: [0, 0]
    });
    const [message, setMessage] = useState({ text: '', type: '' });
    const [loading, setLoading] = useState(false);
    const [showTechFields, setShowTechFields] = useState(false);
    const [currentSkill, setCurrentSkill] = useState('');
    const [currentCert, setCurrentCert] = useState('');

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value
        }));
    };

    const handleAddressChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            address: {
                ...prev.address,
                [name]: value
            }
        }));
    };

    const handleUserTypeChange = (e) => {
        const userType = e.target.value;
        setFormData(prev => ({ ...prev, userType }));
        setShowTechFields(userType === 'technician');
    };

    // Skill Handling Functions

/**
 * Adds a new skill to the form data
 * - Validates the input isn't empty
 * - Creates a new skill object with just the name
 * - Clears the input field after adding
 */
const addSkill = () => {
    if (currentSkill.trim()) {
        setFormData(prev => ({
            ...prev,
            // Add new skill to array while maintaining existing skills
            skills: [...prev.skills, { name: currentSkill.trim() }]
        }));
        // Reset input field
        setCurrentSkill('');
    }
};

/**
 * Removes a skill from the form data by index
 * @param {number} index - The position of the skill to remove
 */
const removeSkill = (index) => {
    setFormData(prev => ({
        ...prev,
        // Filter out the skill at the specified index
        skills: prev.skills.filter((_, i) => i !== index)
    }));
};

/**
 * Updates specific details of a skill
 * @param {number} index - Position of skill to update
 * @param {string} field - Field to update ('name', 'category', 'experience')
 * @param {string|number} value - New value for the field
 */
const updateSkillDetail = (index, field, value) => {
    setFormData(prev => {
        // Create copy of current skills array
        const updatedSkills = [...prev.skills];
        
        // Update the specific skill
        updatedSkills[index] = { 
            ...updatedSkills[index], // Keep existing properties
            // Convert to number if field is experience, else use value as-is
            [field]: field === 'experience' ? Number(value) : value 
        };
        
        return { ...prev, skills: updatedSkills };
    });
};

// Certification Handling Functions

/**
 * Adds a new certification to the form data
 * - Validates the input isn't empty
 * - Adds the certification text to the array
 * - Clears the input field after adding
 */
const addCertification = () => {
    if (currentCert.trim()) {
        setFormData(prev => ({
            ...prev,
            // Add new certification to array while maintaining existing ones
            certifications: [...prev.certifications, currentCert.trim()]
        }));
        // Reset input field
        setCurrentCert('');
    }
};

/**
 * Removes a certification from the form data by index
 * @param {number} index - The position of the certification to remove
 */
const removeCertification = (index) => {
    setFormData(prev => ({
        ...prev,
        // Filter out the certification at the specified index
        certifications: prev.certifications.filter((_, i) => i !== index)
    }));
};

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setMessage({ text: '', type: '' });

        try {
            // Basic validation
            if (!formData.username || !formData.password || !formData.email || !formData.phoneNumber) {
                throw new Error('Please fill in all required fields');
            }

            const response = await axios.post('http://localhost:5000/api/users/register', {
                ...formData,
                
            });

            setMessage({ text: 'Registration successful!', type: 'success' });
            
            // If registration includes automatic login
            if (response.data.token) {
                localStorage.setItem('token', response.data.token);
                if (onSuccessfulSignup) {
                    onSuccessfulSignup(response.data.user);
                }
                navigate(formData.userType === 'technician' ? '/my-application/tickets' : '/my-application');
            } else {
                // Redirect to login after successful registration
                setTimeout(() => navigate('/login'), 2000);
            }
        } catch (error) {
            const errorMsg = error.response?.data?.message || error.message || 'Registration failed';
            setMessage({ text: errorMsg, type: 'error' });
            console.error('Registration error:', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="signup-container">
            <h2>Create Your Account</h2>
            <p className="signup-subtitle">Join as a client or service provider</p>
            
            {message.text && (
                <div className={`message ${message.type}`}>
                    {message.text}
                </div>
            )}

            <form onSubmit={handleSubmit} className="signup-form">
                <div className="form-section">
                    <h3>Basic Information</h3>
                    <div className="form-group">
                        <label>Username*</label>
                        <input
                            type="text"
                            name="username"
                            value={formData.username}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    
                    <div className="form-group">
                        <label>Email*</label>
                        <input
                            type="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    
                    <div className="form-group">
                        <label>Password*</label>
                        <input
                            type="password"
                            name="password"
                            value={formData.password}
                            onChange={handleChange}
                            required
                            minLength="6"
                        />
                    </div>
                    
                    <div className="form-group">
                        <label>Phone Number*</label>
                        <input
                            type="tel"
                            name="phoneNumber"
                            value={formData.phoneNumber}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    
                    <div className="form-group">
                        <label>Account Type*</label>
                        <div className="user-type-selector">
                            <label>
                                <input
                                    type="radio"
                                    name="userType"
                                    value="client"
                                    checked={formData.userType === 'client'}
                                    onChange={handleUserTypeChange}
                                />
                                <span>Client</span>
                            </label>
                            <label>
                                <input
                                    type="radio"
                                    name="userType"
                                    value="technician"
                                    checked={formData.userType === 'technician'}
                                    onChange={handleUserTypeChange}
                                />
                                <span>Service Provider</span>
                            </label>
                        </div>
                    </div>
                </div>

                <div className="form-section">
                    <h3>Location Information</h3>
                    <div className="form-group">
                        <label>Street Address*</label>
                        <input
                            type="text"
                            name="street"
                            value={formData.address.street}
                            onChange={handleAddressChange}
                            required
                        />
                    </div>
                    
                    <div className="form-row">
                        <div className="form-group">
                            <label>City*</label>
                            <input
                                type="text"
                                name="city"
                                value={formData.address.city}
                                onChange={handleAddressChange}
                                required
                            />
                        </div>
                        
                        <div className="form-group">
                            <label>State*</label>
                            <input
                                type="text"
                                name="state"
                                value={formData.address.state}
                                onChange={handleAddressChange}
                                required
                            />
                        </div>
                        
                        <div className="form-group">
                            <label>ZIP Code*</label>
                            <input
                                type="text"
                                name="zipCode"
                                value={formData.address.zipCode}
                                onChange={handleAddressChange}
                                required
                            />
                        </div>
                    </div>
                </div>

                {showTechFields && (
                <div className="form-section">
                    <h3>Service Provider Details</h3>
                    <div className="form-group">
                        <label>Hourly Rate ($)</label>
                        <input
                            type="number"
                            name="hourlyRate"
                            value={formData.hourlyRate}
                            onChange={handleChange}
                            min="0"
                            step="0.01"
                        />
                    </div>
                    
                    <div className="form-group">
                        <label>Service Radius (km)</label>
                        <input
                            type="number"
                            name="serviceRadius"
                            value={formData.serviceRadius}
                            onChange={handleChange}
                            min="1"
                        />
                    </div>
                    
                    <div className="form-group checkbox-group">
                        <label>
                            <input
                                type="checkbox"
                                name="willTravel"
                                checked={formData.willTravel}
                                onChange={handleChange}
                            />
                            Willing to travel to client locations
                        </label>
                    </div>
                    
                    {/* Updated Skills Section */}
                    <div className="form-group">
                        <label>Skills*</label>
                        <div className="skills-input-container">
                            <input
                                type="text"
                                value={currentSkill}
                                onChange={(e) => setCurrentSkill(e.target.value)}
                                onKeyDown={(e) => {
                                    if (e.key === 'Enter') {
                                        e.preventDefault();
                                        addSkill();
                                    }
                                }}
                                placeholder="e.g., Electrical Wiring"
                            />
                            <button 
                                type="button" 
                                className="add-btn"
                                onClick={addSkill}
                            >
                                Add
                            </button>
                        </div>
                        <div className="skills-tags">
                            {formData.skills.map((skill, index) => (
                                <div key={index} className="skill-tag">
                                    <span>{skill.name}</span>
                                    <button 
                                        type="button"
                                        onClick={() => removeSkill(index)}
                                    >
                                        &times;
                                    </button>
                                </div>
                            ))}
                        </div>
                        
                        {/* Skill Details */}
                        {formData.skills.length > 0 && (
                            <div className="skill-details">
                                <h4>Skill Details (Optional)</h4>
                                {formData.skills.map((skill, index) => (
                                    <div key={index} className="skill-detail-card">
                                        <h5>{skill.name}</h5>
                                        <div className="form-row">
                                            <div className="form-group">
                                                <label>Category</label>
                                                <input
                                                    type="text"
                                                    value={skill.category || ''}
                                                    onChange={(e) => 
                                                        updateSkillDetail(index, 'category', e.target.value)
                                                    }
                                                    placeholder="e.g., Construction"
                                                />
                                            </div>
                                            <div className="form-group">
                                                <label>Years Experience</label>
                                                <input
                                                    type="number"
                                                    min="0"
                                                    value={skill.experience || ''}
                                                    onChange={(e) => 
                                                        updateSkillDetail(index, 'experience', e.target.value)
                                                    }
                                                    placeholder="0"
                                                />
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>
                    
                    {/* Updated Certifications Section */}
                    <div className="form-group">
                        <label>Certifications (Optional)</label>
                        <div className="cert-input-container">
                            <input
                                type="text"
                                value={currentCert}
                                onChange={(e) => setCurrentCert(e.target.value)}
                                onKeyDown={(e) => {
                                    if (e.key === 'Enter') {
                                        e.preventDefault();
                                        addCertification();
                                    }
                                }}
                                placeholder="e.g., OSHA Certification"
                            />
                            <button 
                                type="button" 
                                className="add-btn"
                                onClick={addCertification}
                            >
                                Add
                            </button>
                        </div>
                        <div className="certifications-list">
                            {formData.certifications.map((cert, index) => (
                                <div key={index} className="cert-item">
                                    <span>{cert}</span>
                                    <button 
                                        type="button"
                                        onClick={() => removeCertification(index)}
                                    >
                                        &times;
                                    </button>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
                
            )
            }
<button 
    type="submit" 
    className="submit-btn"
    disabled={loading}
    onClick={(e) => {
      e.preventDefault(); // Prevent default just in case
      handleSubmit(e); // Explicitly call handleSubmit
    }}
  >
    {loading ? (
      <>
        <span className="spinner"></span> Creating Account...
      </>
    ) : (
      'Create Account'
    )}
  </button>

            <p className="login-prompt">
                Already have an account? <a href="/login">Log in</a>
            </p>
            </form>

        </div>
        
    );
}

export default Signup;
