import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { MapContainer, TileLayer, Marker, Popup, Circle } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix Leaflet marker icons
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png').default,
  iconUrl: require('leaflet/dist/images/marker-icon.png').default,
  shadowUrl: require('leaflet/dist/images/marker-shadow.png').default,
});

// Constants
const DAYS_OF_WEEK = ['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday'];
const CATEGORY_OPTIONS = [
  { value: 'electrical', label: 'Electrical' },
  { value: 'plumbing', label: 'Plumbing' },
  { value: 'hvac', label: 'HVAC' },
  { value: 'carpentry', label: 'Carpentry' },
  { value: 'automotive', label: 'Automotive' },
];
const DEFAULT_TIME_SLOT = { start: '09:00', end: '17:00', maxJobs: 1 };
const DEFAULT_LOCATION = [51.505, -0.09];
const MAX_SKILLS = 5;


// Main TechnicianProfile Component
const TechnicianProfile = ({ user = {}, onSubmit }) => {
  const getDefaultValues = (userData) => ({
    name: userData.username || '',
    phoneNumber: userData.phoneNumber || '',
    address: userData.address?.formattedAddress || '',
    location: userData.location?.coordinates || DEFAULT_LOCATION,
    serviceAreas: userData.serviceAreas || [],
    willTravel: userData.willTravel || false,
    travelFee: userData.travelFee || 0,
    availability: userData.availability || DAYS_OF_WEEK.map(day => ({
      day,
      available: false,
      timeSlots: [{ ...DEFAULT_TIME_SLOT }]
    })),
    preferredCategories: userData.preferredCategories || [],
    skills: userData.skills?.slice(0, MAX_SKILLS) || Array(MAX_SKILLS).fill({ 
      name: '', 
      category: '', 
      experience: 0 
    })
  });

  const { 
    register, 
    handleSubmit, 
    setValue, 
    watch, 
    formState: { errors } 
  } = useForm({
    defaultValues: getDefaultValues(user)
  });

  const [editing, setEditing] = useState(false);
  const [mapCenter, setMapCenter] = useState(watch('location'));
  const [serviceAreaRadius, setServiceAreaRadius] = useState(10);

  const BasicInfoSection = () => (
    <div className="form-section">
      <h3>Basic Information</h3>
      <div className="form-row">
        <div className="form-group">
          <label>Name</label>
          <input {...register("name", { required: "Name is required" })} />
          {errors.name && <span className="error">{errors.name.message}</span>}
        </div>
        <div className="form-group">
          <label>Phone Number</label>
          <input {...register("phoneNumber", { required: "Phone is required" })} />
          {errors.phoneNumber && <span className="error">{errors.phoneNumber.message}</span>}
        </div>
      </div>
      <div className="form-group">
        <label>Address</label>
        <input {...register("address", { required: "Address is required" })} />
        {errors.address && <span className="error">{errors.address.message}</span>}
      </div>
    </div>
  );

  const handleFormSubmit = (data) => {
    onSubmit(data);
    setEditing(false);
  };

  const handleLocationChange = (e) => {
    const { lat, lng } = e.target.getLatLng();
    setMapCenter([lat, lng]);
    setValue('location', [lat, lng]);
  };

  const addServiceArea = () => {
    const newServiceArea = {
      type: 'Point',
      coordinates: mapCenter,
      radius: serviceAreaRadius
    };
    setValue('serviceAreas', [...watch('serviceAreas'), newServiceArea]);
  };

  const removeServiceArea = (index) => {
    const updatedAreas = [...watch('serviceAreas')];
    updatedAreas.splice(index, 1);
    setValue('serviceAreas', updatedAreas);
  };

  const toggleAvailability = (day) => {
    const updatedAvailability = [...watch('availability')];
    const dayIndex = updatedAvailability.findIndex(a => a.day === day);
    
    if (dayIndex >= 0) {
      updatedAvailability[dayIndex].available = !updatedAvailability[dayIndex].available;
    } else {
      updatedAvailability.push({
        day,
        available: true,
        timeSlots: [{ ...DEFAULT_TIME_SLOT }]
      });
    }
    
    setValue('availability', updatedAvailability);
  };

  const updateTimeSlot = (day, slotIndex, field, value) => {
    const updatedAvailability = [...watch('availability')];
    const dayIndex = updatedAvailability.findIndex(a => a.day === day);
    
    if (dayIndex >= 0) {
      updatedAvailability[dayIndex].timeSlots[slotIndex][field] = value;
      setValue('availability', updatedAvailability);
    }
  };
// Sub-components
const LocationSection = ({ mapCenter, onLocationChange }) => (
  <div className="form-section">
    <h3>Location</h3>
    <div className="form-group">
      <label>Drag marker to set your location</label>
      <div className="map-container">
        <MapContainer 
          center={mapCenter} 
          zoom={13} 
          style={{ height: '200px', width: '100%' }}
        >
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
          <Marker 
            position={mapCenter}
            draggable={true}
            eventHandlers={{ dragend: onLocationChange }}
          />
        </MapContainer>
      </div>
    </div>
  </div>
);

const ServiceAreasSection = ({ serviceAreas, radius, onRadiusChange, onAddArea, onRemoveArea }) => (
  <div className="form-section">
    <h3>Service Areas</h3>
    <div className="form-row">
      <div className="form-group">
        <label>Radius (km)</label>
        <input 
          type="number" 
          value={radius} 
          onChange={(e) => onRadiusChange(Number(e.target.value))} 
          min="1"
        />
      </div>
      <button type="button" onClick={onAddArea} className="btn-add">
        Add Service Area
      </button>
    </div>
    
    <div className="service-areas-list">
      {serviceAreas.map((area, index) => (
        <div key={index} className="service-area-item">
          <span>Area {index + 1}: {area.coordinates[0].toFixed(4)}, {area.coordinates[1].toFixed(4)} - {area.radius}km</span>
          <button type="button" onClick={() => onRemoveArea(index)} className="btn-remove">
            Remove
          </button>
        </div>
      ))}
    </div>
  </div>
);

const TravelPreferencesSection = ({ register, willTravel }) => (
  <div className="form-section">
    <h3>Travel Preferences</h3>
    <div className="form-row">
      <div className="form-group checkbox-group">
        <input 
          type="checkbox" 
          id="willTravel" 
          {...register("willTravel")} 
        />
        <label htmlFor="willTravel">Willing to travel to clients</label>
      </div>
      {willTravel && (
        <div className="form-group">
          <label>Travel Fee ($)</label>
          <input 
            type="number" 
            {...register("travelFee", { min: 0 })} 
            min="0"
          />
        </div>
      )}
    </div>
  </div>
);



const AvailabilitySection = ({ availability, daysOfWeek, onToggleAvailability, onUpdateTimeSlot }) => (
  <div className="form-section">
    <h3>Availability</h3>
    {daysOfWeek.map(day => {
      const dayAvailability = availability.find(a => a.day === day);
      const isAvailable = dayAvailability?.available || false;
      
      return (
        <div key={day} className="day-availability">
          <div className="form-row">
            <div className="form-group checkbox-group">
              <input
                type="checkbox"
                id={`available-${day}`}
                checked={isAvailable}
                onChange={() => onToggleAvailability(day)}
              />
              <label htmlFor={`available-${day}`}>{day.charAt(0).toUpperCase() + day.slice(1)}</label>
            </div>
          </div>
          
          {isAvailable && dayAvailability.timeSlots.map((slot, slotIndex) => (
            <div key={slotIndex} className="time-slot">
              <div className="form-row">
                <div className="form-group">
                  <label>Start Time</label>
                  <input
                    type="time"
                    value={slot.start}
                    onChange={(e) => onUpdateTimeSlot(day, slotIndex, 'start', e.target.value)}
                  />
                </div>
                <div className="form-group">
                  <label>End Time</label>
                  <input
                    type="time"
                    value={slot.end}
                    onChange={(e) => onUpdateTimeSlot(day, slotIndex, 'end', e.target.value)}
                  />
                </div>
                <div className="form-group">
                  <label>Max Jobs</label>
                  <input
                    type="number"
                    value={slot.maxJobs}
                    min="1"
                    onChange={(e) => onUpdateTimeSlot(day, slotIndex, 'maxJobs', parseInt(e.target.value))}
                  />
                </div>
              </div>
            </div>
          ))}
        </div>
      );
    })}
  </div>
);

const SkillsSection = ({ register, skills }) => (
  <div className="form-section">
    <h3>Skills (Max 5)</h3>
    {skills.map((skill, index) => (
      <div key={index} className="skill-item">
        <div className="form-row">
          <div className="form-group">
            <label>Skill Name</label>
            <input
              {...register(`skills.${index}.name`)}
              placeholder="e.g., Electrical Wiring"
            />
          </div>
          <div className="form-group">
            <label>Category</label>
            <input
              {...register(`skills.${index}.category`)}
              placeholder="e.g., Electrical"
            />
          </div>
          <div className="form-group">
            <label>Experience (years)</label>
            <input
              type="number"
              {...register(`skills.${index}.experience`, { min: 0 })}
              min="0"
            />
          </div>
        </div>
      </div>
    ))}
  </div>
);

const PreferredCategoriesSection = ({ register, categoryOptions }) => (
  <div className="form-section">
    <h3>Preferred Categories</h3>
    <div className="form-row">
      <div className="form-group">
        <select
          multiple
          {...register("preferredCategories")}
          className="multi-select"
        >
          {categoryOptions.map(option => (
            <option key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
        </select>
      </div>
    </div>
  </div>
);

const FormActions = ({ onCancel }) => (
  <div className="form-actions">
    <button type="submit" className="btn-save">Save Profile</button>
    <button type="button" onClick={onCancel} className="btn-cancel">
      Cancel
    </button>
  </div>
);

const ServiceAreasView = ({ serviceAreas, location }) => (
  <div className="profile-section">
    <h3>Service Areas</h3>
    {serviceAreas.length > 0 ? (
      <div className="service-areas-map">
        <MapContainer 
          center={location} 
          zoom={12} 
          style={{ height: '250px', width: '100%' }}
        >
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
          <Marker position={location} />
          {serviceAreas.map((area, index) => (
            <Circle
              key={index}
              center={area.coordinates}
              radius={area.radius * 1000}
              color="blue"
              fillColor="blue"
              fillOpacity={0.2}
            />
          ))}
        </MapContainer>
        <div className="service-areas-list">
          {serviceAreas.map((area, index) => (
            <div key={index} className="service-area-item">
              Area {index + 1}: {area.coordinates[0].toFixed(4)}, {area.coordinates[1].toFixed(4)} - {area.radius}km radius
            </div>
          ))}
        </div>
      </div>
    ) : (
      <p>No service areas defined</p>
    )}
  </div>
);

const AvailabilityView = ({ availability, daysOfWeek }) => (
  <div className="profile-section">
    <h3>Availability</h3>
    <div className="availability-grid">
      {daysOfWeek.map(day => {
        const dayAvailability = availability.find(a => a.day === day);
        return (
          <div key={day} className="availability-day">
            <strong>{day.charAt(0).toUpperCase() + day.slice(1)}:</strong>
            {dayAvailability?.available ? (
              <div className="time-slots">
                {dayAvailability.timeSlots.map((slot, index) => (
                  <div key={index}>
                    {slot.start} - {slot.end} (Max jobs: {slot.maxJobs})
                  </div>
                ))}
              </div>
            ) : (
              <span>Not available</span>
            )}
          </div>
        );
      })}
    </div>
  </div>
);

const SkillsView = ({ skills }) => (
  <div className="profile-section">
    <h3>Skills</h3>
    <div className="skills-list">
      {skills.filter(skill => skill.name).map((skill, index) => (
        <div key={index} className="skill-item">
          <strong>{skill.name}</strong> ({skill.category}) - {skill.experience} years
        </div>
      ))}
    </div>
  </div>
);



const PreferredCategoriesView = ({ preferredCategories, categoryOptions }) => (
  <div className="profile-section">
    <h3>Preferred Categories</h3>
    {preferredCategories.length > 0 ? (
      <div className="categories-list">
        {preferredCategories.map((category, index) => {
          const categoryLabel = categoryOptions.find(opt => opt.value === category)?.label || category;
          return (
            <span key={index} className="category-tag">
              {categoryLabel}
            </span>
          );
        })}
      </div>
    ) : (
      <p>No preferred categories selected</p>
    )}
  </div>
);

const ProfileView = ({ data, onEdit, daysOfWeek, categoryOptions }) => {
  return (
    <div className="profile-view">
      <div className="profile-section">
        <h2>{data.name}</h2>
        <div className="info-row">
          <span><strong>Phone:</strong> {data.phoneNumber}</span>
          <span><strong>Address:</strong> {data.address}</span>
        </div>
      </div>

      <ServiceAreasView 
        serviceAreas={data.serviceAreas} 
        location={data.location} 
      />

      <div className="profile-section">
        <h3>Travel Preferences</h3>
        <p>
          {data.willTravel 
            ? `Willing to travel to clients (Fee: $${data.travelFee})`
            : 'Not available for travel to clients'}
        </p>
      </div>

      <AvailabilityView 
        availability={data.availability} 
        daysOfWeek={daysOfWeek} 
      />

      <SkillsView skills={data.skills} />

      <PreferredCategoriesView 
        preferredCategories={data.preferredCategories} 
        categoryOptions={categoryOptions}
      />

      <button onClick={onEdit} className="btn-edit">
        Edit Profile
      </button>
    </div>
  );
};






  if (!editing) {
    return (
      <ProfileView 
        data={watch()} 
        onEdit={() => setEditing(true)} 
        daysOfWeek={DAYS_OF_WEEK}
        categoryOptions={CATEGORY_OPTIONS}
      />
    );
  }

  return (
    <form onSubmit={handleSubmit(handleFormSubmit)} className="profile-form">
      <BasicInfoSection register={register} errors={errors} />
      <LocationSection 
        mapCenter={mapCenter} 
        onLocationChange={handleLocationChange} 
      />
      <ServiceAreasSection 
        serviceAreas={watch('serviceAreas')} 
        radius={serviceAreaRadius} 
        onRadiusChange={setServiceAreaRadius}
        onAddArea={addServiceArea}
        onRemoveArea={removeServiceArea}
      />
      <TravelPreferencesSection 
        register={register} 
        willTravel={watch('willTravel')} 
      />
      <AvailabilitySection 
        availability={watch('availability')} 
        daysOfWeek={DAYS_OF_WEEK}
        onToggleAvailability={toggleAvailability}
        onUpdateTimeSlot={updateTimeSlot}
      />
      <SkillsSection 
        register={register} 
        skills={watch('skills')} 
      />
      <PreferredCategoriesSection 
        register={register} 
        categoryOptions={CATEGORY_OPTIONS}
      />
      <FormActions onCancel={() => setEditing(false)} />
    </form>
  );
};

export default TechnicianProfile;
