// UserIcon.js - Make sure it has proper login handling
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './User.css';


function UserIcon({ isAuthenticated, username, userType, onLogin, onLogout, onProfileNavigate }) {
    const navigate = useNavigate();
    const [showDropdown, setShowDropdown] = useState(false);

    const handleLoginClick = () => {
        navigate('/login');  // Navigate to login page
    };

    const handleRegisterClick = () => {
        navigate('/register');  // Navigate to register page
    };

    const handleProfileClick = () => {
        if (onProfileNavigate) {
            onProfileNavigate();
        }
        setShowDropdown(false);
    };

    const handleLogoutClick = () => {
        if (onLogout) {
            onLogout();
        }
        setShowDropdown(false);
    };

    const toggleDropdown = () => {
        setShowDropdown(!showDropdown);
    };

    return (
        <div className="user-icon">
            {!isAuthenticated ? (
                <div className="auth-buttons">
                    <button onClick={handleLoginClick} className="login-btn">Login</button>
                    <button onClick={handleRegisterClick} className="register-btn">Register</button>
                </div>
            ) : (
                <div className="user-menu">
                    <div className="user-info" onClick={toggleDropdown}>
                        <span className="username">Hello, {username}</span>
                        <div className="avatar">
                            {username ? username.charAt(0).toUpperCase() : 'U'}
                        </div>
                    </div>
                    
                    {showDropdown && (
                        <div className="dropdown-menu">
                            <div className="dropdown-item" onClick={handleProfileClick}>
                                My Profile
                            </div>
                            <div className="dropdown-item" onClick={handleLogoutClick}>
                                Logout
                            </div>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

export default UserIcon;
