import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import Navbar from './Components/Navbar/Navbar';
import Home from './Pages/Home/Home';
import MyApplication from './Pages/My-Application/MyApps';
import Shifts from './Pages/Shifts/Shifts';
import InternetStatus from './Pages/InternetStatus/InternetStatus';
import Chat from './Pages/Chats/Chats';
import Login from './Pages/Login/Login';
import Signup from './Pages/Signup/Signup';
import TechnicianProfile from './Protected_Apps/TechnicianProfile/TechnicianProfile';
import TechnicianTickets from './Protected_Apps/Tickets/TechnicianTickets';

function App() {
    const [authState, setAuthState] = useState({
        isAuthenticated: false,
        username: '',
        email: '',
        userType: '',
        loading: true
    });

    useEffect(() => {
        // Check for existing token on initial load
        const token = localStorage.getItem('token');
        if (token) {
            try {
                const decoded = jwtDecode(token);
                setAuthState({
                    isAuthenticated: true,
                    username: decoded.username,
                    email: decoded.email,
                    userType: decoded.userType,
                    loading: false
                });
            } catch (error) {
                localStorage.removeItem('token');
                setAuthState(prev => ({ ...prev, loading: false }));
            }
        } else {
            setAuthState(prev => ({ ...prev, loading: false }));
        }
    }, []);

    const handleLogin = (user) => {
        setAuthState({
            isAuthenticated: true,
            username: user.username,
            email: user.email,
            userType: user.userType,
            loading: false
        });
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        setAuthState({
            isAuthenticated: false,
            username: '',
            email: '',
            userType: '',
            loading: false
        });
    };

    // Protected Route Component
    const ProtectedRoute = ({ children }) => {
        if (authState.loading) {
            return <div>Loading...</div>;
        }
        return authState.isAuthenticated ? children : <Navigate to="/login" />;
    };

    if (authState.loading) {
        return <div>Loading...</div>;
    }

    return (
        <Router>
            <Navbar 
                isAuthenticated={authState.isAuthenticated} 
                username={authState.username} 
                email={authState.email}
                userType={authState.userType}
                onLogout={handleLogout} 
            />
            <Routes>
                <Route path="/" element={<Home />} />

                <Route path="/my-application" element={
                    <ProtectedRoute>
                        <MyApplication />
                    </ProtectedRoute>
                }>
                    <Route path="tickets" element={<TechnicianTickets username={authState.username} />} />
                </Route>

                <Route path="/shifts" element={<Shifts />} />
                <Route path="/internet-status" element={<InternetStatus />} />
                <Route path="/chat" element={<Chat />} />
                <Route path="/login" element={<Login onLogin={handleLogin} />} />
                <Route path="/signup" element={<Signup />} />

                <Route path="/technicianprofile" element={
                    <ProtectedRoute>
                        <TechnicianProfile user={authState} />
                    </ProtectedRoute>
                } />
            </Routes>
        </Router>
    );
}

export default App;
