import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './UserIcon.css';
import default_image from '../../Assets/default-image.jpg';

function UserIcon({ isAuthenticated, username, email: propEmail, userType, onLogin, onLogout }) {
    const [showMenu, setShowMenu] = useState(false);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();
    const menuRef = useRef(null);


     // This useEffect hook runs when the component mounts and handles click-outside behavior
useEffect(() => {
    // Function to handle click events outside the menu
    const handleClickOutside = (event) => {
        // Check if:
        // 1. menuRef.current exists (the menu DOM element is available)
        // 2. The clicked element (event.target) is NOT contained within the menu
        if (menuRef.current && !menuRef.current.contains(event.target)) {
            // If both conditions are true, close the menu
            setShowMenu(false);
        }
    };

    // Add event listener to the document for mousedown events
    // This will trigger handleClickOutside whenever any mouse click occurs
    document.addEventListener('mousedown', handleClickOutside);

    // Cleanup function that runs when the component unmounts
    // or before the effect runs again
    return () => {
        // Remove the event listener to prevent memory leaks
        document.removeEventListener('mousedown', handleClickOutside);
    };
}, []); // Empty dependency array means this effect runs only once on mount

    const handleLogin = async (e) => {
        e.preventDefault();
        
        // Basic validation
        if (!email || !password) {
            setMessage('Please fill in all fields');
            return;
        }

        setLoading(true);
        setMessage('');

        try {
            const response = await axios.post('http://localhost:5000/api/users/login', {
                email,
                password
            }, {
                headers: {
                    'Content-Type': 'application/json'
                }
            });

            console.log('Full backend response:', response); // Log complete response
    console.log('Response data:', response.data); // Log response data
    console.log('User data:', response.data.user); // Log user object

            const { token, user } = response.data;
            
            // Store token and basic user info
            localStorage.setItem('token', token);
            localStorage.setItem('userInfo', JSON.stringify({
                username: user.username,
                userType: user.userType,
                id: user._id,
                email: user.email,  // Store email

            }));

            // Update parent component state
            onLogin({
                username: user.username,
                email: user.email,  // Store email
                userType: user.userType,
                id: user._id,
                
            });

            setMessage('Login successful');
            
            // Close menu after successful login
            setTimeout(() => {
                setShowMenu(false);
                navigate(user.userType === 'technician' ? '/my-application/tickets' : '/my-application');
            }, 1000);

        } catch (error) {
            console.error('Login error:', error);
            setMessage(
                error.response?.data?.message || 
                error.response?.data?.error || 
                error.message || 
                'Login failed. Please try again.'
            );
        } finally {
            setLoading(false);
        }
    };

    const handleLogout = () => {
        // Clear all auth-related storage
        localStorage.removeItem('token');
        localStorage.removeItem('userInfo');
        
        // Update parent component
        onLogout();
        
        // Reset local state
        setShowMenu(false);
        setEmail('');
        setPassword('');
        setMessage('');
        
        // Redirect to home
        navigate('/');
    };

    const getInitials = (username) => {
        if (!username) return '';
        const nameArray = username.split(' ');
        return nameArray.map(name => name[0]?.toUpperCase()).join('').slice(0, 2);
    };

    // Add this useEffect to maintain login state on refresh
useEffect(() => {
    const userInfo = JSON.parse(localStorage.getItem('userInfo'));
    if (userInfo) {
        onLogin({
            username: userInfo.username,
            email: userInfo.email,
            userType: userInfo.userType,
            id: userInfo.id
        });
    }
}, []);

    return (
        <div className="user-auth" ref={menuRef}>
            {/* User Icon */}
            <div 
                className="user-icon" 
                onClick={() => {
                    setShowMenu(!showMenu);
                    setMessage('');
                }}
                aria-label={isAuthenticated ? 'User menu' : 'Login menu'}
            >
                {isAuthenticated ? (
                    <div className="initials">{getInitials(username)}</div>
                ) : (
                    <img src={default_image} alt="Default User Icon" />
                )}
            </div>

            {/* Dropdown Menu */}
            {showMenu && (
                <div className="dropdown-menu">
                    {isAuthenticated ? (
                        <>
                            <div className="user-info">
                                <p className="username">{username}</p>
                                <p className="user-email">{propEmail}</p> {/* Email */}
                                <p className="user-type">{userType}</p>
                                
                            </div>
                            <div className="menu-actions">
                            <button 
    className="dashboard-btn"
    onClick={() => {
        navigate(userType === 'technician' 
            ? '/profile' 
            : '/profile');
        setShowMenu(false);
    }}
    disabled={loading}
>
    Profile
</button>
                                
                                <button 
                                    className="logout-btn"
                                    onClick={handleLogout}
                                    disabled={loading}
                                >
                                    {loading ? 'Logging out...' : 'Logout'}
                                </button>
                            </div>
                        </>
                    ) : (
                        <div className="login-form-container">
                            <h3>Login</h3>
                            {message && (
                                <p className={`message ${message.includes('success') ? 'success' : 'error'}`}>
                                    {message}
                                </p>
                            )}
                            <form onSubmit={handleLogin}>
                                <div className="form-group">
                                    <label htmlFor="email">Email</label>
                                    <input
                                        id="email"
                                        type="email"
                                        placeholder="Enter your email"
                                        value={email}
                                        onChange={(e) => setEmail(e.target.value)}
                                        required
                                        autoComplete="username"
                                    />
                                </div>
                                <div className="form-group">
                                    <label htmlFor="password">Password</label>
                                    <input
                                        id="password"
                                        type="password"
                                        placeholder="Enter your password"
                                        value={password}
                                        onChange={(e) => setPassword(e.target.value)}
                                        required
                                        minLength="6"
                                        autoComplete="current-password"
                                    />
                                </div>
                                <button 
                                    type="submit" 
                                    disabled={loading}
                                    className="login-btn"
                                >
                                    {loading ? (
                                        <>
                                            <span className="spinner"></span>
                                            Logging in...
                                        </>
                                    ) : 'Login'}
                                </button>
                            </form>
                            <div className="signup-prompt">
                                <p>Don't have an account?</p>
                                <button 
                                    className="signup-link"
                                    onClick={() => {
                                        setShowMenu(false);
                                        navigate('/signup');
                                    }}
                                    type="button"
                                >
                                    Sign up here
                                </button>
                            </div>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

export default UserIcon;
