import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import Navbar from './Components/Navbar/Navbar';

import Home from './Pages/Home/Home';
import MyApplication from './Pages/My-Application/MyApps';
import Shifts from './Pages/Shifts/Shifts';
import InternetStatus from './Pages/InternetStatus/InternetStatus';
import Chat from './Pages/Chats/Chats';
import Login from './Pages/Login/Login';
import Signup from './Pages/Signup/Signup';
import TechnicianProfile from './Protected_Apps/TechnicianProfile/TechnicianProfile';
import TechnicianTickets from './Protected_Apps/Tickets/TechnicianTickets';


function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [username, setUsername] = useState('');
    const [userType, setUserType] = useState('');
    const [email, setEmail] = useState('');
    const [loading, setLoading] = useState(true);

    const handleLogin = (user) => {
        setIsAuthenticated(true);
        setUsername(user.username);
        setUserType(user.userType);
        setEmail(user.email);
    };

    const handleLogout = () => {
        setIsAuthenticated(false);
        setUsername('');
        setUserType('');
        setEmail('');
    };

    return (
        <Router>
            <Navbar 
                isAuthenticated={isAuthenticated} 
                username={username} 
                userType={userType}
                onLogout={handleLogout} 
            />
            <Routes>
                <Route path="/" element={<Home />} />

                <Route path="/my-application" element={<MyApplication />}>
                    <Route path="tickets" element={<TechnicianTickets username={username} />} />
                </Route>

                <Route path="/profile" element={
                    userType === 'technician' 
                        ? <TechnicianProfile userType={userType} username={username} email={email} /> 
                        : <ClientProfile userType={userType} username={username} email={email} />
                } />

                <Route path="/shifts" element={<Shifts />} />
                <Route path="/internet-status" element={<InternetStatus />} />
                <Route path="/chat" element={<Chat />} />
                <Route path="/login" element={<Login onLogin={handleLogin} />} />
                <Route path="/signup" element={<Signup />} />
            </Routes>
        </Router>
    );
}

export default App;
