import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

import Navbar from './Components/Navbar/Navbar';
import Home from './Pages/Home/Home';
import MyApplication from './Pages/My-Application/MyApps';
import Shifts from './Pages/Shifts/Shifts';
import InternetStatus from './Pages/InternetStatus/InternetStatus';
import Chat from './Pages/Chats/Chats';
import Login from './Pages/Login/Login';
import Signup from './Pages/Signup/Signup';
import TechnicianTickets from './Protected_Apps/Tickets/TechnicianTickets';

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [username, setUsername] = useState(''); // Set username on login

     useEffect(() => {
            const token = localStorage.getItem('token');
            if (token) {
                try {
                    const decodedToken = jwtDecode(token); // Decode token
                    setIsAuthenticated(true);
                    setUsername(decodedToken.username || ''); // Handle missing fields gracefully
                    setEmail(decodedToken.email || '');
                } catch (error) {
                    console.error('Error decoding token:', error);
                    localStorage.removeItem('token'); // Remove invalid token
                    setIsAuthenticated(false);
                }
            }
            setLoading(false); // Set loading to false after token validation
        }, []);


    const handleLogin = (user) => {
        setIsAuthenticated(true);
        setUsername(user.username);
    };

    const handleLogout = () => {
        setIsAuthenticated(false);
        setUsername('');
    };

    return (
        <Router>
            <Navbar isAuthenticated={isAuthenticated} username={username} onLogout={handleLogout} />
            <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/my-application" element={<MyApplication />} >
                <Route path="tickets" element={<TechnicianTickets username={username} />} />

                </Route>


                <Route path="/shifts" element={<Shifts />} />
                <Route path="/internet-status" element={<InternetStatus />} />
                <Route path="/chat" element={<Chat />} />
                <Route path="/login" element={<Login onLogin={handleLogin} />} />
                <Route path="/signup" element={<Signup />} />

            </Routes>
        </Router>
    );
}

export default App;
