// Import React and hooks for state & side effects
import React, { useState, useEffect } from "react";
// Import useNavigate for routing/navigation
import { useNavigate } from "react-router-dom";
import ''

// Define the RelatedCategories component
const RelatedCategories = () => {
  // State to store list of services fetched from backend
  const [services, setServices] = useState([]);
  // State to store technicians for the selected service
  const [techniciansData, setTechniciansData] = useState({});
  // State to track which service is currently selected
  const [selectedService, setSelectedService] = useState(null);
  // Hook for navigation (e.g., go to profile page)
  const navigate = useNavigate();

  // ================= FETCH SERVICES ON COMPONENT LOAD =================
  useEffect(() => {
    // Function to fetch all available services from backend
    const fetchServices = async () => {
      try {
        // API call to backend endpoint /api/services
        const res = await fetch("http://localhost:5000/api/services");
        // Parse JSON response
        const data = await res.json();
        // Save services into state
        setServices(data);
      } catch (err) {
        // Log error if fetching fails
        console.error("Error fetching services:", err);
      }
    };
    // Call fetch function when component mounts
    fetchServices();
  }, []); // [] ensures this runs only once on mount

  // ================= FETCH TECHNICIANS FOR SELECTED SERVICE =================
  const handleServiceClick = async (subService, category) => {
    try {
      // API call with query parameters for subService and category
      const res = await fetch(
        `http://localhost:5000/api/relatedservices?subService=${encodeURIComponent(
          subService
        )}&category=${encodeURIComponent(category)}`
      );
      // Parse JSON response
      const data = await res.json();
      // Save technicians into state: exact matches + related services
      setTechniciansData({
        [subService]: data.exactMatches,
        related: data.relatedServices,
      });
      // Update selected service to show its technicians
      setSelectedService(subService);
    } catch (err) {
      // Log error if API request fails
      console.error("Error fetching technicians:", err);
    }
  };

  // ================= GO BACK TO SERVICES LIST =================
  const handleBack = () => {
    // Reset selection and technicians data
    setSelectedService(null);
    setTechniciansData({});
  };

  // ================= NAVIGATE TO PROFILE PAGE =================
  const handleViewProfile = (id) => {
    // Redirect to technician profile page by ID
    navigate(`/technician/${id}`);
  };

  // ================= RENDER TECHNICIANS LIST =================
  const renderTechniciansList = () => {
    // Exact match technicians for the chosen subservice
    const techs = techniciansData[selectedService] || [];
    // Related service technicians from same category
    const related = techniciansData.related || [];

    return (
      <div className="technicians-section">
        {/* Back button to go back to services list */}
        <button className="back-button" onClick={handleBack}>
          <i className="fas fa-arrow-left"></i> Back to Services
        </button>

        {/* Section heading */}
        <h2>Technicians for {selectedService}</h2>
        <p className="technicians-count">{techs.length} technicians available</p>

        {/* Exact Matches Section */}
        {techs.length > 0 ? (
          <div className="technicians-grid">
            {/* Loop through technicians and render cards */}
            {techs.map((t) => (
              <div key={t._id} className="technician-card">
                <div className="technician-image">
                  {/* Display technician profile image or default fallback */}
                  <img
                    src={t.profileImage || "/default-profile.png"}
                    alt={t.username}
                  />
                </div>
                <div className="technician-info">
                  <h4>{t.username}</h4>
                  <p>{t.profession}</p>
                  {/* Show short preview of "about" section */}
                  <p>{t.about?.slice(0, 60)}...</p>
                  {/* Button to navigate to profile */}
                  <button
                    className="profile-btn"
                    onClick={() => handleViewProfile(t._id)}
                  >
                    View Profile
                  </button>
                </div>
              </div>
            ))}
          </div>
        ) : (
          // If no technicians available for this service
          <div className="no-technicians">
            <i className="fas fa-user-slash"></i>
            <h3>No technicians available</h3>
            <p>No technicians are available for {selectedService} right now.</p>
          </div>
        )}

        {/* Related Services Section */}
        {related.length > 0 && (
          <div className="related-services">
            <h3>Other Technicians in Same Category</h3>
            <div className="technicians-grid">
              {/* Loop through related technicians and render cards */}
              {related.map((t) => (
                <div key={t._id} className="technician-card related">
                  <div className="technician-image">
                    {/* Show profile image or fallback */}
                    <img
                      src={t.profileImage || "/default-profile.png"}
                      alt={t.username}
                    />
                  </div>
                  <div className="technician-info">
                    <h4>{t.username}</h4>
                    <p>{t.profession}</p>
                    {/* Shortened about info */}
                    <p>{t.about?.slice(0, 50)}...</p>
                    {/* View profile button */}
                    <button
                      className="profile-btn"
                      onClick={() => handleViewProfile(t._id)}
                    >
                      View Profile
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    );
  };

  // ================= MAIN RETURN =================
  return (
    <div className="services-page">
      {/* If no service selected, show list of all services */}
      {!selectedService ? (
        <div className="services-grid">
          {services.map((s) => (
            <div
              key={s._id}
              className="service-card"
              onClick={() => handleServiceClick(s.name, s.category)}
            >
              {/* Display service icon */}
              <i className={`fas ${s.icon}`}></i>
              <h4>{s.name}</h4>
              <p>{s.category}</p>
            </div>
          ))}
        </div>
      ) : (
        // If service selected, show technicians list
        renderTechniciansList()
      )}
    </div>
  );
};

// Export component so it can be imported in routes
export default RelatedCategories;
