import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TechnicianTickets.css';

const TechnicianTickets = ({ user }) => {
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTickets = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/tickets/technician/${user.username}`);
        setTickets(response.data); // Store the fetched tickets in state
      } catch (err) {
        console.error('Error fetching tickets:', err);
        setError('Failed to fetch tickets. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchTickets();
  }, [user.username]);

  if (loading) return <p>Loading tickets...</p>;
  if (error) return <p className="error">{error}</p>;

  return (
    <div className="technician-tickets-container">
      <h2>Assigned Tickets</h2>
      {tickets.length === 0 ? (
        <p>No tickets assigned to you.</p>
      ) : (
        <ul className="ticket-list">
          {tickets.map((ticket) => (
            <li key={ticket._id} className="ticket-item">
              <h3>{ticket.ticketType}</h3>
              <p><strong>Client Name:</strong> {ticket.clientName}</p>
              <p><strong>Mobile Number:</strong> {ticket.mobileNumber}</p>
              <p><strong>Location:</strong> {ticket.location}</p>
              <p><strong>Created By:</strong> {ticket.createdBy.username}</p>
              <p><strong>Date:</strong> {new Date(ticket.createdAt).toLocaleDateString()}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TechnicianTickets;
