import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import Navbar from './Components/Navbar/Navbar';

import Home from './Pages/Home/Home';
import MyApplication from './Pages/My-Application/MyApps';
import Shifts from './Pages/Shifts/Shifts';
import InternetStatus from './Pages/InternetStatus/InternetStatus';
import Chat from './Pages/Chats/Chats';
import Login from './Pages/Login/Login';
import Signup from './Pages/Signup/Signup';
import TechnicianProfile from './Protected_Apps/TechnicianProfile/TechnicianProfile';
import TechnicianTickets from './Protected_Apps/Tickets/TechnicianTickets';

// ProtectedRoute component for technician-only routes
const TechnicianRoute = ({ children, isAuthenticated, userType }) => {
  if (!isAuthenticated) {
    return <Navigate to="/login" replace />;
  }
  if (userType !== 'technician') {
    return <Navigate to="/" replace />; // Redirect non-technicians to home
  }
  return children;
};

function App() {
  const [authState, setAuthState] = useState({
    isAuthenticated: false,
    username: '',
    email: '',
    userType: '', // 'technician', 'admin', 'customer', etc.
    loading: true
  });

  const handleLogin = (user) => {
    setAuthState({
      isAuthenticated: true,
      username: user.username,
      email: user.email,
      userType: user.userType, // Make sure your login returns userType
      loading: false
    });
  };

  const handleLogout = () => {
    setAuthState({
      isAuthenticated: false,
      username: '',
      email: '',
      userType: '',
      loading: false
    });
  };

  // Check for existing session on app load
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decoded = jwtDecode(token);
        handleLogin({
          username: decoded.username,
          email: decoded.email,
          userType: decoded.userType // Make sure your JWT includes userType
        });
      } catch (error) {
        console.error('Invalid token', error);
        handleLogout();
      }
    } else {
      setAuthState(prev => ({ ...prev, loading: false }));
    }
  }, []);

  if (authState.loading) {
    return <div>Loading...</div>;
  }

  return (
    <Router>
      <Navbar 
        isAuthenticated={authState.isAuthenticated} 
        username={authState.username} 
        onLogout={handleLogout} 
      />
      <Routes>
        <Route path="/" element={<Home />} />

        <Route path="/my-application" element={<MyApplication />}>
          <Route 
            path="tickets" 
            element={<TechnicianTickets username={authState.username} />} 
          />
        </Route>

        <Route 
          path="/technicianprofile" 
          element={
            <TechnicianRoute 
              isAuthenticated={authState.isAuthenticated} 
              userType={authState.userType}
            >
              <TechnicianProfile 
                username={authState.username}
                userType={authState.userType} 
              />
            </TechnicianRoute>
          } 
        />

        <Route path="/shifts" element={<Shifts />} />
        <Route path="/internet-status" element={<InternetStatus />} />
        <Route path="/chat" element={<Chat />} />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/signup" element={<Signup />} />
      </Routes>
    </Router>
  );
}

export default App;
